package com.example.golpo;

import androidx.appcompat.app.AppCompatActivity;

import android.content.Intent;
import android.os.Bundle;
import android.widget.TextView;

public class StoryActivity extends AppCompatActivity {

    TextView title, description;

    private int story;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_story);

        title = findViewById(R.id.title);
        description = findViewById(R.id.description);

        story = getIntent().getIntExtra("story", 1);

        switch (story) {
            case 1:
                firstStory();
                break;
            case 2:
                secondStory();
                break;
            case 3:
                thirdStory();
                break;
            case 4:
                fourthStory();
                break;
            case 5:
                fifthStory();
                break;
            case 6:
                sixthStory();
                break;
            case 7:
                seventhStory();
                break;
            case 8:
                eightStory();
                break;
            case 9:
                ninthStory();
                break;
            case 10:
                tenthStory();
                break;
            case 11:
                eleventhStory();
                break;
            case 12:
                twelvethStory();
                break;
            case 13:
                thirteenthStory();
                break;
            case 14:
                fourteenthStory();
                break;
            case 15:
                fifteenthStory();
                break;
            case 16:
                sixteenthStory();
                break;

        }
    }

    private void firstStory() {

        title.setText(R.string.gorur);
        description.setText("কশ্যপ মুনির ঔরসে বিনতার গর্ভে গরুড় জন্মগ্রহণ করেন। ইনি ছিলেন বিষ্ণুর বাহন।\n" +
                "উল্লেখ্য, এঁর মাতা বিনতা দুটি ডিম প্রসব করেছিলেন। এর একটি অসময়ে ভেঙে ফেলার জন্য, উর্ধ্বভাগ সম্পূর্ণ ও নিম্নভাগ অসম্পূর্ণ অবস্থায় একটি পুত্র লাভ করেন। এই পুত্রের নামকরণ করা হয় অরুণ। এরপর বিনতার দ্বিতীয় ডিম স্বাভাবিকভাবে ফোটে এবং গরুড়ের জন্ম হয়।\n" +
                "মহাভারতে অরুণ ও গরুড়ের জন্মের একটি কাহিনি পাওয়া যায়। একবার কশ্যপ মুনি পুত্রলাভের আশায় একটি মহাযজ্ঞ করেন। উক্ত যজ্ঞে সাহায্য করার জন্য দেবতাগণ, ঋষিগণ ও গন্ধর্বগণ অংশগ্রহণ করেছিলেন। ইন্দ্র নিজের ক্ষমতাবলে প্রচুর কাঠ যোগাড় করে আনার সময় দেখতে পান যে, ক্ষুদ্রাকার বালখিল্য ঋষিরা সমবেতভাবে একটি পত্রবৃন্ত আনার চেষ্টা করছিলেন। উল্লেখ্য বালখিল্যরা উপাসনার জন্য প্রায় অনাহারে থাকতেন। এই কারণে তাঁরা দুর্বল ছিলেন। এ ছাড়া এঁরা আকারে এতটাই ক্ষুদ্র ছিলেন যে, গরুর পায়ের চাপে মাটিতে যে গর্ত হয়, তাতে জমে থাকা জলে ডুবে যেতেন। বলাই বাহুল্য এরূপ বালখিল্যদের একটি পত্রবৃন্ত সংগ্রহ করা এবং তা বহন করে নিয়ে যাওয়াটা অনেক কঠিন ছিল।\n" +
                "ইন্দ্র বালখিল্যদের এই পত্রবৃন্ত সংগ্রহের দৃশ্য দেখে উপহাস করেন। এতে বালখিল্যরা অপমানিত হন এবং ইন্দ্রের চেয়ে শক্তিশালী পুত্র কামনায় যজ্ঞ করেন। বিষয়টি ইন্দ্র জানতে পেরে, কশ্যপের কাছে সাহায্য প্রার্থনা করেন। কশ্যপ ইন্দ্রের অনুরোধে বালখিল্যদের কাছে এসে কার্যসিদ্ধির প্রার্থনা করলেন। বালখিল্যরা এ প্রার্থনা মঞ্জুর করেন।  এরপর কশ্যপ বালখিল্যদের বলেন যে, ইন্দ্রকে ত্রিভুবনের নিয়ন্ত্রণ অধিকার ব্রহ্মা দিয়েছেন। সুতরাং ইন্দ্রের চেয়ে শক্তিশালী কেউ হলে, ব্রহ্মার নিয়ম লঙ্ঘিত হবে। তাই বালখিল্যদের ইন্দ্র হবে পক্ষীদের ইন্দ্র। যেহেতু বালখিল্যরা কশ্যপ যে পুত্রকামনায় যজ্ঞ করছিলেন, তাই এক্ষেত্রে কি করা উচিৎ, তা বিবেচনার ভার কশ্যপকেই প্রদান করেন। এর কিছুদিন পর বিনতা যখন পুত্রকামনায় ঋতুস্নান শেষে কশ্যপের কাছে আসেন। তখন কশ্যপ বিনতাকে জানান যে, বালখিল্যদের প্রার্থনা অনুসারে তাঁর গর্ভে দুটি পুত্র জন্মগ্রহণ করবে। এই পক্ষীরূপী ওই দুই পুত্র পক্ষীজাতির ইন্দ্র হবে।\n" +
                "[সূত্র: মহাভারত, আদিপর্ব, একত্রিংশ অধ্যায়]\n" +
                "সমুদ্রমন্থনে উদ্ভূত উচ্চৈঃশ্রবা নামক অশ্বের লেজের রঙ সাদা না কালো এই নিয়ে, একবার কদ্রুর সাথে বিনতার সাথে তর্ক উপস্থিত হলে- কদ্রু অশ্বের লেজ কালো ও বিনতা সাদা বলেন। পরের দিন এই বিষয়টি মীমাংসার জন্য উভয়ই শর্ত সাপেক্ষে রাজী হন। শর্তটি ছিল- এই তর্কে যিনি জয়ী হবেন, তাঁর অধীনে অপরজনকে দাসত্ব করতে হবে। পরদিন উচ্চৈঃশ্রবাকে দেখতে উভয়ই অগ্রসর হলে, কদ্রু তাঁর সর্পপুত্রদের আদেশ দিলেন যে, তারা যেন উচ্চৈঃশ্রবার লেজে জড়িয়ে থেকে তা কালো বর্ণের করে দেয়। কদ্রুর এই আদেশ অনুসারে সে সকল সাপ উচ্চৈঃশ্রবার লেজে অবস্থান নেয় তারা কদ্রুর আশীর্বাদ লাভ করে। \n" +
                "পরদিন সমুদ্র পার হয়ে, কদ্রু ও বিনতা উভয়ই দূর থেকে উচ্চৈঃশ্রবাকে পরীক্ষা করে- দেখতে পান যে, এর লেজ কালো। তখন শর্তানুসারে বিনতা কদ্রুর দাসীতে পরিণত হন।\n" +
                "দূরের সাগর পারে বিনতা যখন হেরে গিয়ে কদ্রুর দাসী হয়, সেই সময় গরুড় ডিম ফুটে বের হয়ে আসে। খুব অল্প সময়ের ভিতরে তার দেহ বৃদ্ধি পেয়ে প্রকাণ্ডরূপ ধারণ করে। এর ফলে দেবগণ এবং ঋষিগণ ভীত সন্ত্রস্ত অবস্থায় গরুড়ের স্তূতিবাক্য দ্বারা সন্তুষ্ট করেন। গরুড় তার নিজদেহকে সংকুচিত করে, দেবতা এবং ঋষিদের আস্বস্ত করেন। এরপর গরুড় তাঁর ভাই অরুণকে সাথে নিয়ে তাঁর মা বিনতার সাথে দেখা করতে যান। এই সময় সূর্য দেবতাদের উপর ক্ষিপ্ত হয়ে, সারা পৃথিবী দগ্ধ করার উদ্যোগ নেন। এই কারণে গরুড় অরুণকে পূর্বদিকে নিরাপদ স্থানে রেখে আসেন। সূর্য সংহারক-মূর্তি ধারণ করলে রাত্রিকালেই মহাদাবদাহের সৃষ্টি হয়। দেবতা এবং ঋষিরা এই দাবদাহে উদ্বিগ্ন হয়ে ব্রহ্মার কাছে যান। ব্রহ্মা এর প্রতিকার হিসেবে অরুণকে সূর্যের রথের সারথি হিসেবে নিয়োগ করেন। এর ফলে সূর্য তেজ বৃদ্ধি করলেও অরুণ, সে তেজকে হ্রাস করে দেবেন। এরপর অরুণ রথের সারথি হলে, সূর্যের তেজ থেকে জগৎ রক্ষা পেয়েছিল।\n" +
                "[সূত্র: মহাভারত, আদিপর্ব, ষোড়শ-চতুর্বিংশ অধ্যায়।]\n" +
                "গরুড়ের মা বিনতা দাসী অবস্থায় কদ্রুর সকল আদেশ পালন করতেন। একই সাথে গরুড় বিনতা র পুত্র বলে তাঁকেও কদ্রু দাসের মতো ব্যবহার করতেন। একবার কদ্রু সমুদ্রের ভিতরে নাগদের একটি মনোরম বাসস্থানে যাওয়ার জন্য মনস্থির করেন। তিনি বিনতাকে ওই দ্বীপে নিয়ে যাওয়ার জন্য আদেশ করেন। বিনতা কদ্রুকে পিঠে বহন করে নিয়ে যান। এই সময় গরুড়ও অন্যান্য নাগদের বহন করে ওই দ্বীপে নিয়ে যেতে বাধ্য করেন। গরুড় ইচ্ছা করে নাগদের বহন করে সূর্যের দিকে অগ্রসর হলে, সূর্যের তাপে নাগদের এক একজন অজ্ঞান হয়ে যেতে থাকে। এই অবস্থা বুঝতে পেরে কদ্রু ইন্দ্রের কাছে বৃষ্টি প্রার্থনা করেন। প্রার্থনায় সন্তুষ্ট হয়ে ইন্দ্র বৃষ্টি বর্ষণ করলে, নাগরা রক্ষা পায়। এরপর নাগরা গরুড়কে নানা মনোরম স্থানে নিয়ে যাওয়ার জন্য আদেশ করেন। \n" +
                "গরুড় সে সব আদেশ মান্য করেন। এরপর তিনি বিনতার কাছে এসে তাঁদের এই দাসত্ব বৃত্তির কারণ জিজ্ঞাসা করলে, বিনতা সকল বিষয় গরুড়কে জানান। এরপর গরুড় নাগদের কাছে এই দাসত্ববৃত্তি থেকে রক্ষা পাওয়ার উপায় জানতে চাইলে, নাগরা বলেন যে, যদি গরুড় অমৃত এনে দিতে পারে, তবেই এই দাসত্ববৃত্তি থেকে মুক্ত হবেন। এরপর তিনি মায়ের অনুমতি নিয়ে অমৃতলাভের উদ্দেশ্যে রওনা দেন।\n" +
                "গরুড় তাঁর মায়ের মুক্তির জন্য অমৃত আনার জন্য অগ্রসর হয়ে অচিরেই ক্ষুধার্ত হয়ে পড়েন। এই সময় বিনতার দেয়া পরামর্শ অনুসারে সমুদ্রপ্রান্তের নিষাদপল্লীতে নেমে নিষাদদের গ্রাস করলেন। এরপর তাঁর ক্ষুধা নিবৃত্ত না হলে, নিষাদ পল্লী ত্যাগ করে আকাশে উঠলেন। কিছুক্ষণ পর তিনি তাঁর পিতা কশ্যপের দেখা পান।\n" +
                "পিতা-পুত্র পরস্পরের কুশলাদি বিনিময় করার পর, গরুড় তাঁর নিষাদ ভক্ষণের কথা বলেন এবং এতে তাঁর ক্ষুধা নিবৃত্ত হয় নাই সে কথাও কশ্যপকে জানান। এরপর কশ্যপের পরামর্শ অনুসারে গজ-কচ্ছপরূপী আত্মকলহরত বিভাবসু ও সুপ্রতীককে গ্রাস করার উদ্দেশ্যে রওনা দেন। এই পরামর্শ অনুসারে এই গজ-কচ্ছপকে নখ দিয়ে দৃঢ়ভাবে ধরে গরুড় আকাশে উঠে আসেন। এরপর গরুড় উড়তে উড়তে অলম্ব নামক তীর্থে আসেন। গরুড় কোনো গাছের ডালে বসে, গজ-কচ্ছপ খাবেন বলে যখন শক্ত গাছের ডাল খুঁজছিলেন, তখন গরুড়ের বিশাল ভার বহন করতে পারবে না বলে, গাছেরা আতঙ্কিত হয়ে পড়ে। এরপর একটি বিশাল বট গাছ, তার ডালে বসে আহার করার জন্য আহ্বান করে। বট গাছের এই কথা শুনে, গরুড় ওই গাছের শতযোজন ব্যাপী বিস্তৃত একটি শাখায় বসা মাত্র, তা ভেঙে যায়। গরুড় ভাঙা ডালটিকে আঁকড়ে ধরে নিচের দিকে তাকাতেই দেখতে পেলেন যে, ওই ডালে বালখিল্য মুনিরা অধঃশিরে তপস্যারত অবস্থায় ঝুলছেন। এই ডাল ছেড়ে দিলে বালখিল্য ঋষিদের মৃত্যু হবে, এই ভয়ে ডালটি মুখে নিয়ে আকাশে উঠে গেলেন। \n" +
                "এই দৃশ্য দেখে, ঋষিরা বললেন, যেহেতু গুরুভার বাহন করে এই পক্ষী অবিচলিতভাবে আকাশে উড্ডীন হলেন, তাই তার নাম হবে গরুড়। দুই নখে গজ ও কচ্ছপ এবং মুখে ঝুলন্ত বালখিল্য ঋষিবৃন্দ-সহ বিশাল শাখা মুখে নিয়ে গরুড় নানা স্থানে ঘুরে বেড়াতে লাগলেন, কিন্তু কোথায় বসার জায়গা পেলেন না। এরপর তিনি গন্ধমাদন পর্বতে তপস্যারত পিতা কশ্যপ্যের কাছে যান। কশ্যপ প্রথমে গরুড়কে এমন কিছু করতে নিষেধ করলেন, যাতে বালখিল্যরা রেগে গিয়ে অভিশাপ দ্বারা গরুড়কে ভষ্মীভূত করেন। এরপর কশ্যপ নানা ভাবে বালখিল্যদের বাক্যের দ্বারা সন্তুষ্ট করেন। এবং এই শাখা ত্যাগ করার জন্য অনুরোধ করেন। অবশেষে বালখিল্যরা এই শাখা ত্যাগ করে তপস্যার জন্য হিমালয়ে চলে যান।\n" +
                "এরপর কশ্যপের পরামর্শে গরুড় একটি মনুষ্যশূন্য বরফে ঢাকা পর্বতে গিয়ে, মুখ থেকে ডালটি ছেড়ে দেন। এরপর সেই গিরিশৃঙ্গে বসে, গরুর গজ-কচ্ছপ আহার করেন। এরপর গরুড় অমৃত পাওয়ার জন্য, দেবতাদের নানাভাবে উত্যক্ত করা শুরু করেন। গরুড়ের উৎপাতে ইন্দ্রের বজ্র ভয়ে আপনা-আপনি প্রজ্বলিত হলো। আকাশ থেকে উল্কাপাত শুরু হলো। সেই সাথে প্রবলবেগে বাতাস প্রবাহিত হতে থাকলো। আকাশ থেকে রক্তবর্ষণ হতে থাকলো। দেবতার এই উৎপাতের কোনো কারণ খুঁজে পেলেন না।\n" +
                "ফলে দেবতারা এর কারণ জানার জন্য গুরু বৃহস্পতির কাছে যান। বৃহস্পতি জানান যে, কশ্যপের পুত্রলাভের যজ্ঞের সময় ইন্দ্র বালখিল্যদের অপমান করেছিলেন। এই কারণে, ইন্দ্রের চেয়ে ক্ষমতাশালী পুত্রের জন্য বালখিল্যরা যজ্ঞ করেছিলেন। সেই সূত্রে কশ্যপের ঔরসে বিনতার গর্ভে অরুণ ও গরুড় নামক পক্ষীদ্বরয়ে জন্ম হয়েছে। এই পক্ষী অমৃত হরণের ক্ষমতা রাখে। ইন্দ্র এই কথা শুনে, ইন্দ্র অমৃতরক্ষক দেবতাদের বিশেষভাবে সাবধান করে দেন।\n" +
                "এরপর অমৃত পাওয়ার জন্য গরুড় দেবতাদের সামনে এলে, দেবতারা তাঁকে আক্রমণ করেন। যুদ্ধে প্রচুর দেবসৈন্য হতাহত হলেও গরুড় অক্ষত থাকেন। যুদ্ধে দেবতাদের পরাজিত করে, অমৃতভাণ্ডের কাছে দেখলেন, অমৃতভাণ্ডকে ঘিরে রয়েছে অগ্নিবলয়। গরুড় সহস্র মুখ সৃষ্টি করে, সহস্রমুখে জলবর্ষণ করে, আগুন নিভিয়ে ফেলেন। এরপর গরুড় স্বর্ণময় ভয়ঙ্কর রূপ ধারণ করে অমৃতভাণ্ডের কাছে চলে আসেন। এবার তিনি দেখলেন লোহার তৈরি ক্ষুরধার চক্র, অমৃতভাণ্ডকে ঘিরে আবর্তিত হচ্ছে। গরুর নিজদেহকে সঙ্কুচিত করে ওই লৌহচক্রের ফাঁক গলে ভিতরে প্রবেশ করলেন। এবার তিনি দেখলেন, দুটি ভয়ঙ্কর সাপ অমৃতভাণ্ডকে রক্ষা করছে। এই সাপের মুখ থেকে অগ্নিস্ফুলিঙ্গ নির্গত হচ্ছে। এর দৃষ্টিও ছিল বিষযুক্ত। একবার এই সাপ দুটির দিকে যে তাকাবে, সে ওই বিষের প্রভাবে ভস্মীভূত হয়ে যাবে। \n" +
                "গরুড় প্রথমে ধূলি নিক্ষেপ করে, সাপ দুটির চোখ অন্ধ করে দিলেন। এরপর নখ ও চঞ্চু দিয়ে সাপ দুটিকে ছিন্নভিন্ন করে, অমৃতভাণ্ড নিয়ে আকশে উঠে এলেন। এরপর তিনি আকশপথে রওনা দিলেন। এই সময় বিষ্ণুর সাথে তাঁর দেখা হয়। বিষ্ণু গরুড়কে বর প্রার্থনা করতে বললে, গরুড় বলেন যে, তিনি যেন বিষ্ণুর উপরিভাগে অবস্থান করতে পারেন এবং অমৃত পান ছাড়াই অমর হতে পারেন। বিষ্ণু সেই বরই প্রদান করলেন। গরুর এবার বিষ্ণুকে বর প্রদানের ইচ্ছা প্রকাশ করলে, বিষ্ণু তাঁকে তাঁর বাহন হওয়ার ইচ্ছা প্রকাশ করেন। কিন্তু যেহেতু গরুড় বিষ্ণুর উপরে থাকবেন, এই বর আগেই পেয়েছিলেন, তাই বিষ্ণু পুনরায় বলেন গুরুড় যেন তাঁর রথের পতাকারূপে বিরাজ করেন। গরুড় সেই বর প্রদান করেন। পরে উভয় উভয়ের কাছ থেকে বিদায় নিলেন।\n" +
                "এরপর ইন্দ্রর বজ্র দ্বারা গরুড়কে আঘাত করলেন। এই আঘাতে গরুড়ের কোনই ক্ষতি হলো না। গরুড় ইন্দ্রকে বললেন যে, বজ্রাঘাতে তাঁর কোনো ক্ষতি হবে না। তবে যে মুনির অস্থি থেকে এই বজ্র তৈরি হয়েছে, তাঁর সম্মানার্থে তিনি একটি পালক ত্যাগ করবেন।\n" +
                "গরুড়ের ত্যাগ করা এই পালকের সৌন্দর্য দেখে দেবতারা মোহিত হয়ে যান। পরে দেবতারা গরুড়ের নামকরণ করেন সুপর্ণ। এরপর ইন্দ্র গরুড়ের সাথে বন্ধুত্ব স্থাপনের প্রস্তাব দেন। ইন্দ্র গরুড়কে জানান যে, নাগরা অমৃত পানে অমর হলে, তারা সকলের উপর অত্যাচার করবে। এরপর গরুড় অমৃত হরণের কারণ বর্ণনা করেন।\n" +
                "গরুড় আরও বললেন যে, অমৃত নিয়ে গিয়ে যে স্থানে রাখা হবে সে স্থান থেকে ইন্দ্র অমৃত চুরি করে আনলেই সাপেরা অমৃত পাবে না। ইন্দ্র খুশি হয়ে তাতে রাজি হলেন এবং তাঁর কাছে বর প্রার্থনা করতে বললেন। গরুড় বর হিসেবে প্রার্থনা করেন, সাপেরা যেন তাঁর খাদ্য হয়। ইন্দ্র সেই বরই দিলেন। এরপর গরুড় অমৃত এনে একটি কুশের উপর রেখে সাপদেরকে স্নান করে এসে অমৃত পান করতে বললেন। শর্তানুসারে এই সময় বিনতা মুক্তি পেলেন। আর সাপেরা স্নান করতে গেলে, ইন্দ্র অমৃত হরণ করে নিয়ে যান। স্নান শেষে সাপেরা ফিরে এসে অমৃত না দেখতে পেয়ে কুশ লেহন করতে থাকে। ফলে এদের জিহ্বা দ্বি-বিভক্ত হয়। এর মধ্য দিয়ে বিনতা দাসত্ব থেকে মুক্তি লাভ করেন। \n" +
                "[সূত্র: মহাভারত, আদিপর্ব, ঊনত্রিংশ-চতুস্ত্রিংশ অধ্যায়]\n" +
                "একবার ইন্দ্রের সারথি মাতলি তাঁর কন্যা গুণকেশীর সাথে নাগ বংশীয় সমুখের বিবাহ স্থির করেন। কিন্তু সমুখ একমাস পরে গরুড়ের খাদ্য হবেন জেনে মাতলি সমুখকে নিয়ে ইন্দ্র ও বিষ্ণুর কাছে বিষয়টি জানান। বিষ্ণু অমৃত পান করিয়ে সমুখকে অমরত্ব প্রদানের জন্য ইন্দ্রকে নির্দেশ দেন। কিন্তু ইন্দ্র তাঁকে অমর না করে দীর্ঘ জীবন দান করেন। এতে গরুড় ইন্দ্রের প্রতি ক্রোধ প্রকাশ করলে, ইন্দ্র বলেন যে, বিষ্ণুর ইচ্ছায় সমুখের জন্য ব্যবস্থা নেয়া হয়েছে। এরপর গরুড় বিষ্ণুর কাছে শক্তির বড়াই করে বলেন যে, আমি আমার শক্তিতে তোমাকে বহন করি। এতে বিষ্ণু তাঁর বিশালত্ব বুঝানোর জন্য তাঁর বাম হাত গরুড়ের পাখায় রাখেন। এর ফলে গরুড় হতচেতন হয়ে নিচে পড়ে যান। এরপর গরুড় বিষ্ণুর কাছে ক্ষমা চান। বিষ্ণু তাঁর পায়ের আঙুল দিয়ে সমুখকে গরুড়ের বক্ষে নিক্ষেপ করেন। এরপর থেকে গরুড় সমুখকে হত্যা করা থেকে বিরত হন।\n" +
                "লঙ্কা যুদ্ধে ইন্দ্রজিৎ রাম-লক্ষ্মণকে নাগ পাশে বন্ধন করলে গরুড় এঁদেরকে মুক্ত করেন। হিন্দু পৌরাণিক কাহিনীতে গরুড় বিভিন্ন নামে উল্লেখিত হয়ে থাকেন। যেমন- অমৃতাহরণ, অরুণাগ্রজ, অরুণানুজ, অরুণাবরজ, অহিদ্বিট্, অহিভুক্, অহিমার, অহিরিপু।\n" +
                "**\n" +
                "গরুড়ের বিভিন্ন নামের কারণসমুহ:-\n" +
                "কাশ্যপী:- কাশ্যপ তাঁর পিতা ছিলেন।\n" +
                "খগপতি- পাখিদের রাজা ছিলেন।\n" +
                "খগরাজ:- পাখিদের রাজা ছিলেন।\n" +
                "খগেশ্বর:- পাখিদের রাজা ছিলেন।\n" +
                "গগনেশ্বর- আকাশ মণ্ডলে তাঁর সমকক্ষ কেউ ছিল না।\n" +
                "বজ্রজিৎ:- ইন্দ্রের বজ্রকে তিনি জয় করেছিলেন।\n" +
                "বিষ্ণুরথ:- বিষ্ণুর বাহন ছিলেন।\n" +
                "বৈনতয়:- বিনতা তাঁর মা ছিলেন।\n" +
                "রক্তপক্ষ:- রক্তবর্ণের পাখা ছিল।\n" +
                "সুধাহর:- অমৃত হরণ করতে সক্ষম হয়েছিলেন।\n" +
                "সুপর্ণ- ইন্দ্র তাঁর প্রতি বজ্র নিক্ষেপ করলে, তাতে গরুড়ের কিছু হয়নি। বরং ইন্দ্রের বজ্রের সম্মানার্থে একটি সোনার পালক নিজেই তাঁর শরীর থেকে খসিয়ে দেন। সে কারণে ইনি সুপর্ণ।\n" +
                "সুবর্ণকায়:- শরীর সোনার মতো উজ্জ্বল ছিল।\n" +
                "সুরেন্দ্রজিৎ:- ইন্দ্রকে জয় করেছিলেন।\n" +
                "**\n" +
                "গরুড় শুধুই একটা পক্ষী নয়, দেবতাদের থেকেও শক্তিশালী বলে একে গরুড়দেব নামেও অভিহিত করা হয়। তদুপরি ভগবান বিষ্ণু বা নারায়ন বা শ্রীকৃষ্ণের বাহন বলে, গরুড় পূজনীয় ও বটে। যিনি বাহনরুপে দ্বায়িত্ব পালন করেন, তিনি ভক্ত হিসেবে কতোটা উচ্চস্তরের সাধক, তা বলাই বাহুল্য।\n" +
                "পরমকরুনাময় গোলোকপতি গরুড়বাহীনি ভগবান শ্রীহরি সবার জীবন মঙ্গলময়, কল্যাণময়, শান্তিময় আর আনন্দময় করে রাখুন নিরন্তর, এই প্রার্থনা আমাদের।\n" +
                "\"হরে কৃষ্ণ হরে কৃষ্ণ\n" +
                " কৃষ্ণ কৃষ্ণ হরে হরে\n" +
                " হরে রাম হরে রাম\n" +
                " রাম রাম হরে হরে!!\"\n" +
                "!!জয় হোক সকল ভক্তদের!!\n" +
                "!!জয় হোক গরুড়দেবের!!\n" +
                "!!জয় শ্রীকৃষ্ণ!! জয় রাধে!!\n");
    }

    private void secondStory() {
        title.setText(R.string.balmiki);
        description.setText("প্রাচীনকালে চ্যবন মুনির পুত্র রত্নাকর ছিল এক দুর্ধর্ষ ডাকাত । সে লোহার গদা নিয়ে বনের মধ্যে লুকিয়ে থাকতো । পথিক দেখলেই তাকে হত্যা করে সর্বস্ব কেড়ে নিতো । এইভাবে অনেক দিন ধরে বহু পথিককে সে হত্যা করেছে । \n" +
                "একদিন রত্নাকরের খুব মন খারাপ । সকাল থেকে সে বসে আছে অথচ কোন পথিকের দেখা নেই । হঠাৎ সে দেখল, বনপথ ধরে দুজন সন্ন্যাসী আসছেন । রত্নাকর ঠিক করলো এদের হত্যা করে সর্বস্ব হরণ করবে । সন্ন্যাসীরা কাছে আসতেই সে গদা তুলে দুজনকে হত্যা করতে ধেয়ে গেল । বলা হয়নি, সন্ন্যাসী দুজন হলেন প্রভু ব্রহ্মা আর দেবর্ষি নারদ । ব্রহ্মার মায়ার প্রভাবে রত্নাকরের ডান হাত অচল হয়ে গেল । বহু চেষ্টা করেও সে আর হাত নাড়াতে পারল না । তখন ব্রহ্মাজী হাসতে হাসতে তাকে বললেন, \" হে দস্যু, তুমি আমাদের তো মারতে এসেছ । মানুষ মারলে যে পাপ হয় তা কি তোমার জানা নেই? তুমি তো দিনের পর দিন এই পাপ কাজ করে চলেছ, তোমার এ পাপের ভাগী কি কেউ আছে? \"\n" +
                "\n" +
                "রত্নাকর বললেন, \" পাপ-পূণ্য আমি কিছুই চিন্তা করিনি । আমি মানুষ মেরে যে টাকা পাই তাই দিয়ে সংসার চালাই । সংসারে রয়েছে আমার মা, বাবা ও স্ত্রী । যদি মানুষ মেরে পাপ করে থাকি তাহলে, তবে আমরা চার জনই পাপের ভাগী হবো । \" একথা শুনে ব্রহ্মা হেসে বললেন, \" তোমার পাপের ভাগ নিতে এই পৃথিবীতে কেউ নেই । যদি আমার কথা বিশ্বাস না হয়, তবে, যাও তোমার মা, বাবা এবং স্ত্রীকে জিজ্ঞেস করে এসো, আমরা এখানে অপেক্ষা করছি । \"\n" +
                "\"বেশ তো সন্ন্যাসী । আমি গৃহে যাই, আর তোমরা এখান থেকে পালাও । সে হবার নয় । যাওয়ার আগে তোমাদের এই গাছের সঙ্গে বেঁধে রেখে যাচ্ছি । \" এই বলে সন্ন্যাসী দুজনকে বেঁধে রেখে রত্নাকর বাড়ি গেল । \n" +
                "প্রথমেই পিতার নিকট গিয়ে রত্নাকর বলল, \"পিতাশ্রী, দস্যুবৃত্তি করি, মানুষ হত্যা করে টাকা পয়সা সংগ্রহ করে সংসার চালাই, এতে নাকি আমার পাপ হয়? তাই যদি হয়, তবে আপনি কি আমার পাপের ভাগী হবেন না? \"\n" +
                "বৃদ্ধ পিতা বললেন, \" যখন তুমি শিশু ও অসহাত ছিলে, তখন আমি তোমাকে পালন করেছি । সময়ের পরিবর্তনে আজ তুমি সবল যুবক, আর আমি দুর্বল অসহায় বৃদ্ধ । এখন তুমিই আমাকে পালন করবে-- এই তো সমাজের বিধান । তুমি যেমন করে পার, আমাকে পালন করবে । এ কাজের জন্য তোমাকে তো পাপ করতে বলা হয়নি। তোমার পাপের ভাগ আমি নিতে যাব কেন? \"\n" +
                "পিতার কথা শুনে রত্নাকর চমকে উঠে । চিন্তাকুল মনে সে মায়ের নিকট গিয়ে একই প্রশ্ন জিজ্ঞেস করে । উত্তরে মা বললেন, \" তোমাকে দশ মাস দশ দিন গর্ভে ধারন করেছি । গর্ভধারিণীর এ ঋণ কোন সন্তানই শোধ করতে পারে না । তবে আমি তোমার পাপের ভাগ নিতে যাবো কেন? \"\n" +
                "অবশেষে স্ত্রীকেও সে জিজ্ঞেস করলো, \" তোমার ভরণ পোষণের জন্য আমি যে পাপ করেছি, তুমি সে পাপের ভাগী হবে কি? \"\n" +
                "উত্তরে স্ত্রী বলল, \" দেখুন, আমি আপনার সহধর্মিণী । বিয়ের সময় মন্ত্রপাঠ করে প্রতিজ্ঞা করেছেন যে, আপনি আমাকে পালন করবেন । তাই আমাকে পালন করতে গিয়ে আপনি যদি পাপ করে থাকেন, সে পাপ আমাকে স্পর্শ করবে না । তবে আপনার পূণ্যের অর্ধেক অবশ্যই আমি পাব।\"\n" +
                "পিতা, মাতা ও স্ত্রীর এ হেনু কথা শুনে রত্নাকর একেবারে স্তম্ভিত। সে এতদিন ধরে কি করেছে? যাদের ভরণ পোষণের জন্য  দিনের পর দিন নরহত্যা করতেও পিছপা হয়নি, তারা কেউ তো পাপের ভাগ নিতে রাজি নয় । সে এখন কেমন করে তার পাপের সাগর পার হবে?\n" +
                "রত্নাকর পাপের চিন্তায় অস্থির হয়ে উঠলো । কাঁদতে কাঁদতে সন্ন্যাসীদের বাঁধন খুলে দিয়ে ভগবান ব্রহ্মার পায়ে পড়ে বলল, \"হে সন্ন্যাসী, আপনি ঠিকই বলেছেন, তারা কেউই আমার পাপের ভাগ নেবে না । এখন আমি কি করবো? ঠাকুর দয়া করে বলে দিন, আমি কেমন করে পাপ থেকে মুক্তি পাবো?\"\n" +
                "রত্নাকরের কান্না দেখে ব্রহ্মাজীর মনে করুণার উদয় হয় । তিনি তাকে পুকুর থেকে স্নান করে আসতে বললেন । রত্নাকর স্নান করতে গিয়ে দেখে তার সামনে থেকে জল শুকিয়ে যাচ্ছে । জলের অভাবে তার স্নান হলো না । অবশেষে ব্রহ্মা তার কমন্ডলু থেকে জল ছিটিয়ে দিলেন রত্নাকরের মাথায় । রত্নাকর গঙ্গাজলে সিক্ত হয়ে ব্রহ্মার চরণপ্রান্তে বসে পড়লো । ব্রহ্মা তাকে রাম নামে দীক্ষিত করে বললেন, তোমাকে আর কিছু করতে হবে না, তুমি শুধু রাম নাম জপ কর । জপ করতে করতে দেখবে তোমার সব পাপ দূর হয়ে যাবে । \"\n" +
                "\n" +
                "রত্নাকর বহু চেষ্টা করেও রাম নাম উচ্চারণ করতে পারল না । ব্রহ্মা বললেন, \" পাপে তোমার জিহ্বা আড়ষ্ট হয়ে গেছে । তুমি এক কাজ কর, মরা মরা বলতে থাক, দেখবে তোমার মুখে রাম নাম আসবে । তুমি এখানে কঠোর সাধনা করতে থাক । আমরা দেবলোকে যাচ্ছি । সেখান থেকে ফিরে তোমার সাথে দেখা করবো । \" এই বলে দেবর্ষি নারদকে নিয়ে ব্রহ্মা অন্তর্হিত হলেন । \n" +
                "রত্নাকর ব্রহ্মার নির্দেশ অনুসারে যোগাসনে বসে এক মনে মরা শব্দটি জপ করছে । \n" +
                "\"মরা মরা বলিতে আইল রাম নাম । \"\n" +
                "বহুদিন পর আবার ব্রহ্মাজি চলেছেন সেই রত্নাকর দস্যুর বনপথে । তিনি ভুলেই গেছেন রত্নাকরের কথা । চলতে চলতে হঠাৎ তিনি শুনতে পেলেন, কে যেন অবিরাম রাম নাম জপ করে চলেছে । কোথা থেকে আসছে রামের ধ্বনি? ধারে কাছে কাউকে তিনি দেখতে পেলেন না । তখন তার মনে পরে রত্নাকরের কথা । কিন্তু কোথায় সে? কিছুক্ষণ ভালোভাবে লক্ষ করে বুঝলেন, তার সামনের বল্মীকের স্তূপ (উঁইয়ের ঢিবি) থেকে অবিরাম রাম নাম ধ্বনিত হচ্ছে । ব্যাপার কি দেখার জন্য ব্রহ্মাজি দেবরাজ ইন্দ্রকে স্মরণ করলেন । \n" +
                "ব্রহ্মার নির্দেশমতো ইন্দ্র বল্মীকের স্তূপের উপর অবিরল ধারায় জল বর্ষণ করতে লাগলেন । সাত দিন বৃষ্টি বর্ষণের পর দেখা গেল বল্মীকের স্তূপ আর নেই । সেখানে রয়েছে এক মানুষের কঙ্কাল । সে কঙ্কাল হতেই রাম নাম ধ্বনিত হচ্ছে । ব্রহ্মাজির ইচ্ছায় কঙ্কালের গায়ে রক্ত-মাংস যুক্ত হলো । ব্রহ্মা দেখলেন, এই সেই রত্নাকর । তখন ব্রহ্মা বললেন, \"হে রত্নাকর রাম নামে তোমার সমস্ত পাপ ধুয়ে মুছে গেছে । এখন তুমি নিষ্পাপ । তুমি যখন রাম নামে বিভোর ছিলে তখন তোমার গা ঘিরে সৃষ্টি হয়েছিল বল্মীকের স্তূপ । এজন্য আজ থেকে তোমার নাম হলো বাল্মীকি । \n");
    }

    private void thirdStory() {
        title.setText(R.string.basbad);
        description.setText("মহামুনি বশিষ্ঠের পুত্র শক্তি। শক্তি যখন কল্মাষপাদের হাতে মৃত্যু বরণ করলেন, তখন তাঁর একমাত্র পুত্র পরাশর তখন মাতৃগর্ভে। জন্মের পর থেকে মাতা অদৃশ্যন্তী এবং পিতামহ ঋষি বশিষ্ঠ দেবের রক্ষণাবেক্ষেণে পরাশর ক্রমে মহাপন্ডিত হয়ে উঠলেন। একদিন পরাশর মুনি নদীপার হবেন - খেয়া নৌকা চালাচ্ছে মৎস্যগন্ধা নামে এক ধীবর-পালিত কন্যা (ধীবর একজন জেলেদের রাজা, একদিন ধীবর নদীতে মাছ ধরতে যাই আর যে মাছগুলো ধরেছিলেন তার একটি মাছের ভেতরে এক কন্যা শিশু পায় তখন ধীবর সেই মেয়েটির নাম রাখে মৎস্যগন্ধা)। তখন পরাশর মুনি বলেন মাতা আমি যমুনার ওপারে যাব আমাকে কি পার করে দিবেন? মৎস্যগন্ধা বলেন হে মুনিবর আসুন। পরাশর নৌকায় উঠে বসলেন। কিছুটা দূর যাওয়ার পরপরাশর মুনি বললেন মাতা আপনার নৌকায় মাছের গন্ধ আসছে কেন? তখন মৎস্যগন্ধা বললেন মুনিবর মাছের গন্ধ আমার শরীর থেকে আসছে। পরাশর জিজ্ঞাসা করলো, কিভাবে? \n" +
                "মৎস্যগন্ধা বললেন , আমার জন্ম হয়েছিল মাছের ভেতরে আর জন্ম লগ্ন থেকে আমার শরীর থেকে মাছের গন্ধ আসছে। পরাশর মুনি বললেন মাতা আপনি নদী পারে আমাকে সহায়তা করেছেন, আপনি যদি চান আপনার এই উপকারের ফলস্বরূপ আমি আপনার জন্ম লগ্ন থেকে অর্জিত করা এই মাছের গন্ধ আমার যোগ তপস্যা শক্তি তেজর্ভূষী (তেজর্ভূষী বলতে ধোঁয়া সংস্কৃত ভাষায় ধোঁয়াকে র্ভূষী বলে) দ্বারা দূর করতে পারি। মৎস্যগন্ধা তখন বললেন, আমি আপনার নিকট কৃতজ্ঞ হবো। পরাশর মুনি বললেন,কিন্তু আপনি আমার তেজর্ভূষী ধারন করতে অক্ষম হইবেন যার ন্যায় আপনার কোলে এক পুত্র সন্তানের আবির্ভাব হবে। তখন মৎস্যগন্ধা বলেন, হে মহর্ষি আমি একজন কুমারী মেয়ে। পরাশরমুনি বললেন, এতে আপনার কুমারীত্বের কোনও ক্ষতি হবে না। মৎস্যগন্ধা তখন আর চিন্তানা করে বললো ঠিক আছে মহর্ষি। অতঃপর পরাশর মুনি বললেন মাতা সামনে একটি দ্বীপ দেখা যাচ্ছে ঐখানে নৌকা স্থির করুন, অতঃপর মৎস্যগন্ধা যমুনার মধ্যেবর্তী সেই তীরে নৌকা স্থির করলেন। নৌকার একপারে মৎস্য গন্ধা অন্য পারে পরাশর অবস্থিত ছিলেন। তত্ক্ষণাৎ মহর্ষি পরাশর নৌকার ওপার থেকেতপস্যা শক্তি দ্বারা তেজর্ভূষী উৎপক্তি করলেন এবং পরাশরের কল্যাণে ঐ কন্যার গায়ের মৎস্যগন্ধ দূর হলো-তখন তাঁর নাম হলো পদ্মগন্ধা। কিন্তু পদ্মগন্ধার কোলে এক পুত্রের আবির্ভাব হয়, তখন পরাশর ঐ পুত্রের নামে রাখে কৃষ্ণ। যমুনার মধ্যেবর্তী এক দ্বীপে জন্ম গ্রহন করেছেন বলে তাঁর অপর পরিচয় হলো দ্বৈপায়ণ। মৎস্যগন্ধা ঐ পুত্রসন্তানকে নিয়ে ধীবর রাজ গৃহে চলে যায়। \n" +
                "পদ্মগন্ধার কোলে নবজাতক দেখে ধীবর প্রশ্ন করলেন পুত্রী তোমার কোলে এই শিশু কোথায় থেকে এলো? পদ্মগন্ধা তখন বিস্তারিত খুলে বলে, কিন্তু ধীবর রাজ বলেন পুত্রী তুমি কুমারী এই শিশু তোমার কাছে থাকলে তোমার বিবাহ অসম্ভব। তখন পদ্মগন্ধা বলল পিতা আমি আর কি করতে পারতাম? ধীবর রাজ বললো, চলো পুত্রী যার শক্তিতে এই শিশু আবির্ভূত সেই কোনও সমাধান দিবে। অতঃপর ধীবর রাজ ও পদ্মগন্ধা পরাশর মুনির আশ্রমের উদ্দেশ্য প্রস্থান করলেন। আশ্রমে গিয়ে ধীবর রাজমহর্ষি পরাশর কে বোঝালেন ও পদ্মগন্ধা বললেন, মহর্ষি আমার এই পুত্র সন্তান কে আপনার রক্ষণাবক্ষেণে রাখুন তাকে আপনার মতো একজন মহর্ষি মহাপন্ডিত হিসেবে গড়ে তুলুন। হে মহর্ষি আমি নিরুপায় আমার এই সন্তান আমারকাছে থাকলে আমার সতীত্ব নিয়েপ্রশ্ন উঠবে। তখন মহর্ষি পরাশর বলে দাও মাতা, ঐ পুত্র হস্তান্তর করার পর পদ্মগন্ধার নাম হয় সত্যবতী (পরবত্তী কালে হস্তিনারাজ শান্তনু এই সত্যবতীকেই বিয়ে করেছিলেন)( ব্যাসের জন্ম বৃত্তান্ত কথা সমাপ্ত )\n" +
                "(ব্যাসদেবের জীবন বৃত্তান্ত পর্ব) :-\n" +
                "দ্বৈপায়ণ ক্রমে ক্রমে বড় হয়ে উঠেছে কিন্তু তাঁর খেলারমতি বেশি। এ নিয়ে মহর্ষি পরাশর খুব চিন্তিত দ্বৈপায়ণকে কিভাবে পাঠ্যসূচি মনোযোগী করা যায়, এমন কি মহর্ষি বহুবার বোঝাই কিন্তু দ্বৈপায়ণ কিছুতেই বুঝতে রাজি নই। লেখাপড়ায় দ্বৈপায়ণ সম্পূর্ণ অমনোযোগী। দ্বৈপায়ণ যখন আটবছর বয়সী তখন একদিন মহর্ষিপরাশরের পাঠশালায় সব ছাত্ররা যখন জ্ঞান পাঠে মগ্ন তখন মহর্ষি পরাশর দেখলেন দ্বৈপায়ণ ক্রিয়াকর্মে মগ্ন,অতঃপর মহর্ষি দ্বৈপায়ণকে পাঠশালা থেকে তাড়িয়ে দিলেন। মন খারাপ করেদ্বৈপায়ণ আশ্রমের পুকুর ঘাটে গিয়ে বসেন। ঠিক সেই সময় ঐখানে আশ্রমের নারীরা পুকুর থেকে জল মাটির কলসী ভর্তি করে একটা স্থানে শান্ বাধানো(অর্থাৎ পাথরের পাকা ঘাট) ঘাটে রাখে সেখান থেকে আরেক নারী কলসী তুলে নিয়ে যায়। এভাবে (দীর্ঘদিন) দীর্ঘক্ষণ চলতে থাকে। যখন নারীরা পুকুর থেকে চলে যাই তখন প্রায় বেলা শেষ, দ্বৈপায়ণ তখন হাতমুখ দৌত করার জন্য ঘাটে নামে। হঠাৎ দ্বৈপায়ণের দৃষ্টি পরে পুকুর ঘাটের উপর, তিনি দেখলেন শান্ বাধানো ঘাটের ক্ষয় হয়েছে। তখন তিনি ঐ ঘাট স্পর্শ করে দেখলেন, আর গভীর ভাবে চিন্তা করলেন সামান্য মাটির পাত্রের র্ঘষণে যদি শান্ বাধানো ঘাটের ক্ষয় হতে পারে, তাহলে পুনঃ চেষ্টায় আমার জড় বুদ্ধি দূর করে পাঠশালায় অন্য সব শিক্ষাথীর্দের মতো জ্ঞানী হতে পারবো না কেন। দ্বৈপায়ণ তত্ক্ষণাত্ আশ্রমে ফিরে গিয়ে জ্ঞান পাঠে যোগ দেয়। মহর্ষি পরাশর দেখে দ্বৈপায়ণ ফিরে এসে জ্ঞান পাঠে যোগ দিয়েছে। সেদিন থেকে শুরু হয় পাঠ্যক্রম আর ক্রমেশে দ্বৈপায়ণ পাঠশালায় সর্ব শ্রেষ্ঠ শিক্ষাথীর্র গৌরব অর্জন করে। পূর্ণ জ্ঞান অর্জনসমাপন করার পরে দ্বৈপায়ণকে মহর্ষি পরাশর ব্যাস উপাধি প্রদান করে। সেদিন থেকে তাঁর নাম হয় কৃষ্ণ দ্বৈপায়ণ ব্যাসদেব। কিছুদিন পর ব্যাসদেব যোগ তপস্যায় লিপ্ত হয় এবং কঠোর তপস্যা করে। তপস্যায় ব্যাসদেব পূর্ণ রুপে আধ্যাত্মিক শক্তি লাভ করে এবং নিজেকে ত্বত্ত জ্ঞানেজ্ঞানান্নীত করে মহাজ্ঞানী স্বীকৃতি অর্জন করে।(দ্বৈপায়ণ থেকে ব্যাসদেব নামে পরিচিতি পর্ব সমাপ্ত)\n" +
                "(সনাতন ধর্মের গ্রন্থ গদ্য আকারে না হয়ে শ্লোকে কেন লিপিবদ্ধ হলো তার পূর্ণ বিবরণ):-\n" +
                "ব্যাসদেব তপস্যা পূর্ণ করে ফেরার প্রায় পনেরো বছর পর, একদিন ব্যাসদেব একটি যজ্ঞানুষ্ঠানে অংশ গ্রহন করেন। তিনি দেখলেন অত্র যজ্ঞানুষ্ঠানে সকল ভক্তরা অজ্ঞের মতো বসে আছে আর ঋষিরা যজ্ঞানুষ্ঠান পরিচালনা করছেন। ব্যাসদেব নিজের ভেতরে এইটা অনুভব করলো যে, সনাতন সমাজের ব্রাহ্মণ,ক্ষত্রিয়,শুদ্র,বৈশ্য সবাই ধর্ম পালনে তৎপর হইলেও প্রকৃত ধর্মের জ্ঞান সম্পর্কে অনবিজ্ঞ তার একমাত্র কারণ হচ্ছে ধর্মের নিয়ম, শৃঙ্খলা, অনুশাসন ও জ্ঞানবাণী ঋষিদের মুখাশ্রীত পর্যন্ত সীমাবদ্ধ। তখন তিনি চিন্তা করলেন যদি এই জ্ঞানবাণী ঋষি সমাজ পর্যন্ত সীমাবদ্ধ থাকে, তাহলে অদূর ভবিষ্যতে সনাতনী সমাজ বিলুপ্তির পথে দাঁড়াবে। ধর্ম নামক শক্তির কোন অস্তিত্ব থাকবে না, মানুষ অমানুষের আঁকার ধারণ করবে ও সৃষ্টির সেরা এই মানবজাতি হিংস্র দানবের ন্যায় অস্থিতিশীল হয়ে উঠবে। কেননা ধর্মের নিয়ম শৃঙ্খলা ও আদেশ উপদেশ ব্যতীত পৃথিবীর কোনও জাতি সুশৃঙ্খল, আত্মত্যাগী, ন্যায়পরায়ন ও সমাজবাদী শ্রেষ্ঠত্ব অর্জন করতে পারবে না। তখন তিনি মনস্থির করলেন ঋষি সমাজের মুখাশ্রীত ধর্মের এই জ্ঞানবাণী সনাতনী মানব সমাজ পর্যন্ত ধর্মের সংবাদ তথা আদেশ উপদেশ সরুপ পৌঁছাতে হবে। এরপর তিনি আশ্রমে ফিরে এলেন। সেইদিনের পর থেকে ব্যাসদেব সবসময় সর্বক্ষণ বিচলিত থাকতেন, তিনি শুধু এই চিন্তামগ্নের সমাধান খুঁজতেন। \n" +
                "একদিন তিনি ভাবলেন ঋষিদের মুখাশ্রীত এই বাণীই হচ্ছে ধর্মের জ্ঞান কৌশল তথা নিয়মশাসন ও ধর্মের অনুপ্রেরণা। যদি এই অনুপ্রেরণা গ্রন্থ আকারে লিপিবদ্ধ করে মানব সমাজের জন্য উপস্থাপন করা যায়, তাহলেসনাতন ও সনাতনী সমাজ বিলুপ্তির হাত থেকে রক্ষা পাবে। ব্যাসদেবের আশ্রমে ধর্মের গ্রন্থ লেখার কাজ শুরুকরার চিন্তা করলো কিন্তু তিনিভাবলেন শুধু মুখাশ্রীত বাণী লিপিবদ্ধ করলে কিভাবে হবে, তাহলে মানবজাতি ব্রহ্মাণ্ডের ত্রিদেব তথা মহাশক্তির পরিচিতি তাদের কার্যকলাপ ও তাদের সৃষ্টির সৃজনশীলতার গুণাগুণ উপলব্ধি করবে কিভাবে। ব্যাসদেব তখন আরো চিন্তামগ্ন হয়ে গেলেন, তিনি ভাবলেন যদি ঋষির মুখাশ্রীত বাণী ও মহাশক্তিদের গুণাবলি লিপিবদ্ধ করতে যায় তাহলে তো একার পক্ষে সম্ভব নয়। তার সমাধান খুঁজতে তিনি ব্রহ্মলোকের উদ্দেশ্য গমন করলেন। ব্রহ্মলোক গিয়ে তিনি ব্রহ্মদেবকে বললেন, হে সৃষ্টি গুরু আমি সৃষ্টির ইতিহাস তথা মহাশক্তির পরিচিতি গুণাগুণ ও কার্যকলাপ এবং ধর্মের নিয়ম শৃঙ্খলা ও অনুশাসন, অনুশীলন এবং অনুপ্রেরণা গ্রন্থাকারে লিপিবদ্ধ করতে চাই এতে আপনার সুনির্দিষ্ট সাহায্য চাই। ব্রহ্মদেব বললেন অসাধারণ চিন্তা তোমার ঋষি ব্যাস, কিন্তু আমার খুবই দুর্ভাগ্য তোমার এই মহান কার্যে কোনরুপ সহায়তা করতে পারছি না। ব্যাসদেব বললেন হে পিতা, আমি কিভাবে আমার কার্য সিদ্ধি করব? ব্রহ্মদেব বললো ঋষি ব্যাস তুমি বৈকুণ্ঠপতির কাছে যাও তিনিই তোমাকে পথ দেখাবেন।ব্যাসদেব তখন বৈকুণ্ঠের উদ্দেশ্য গমন করলেন। বৈকুণ্ঠে বিষ্ণুদেবের কাছে গিয়ে সেই বাক্য বললেন। বিষ্ণুদেব বললেন ঋষি ব্যাস, আপনি এক মহান কার্য সম্পাদনা করতে যাচ্ছেন যার ন্যায় সৃষ্টি তথা সৃষ্টির মানব আপনাকে শ্রদ্ধার সহিত স্মরণ করবে। অতঃপর বিষ্ণুদেব বললেন আপনার কার্যসিদ্ধির সমাধান আছে তবে সেটা আমার কাছে নয়। ব্যাসদেব প্রশ্ন করলো তাহলে প্রভু ব্রহ্মদেবের কাছে নেই আপনার কাছে নেই কার কাছে আছে? তখন বিষ্ণুদেব বললেন, কৈলাসপতি আদিদেব মহাদেবের কাছে। আপনি অতিশয় কৈলাস গমন করুন, অন্যতা মহাদেব দক্ষিণ কৈলাসে (দক্ষিন কৈলাস বলতে প্রেত কূল তপস্যা) প্রস্থান করবেন। ব্যাসদেব বিলম্ব না করে কৈলাস গমন করলেন। কৈলাসে গিয়ে ব্যাসদেব মাতা পার্বতী ও মহাদেবের সামনে উপস্থিত হইলেন, তিনি কিছু বলার আগেই মহাদেব বললেন, ঋষি ব্যাস অসাধারণ আপনার চিন্তা ভাবনা যা আজ পর্যন্ত কেউ অনুভব করেনি তা আপনি আঁকার দিতে যাচ্ছেন। ঋষি ব্যাস বললেন হে পরম পুজনীয় প্রভু আপনি অন্তর্লীন, আমার কার্যসিদ্ধির পথ সন্ধান হেতু আপনার শরণাপন্ন হওয়া, আমাকে পথ দেখান প্রভু। মহাদেব বললেনহে ঋষি ব্যাস অবশ্যই, মহাদেব তখন গণেশকে ডাকলেন আর বললেন পুত্র যাও ঋষি ব্যাসের সাথে। \n" +
                "র জন্য কিছু গ্রন্থ রচনা করবেন আর তুমি তা লিপিবদ্ধ করবে। গণেশ বললো এই কাজের জন্য আমাকে কেন প্রয়োজন, এই কাজ উনি নিজেই করতে পারে? মহাদেব বললেন, পুত্র এ এক নতুন ইতিহাস রচিত হতে যাচ্ছে যা ঋষি ব্যাসের পক্ষে একা রচনা করা ও লিপিবদ্ধ করা সম্ভবপর নয়, আর পুত্র এই কার্যে নিজেকে জড়িতকরা সৌভাগ্যবান মনে কর কেননা যখনই এই শাস্ত্র গ্রন্থের নামউল্লেখ হবে তখুনি তোমার নাম মানবজাতি শ্রদ্ধার সহিত স্মরণ করবে। ঋষি ব্যাসের সাথেতুমিও অক্ষয় হয়ে থাকবে। তখন গণেশ বললো চলুন ঋষিবর। ঋষি ব্যাস তখন গণেশকে নিয়ে তার আশ্রমে ফিরে আসিলেন। ঋষি ব্যাস গণেশকে নিয়ে অশ্বত্থ বৃক্ষের নীচে বসলেন। ঋষি ব্যাস প্রথমে ঋষিদের মুখাশ্রীত বাণী রচিত করবেন চিন্তা করলো, তখনই গণেশ বললো, ঋষিবর আমি লেখা শুরু করলে আর বন্ধ করতে পারবো না, যদি বন্ধ হয় তাহলে আর লিখিতে পারবো না। ঋষি ব্যাস তখন চিন্তায় পরে গেলেন, তিনি ভাবলেন পার্বতী নন্দন লেখা বন্ধ করলেআমার গ্রন্থ রচনা হবে কি করে আবার গণেশকে যদি স্থগিত করতে না পারি তাহলে রচনা চিন্তা করবো কিভাবে। এদিকে গণেশ বলছেশুরু করুন ঋষিবর। কিছুক্ষণ চিন্তা করে ঋষি ব্যাস মনস্থিরকরলেন আমি শ্লোক আকারে কিছু রচনা করবো তখন কি রচিত করলাম তা জানার জন্য গণেশের মনে আগ্রহ জাগবে। তখন রচিত শ্লোকের অর্থ বুঝতে বুঝতে আমিআরেকটি রচনা করতে পারবো। তখন ঋষি ব্যাস বললেন হে সিদ্ধিবিনায়ক শুরু করুন তবে, প্রথমে ঋষি ব্যাস একটি শ্লোক রচিত করলো অতঃপর গণেশ বললো, একটু অপেক্ষা করুন ঋষিবর। ঋষিব্যাস বললো হে গণপতি? গণেশ বললো আপনি কি রচনা করলেন? ঋষি ব্যাস বললো, আপনি সিদ্ধিনাথ দেবকূলের পরম জ্ঞানী আমি যা রচিত করছি তা আপনার না জানার কথা নয়। আমি তো মনে করেছিলাম আমার কোন ভুল রচনা আপনি ধরবেন কিন্তু আমি কি রচনা করেছি তা আপনি জানতে চাইছেন এইটা আমার জন্য কোন পরীক্ষা নয়তো? তখন গণেশ বললো, ঠিক আছে আপনি যা রচনা করবেন তা আমি বোঝে উঠা পর্যন্ত আরেকটি বলবেন না। মনেমনে ঋষি ব্যাস বললো এটাই তো আমি চেয়েছিলাম। গণেশ বললো আপনি কি চিন্তা করছেন? ঋষিব্যাস বললো, না কিছু না, ঠিক আছে গণপতি আপনার যা ইচ্ছা। এভাবে ঋষি ব্যাস একটি শ্লোক রচিত করে আর গণেশ তা পূণাঙ্গ রুপে বুঝে উঠবার আগে আরেকটি শ্লোক রচিত করে। ক্রমাগত এভাবে ঋষি ব্যাস প্রথমে চারটি বেদ পরে আটারোটি পুরাণ রচিত করে এবং গণেশ নিজের অজান্তে চারটি বেদও আটারোটি পুরাণ লিপিবদ্ধ করে। এই গ্রন্থ রচনার পরে তাঁর নাম হয় কৃষ্ণ দ্বৈপায়ণবেদব্যাস।\n");
    }

    private void fourthStory() {
        title.setText(R.string.eklobbo);
        description.setText("একলব্য ছিলেন নিষাদরাজ হিরণ্য-ধনু আর রাণী বিশাখার সন্তান। একলব্যকে আবার হিরণ্য-ধনুর পালক সন্তান হিসেবেও অভিহিত করা হয়েছে। নিষাদ ছিল এই ভারতীয় উপমহাদেশের আদিবাসীদের জোট। তারা ছিল প্রকৃতির সন্তান। শিকার করা, বনেজঙ্গলে ঘুরে বেড়ানো এগুলো ছিল তাদের দৈনন্দিন জীবনের অংশ। আর্যরা যখন এ অঞ্চলে এলো, তারা স্থানীয়দের দমিয়ে রাখার জন্য যে প্রচেষ্টাগুলো করে তার মধ্যে ছিল বর্ণ প্রথার প্রচলন। ব্রাহ্মণ, ক্ষত্রিয়, বৈশ্য আর শূদ্র এই প্রধান চার শ্রেণীর মধ্যে নিষাদরা ছিল সবচেয়ে নিচের শূদ্র বর্ণভুক্ত। উপরের সম্প্রদায়ের লোকজন নিচের সম্প্রদায়ের লোকদের এড়িয়ে চলত। সমাজের উচু জাতের মানুষ ছিল ব্রাহ্মণ আর ক্ষত্রিয়রা, যারা ছিল আর্য বংশোদ্ভূত। আর্যরা স্থানীয় নিষাদদের বর্বর, অশিক্ষিত বলে প্রচার করে। রামায়ণে বাল্মীকি নিষাদদের অভিশপ্ত করেন এই বলে যে তারা কোনদিন সামাজিক স্বীকৃতি পাবে না, কেননা জনৈক নিষাদ এক বক শিকার করায় তার সঙ্গী কষ্ট পেয়েছিল। তো, একলব্য ছিলেন এমনি একজন নিষাদ। তরুণ বয়সে তিনি স্থির করেন তিনি অস্ত্রবিদ্যা শিক্ষার আরও গভীরে যেতে চান, আর এজন্য তার গুরু দ্রোণের কাছে দীক্ষা নেওয়া প্রয়োজন। দ্রোণ ছিলেন আচার্য ভরদ্বাজের সন্তান, যিনি বাবার কাছে শাস্ত্র শিক্ষা সমাপ্ত করার পর অগ্নিবেশ্যের কাছে যান যুদ্ধবিদ্যা শিখতে। এরপর তিনি পরশুরামের কাছে শিষ্যত্ব গ্রহণ করে। তার ব্রাহ্মণ হয়েও যুদ্ধ-বিদ্যার্জন সমাজে তার জন্য কিছু সমস্যা তৈরি করে। কেননা যুদ্ধ ক্ষত্রিয়দের কাজ। কে সমাজে কোন দায়িত্ব পালন করবে তা তার সম্প্রদায় দিয়ে নির্ধারিত ছিল।\n" +
                "\n" +
                "যদিও ব্রাহ্মণ হওয়ায় দ্রোণকে এত সমস্যা সামলাতে হয়নি। তিনি একসময় কুরু রাজ্যের রাজপুত্রদের শিক্ষক নিযুক্ত হন। একে তো দ্রোণ আর্য ব্রাহ্মণ, তার উপর তিনি রাজপরিবারের শিক্ষক। তিনি অনার্য একলব্যকে শিষ্য হিসেবে গ্রহণ করবেন কিনা এই সন্দেহ থাকলেও একলব্য দ্রোণের শিষ্যত্ব লাভের আশায় কুরু রাজ্যের রাজধানী হস্তিনাপুরের দিকে রওনা দেন। রাজধানীতে পৌঁছানোর পর তিনি মানুষের দুর্ব্যবহারের শিকার হন। কিন্ত মনোবল না হারিয়ে তিনি দ্রোণের আশ্রম খুঁজে বের করেন। আশ্রমে পৌঁছে তিনি দেখেন দ্রোণ রাজা ধৃতরাষ্ট্রের একশ পুত্র আর পঞ্চপাণ্ডবদের সাথে কথা বলছেন। একলব্যের মনে তখন গুরু সামনে আসার আনন্দ, যেন তিনি তীর্থযাত্রা সম্পন্ন করলেন। গুরুকে বিরক্ত না করে একলব্য চুপচাপ দাঁড়িয়ে থাকেন। একসময় দ্রোণাচার্যের চোখ পড়ে একলব্যের উপর। তাকে কাছে ডাকেন দ্রোণ। তার আসার কারণ জানতে চান। যখন একলব্যের উদ্দেশ্যের কথা জানতে পারেন  তখন  দ্রোণ পরে যান মহা বিপদে। রাজপরিবারের শিক্ষকদের বহিরাগতদের শিষ্য হিসেবে নেয়া নিষেধ। এদিকে নিষাদরা ছিল রাজা জড়-সন্ধের মিত্রশক্তি, আর জড়সন্ধ কুরু রাজ্যের প্রতিদ্বন্দ্বী। প্রতিপক্ষের কাছে নিজের জ্ঞান প্রকাশ পাওয়া কখনোই কাম্য নয়। আর দ্রোণ যত বড় গুরুই হওন না কেন, বেড়ে উঠেছেন তো বিভেদ দেখেই, তো জাতিভেদের দেয়াল না ডিঙাতে পেরেই হোক আর রাজপরিবারের প্রথা ভাঙতে না চাওয়ার জন্যই হোক অথবা প্রতিপক্ষ শক্তির প্রতি রাজনৈতিক সতর্কতা অবলম্বনের খাতিরেই হোক, তিনি একলব্যকে শিষ্য হিসেবে গ্রহণ করেন না। যে গুরুর শিষ্যত্ব লাভের আশায় একলব্য এতদিন স্বপ্ন দেখেছেন, তার এহেন আচরণে তিনি মর্মাহত হন। এ কোন দ্রোণাচার্য? যাকে তিনি মনে এতদিন তার শিক্ষকের সম্মান দিয়েছেন এ তো সেই আচার্য দ্রোণ নন! তবে কি শুধুমাত্র শূদ্র বলে তিনি যুদ্ধবিদ্যার সর্বোচ্চ জ্ঞানার্জনের অধিকার রাখেন না? কিন্ত শিক্ষকের কাছে তো জাত কোন সমস্যা হওয়ার কথা নয়, তার কাছে যে কোন বিদ্যার্থীই আমন্ত্রিত। এইসব ভাবতে ভারাক্রান্ত মনে একলব্য বের হয়ে আসেন। তিনি হস্তিনাপুরের কাছেই এক বনে আশ্রয় গ্রহণ করেন। সেখানে তিনি তার সাধনা শুরু করেন। দ্রোণকেই তিনি তার গুরু হিসেবে মনে ঠাঁই দিয়ে রেখেছেন। তিনি দ্রোণের একটা মূর্তি তৈরি করেন। রক্তমাংসের দ্রোণ তাকে গ্রহণ করেননি তো কি হয়েছে, তার অস্ত্র-প্রশিক্ষণ তো আর থেমে থাকতে পারে না! মানুষ দ্রোণ হয়তবা তার পার্থিব কোণ সীমাবদ্ধতার কারণে তাকে ছাত্র হিসেবে মেনে নেননি, কিন্তু তার মনে আচার্য দ্রোণই এখনও তার শিক্ষক, যে দ্রোণ মানব সমাজের বিধিনিষেধের ঊর্ধ্বে, যিনি একজন আদর্শ শিক্ষক, যার কাছে জাতপাতের কোন ভেদাভেদ নেই।\n" +
                "\n" +
                "নিয়মিত একলব্য দ্রোণের মূর্তির কাছে সম্মান প্রদর্শন করে তার চর্চা চালিয়ে যান। একসময় তিনি কল্পনার অতীত দক্ষতা অর্জন করেন। কিন্ত এই দক্ষতাই তার জন্য যেন কাল হয়ে দাঁড়ায়।\n" +
                "\n" +
                "একদিন একলব্য গভীর সাধনায় নিমগ্ন ছিলেন। এই সময় একটি কুকুর প্রচণ্ড শোরগোল করে তাকে বিরক্ত করে। কোনভাবেই কুকুরটিকে তাড়াতে না পেরে একলব্য কুকুরটির মুখে সাতটি তীর এমন দক্ষতার সাথে বিদ্ধ করেন যে, কুকুরটি বেচে থাকে কিন্ত তার আওয়াজ করার জন্য মুখ নাড়ানোর অবস্থা থাকে না। এটি ছিল রাজবংশের পোষা কুকুর। রাজপুত্ররা যখন কুকুরটি পায়, তারা বুঝতে পারে কাছেই কোথাও অসাধারণ একজন অস্ত্র-বিশারদ আছে। তারা খুঁজতে খুঁজতে একলব্যকে পেয়ে যায়। তারা একলব্যের পরিচয় জানতে চাইলে তিনি নিজেকে দ্রোণের শিষ্য বলে দাবী করেন। একে কুরুবংশের কেউ তো নয়ই, বরং সামান্য একজন শূদ্রের এত দক্ষতা দেখে অর্জুন, দ্রোণের সেরা ছাত্র, ঈর্ষা বোধ করেন। তিনি দ্রোণের কাছে গিয়ে অভিযোগ করেন যে দ্রোণাচার্য  অর্জুনকে পৃথিবীর শ্রেষ্ঠ ধনুর্ধর হিসেবে গড়ে তুলতে তো পারেন নিই, উলটো তার সেরা ছাত্র হচ্ছে সামান্য এক নিষাদ, এক শূদ্র, একলব্য। অথচ দ্রোণ প্রতিজ্ঞা করেছিলেন তাকে অপ্রতিদ্বন্দ্বী করে গড়ে তুলবেন। দ্রোণ তো কেবল আকাশ থেকেই পড়লেন না, তার মাথায় একই সাথে যেন আকাশও ভেঙে পড়ল। কোথাকার কোন একলব্য নাকি তার শিষ্য, যাকে তিনি তাড়িয়ে দিয়েছিলেন, আর তিনিই নাকি তাকে এমন শিক্ষা দিয়েছেন যা নাকি অর্জুনেরও নাগালের বাইরে! পড়িমরি করে ছুটলেন দ্রোণ অর্জুনকে নিয়ে একলব্যের খোঁজে। একলব্য তার ঘরের বাইরে গুরুকে দাঁড়িয়ে থাকতে দেখে আনন্দে আত্মহারা হয়ে গেলেন। আগের বার গুরু তার আগ্রহে ঠাণ্ডা পানি ঢেলে দিয়েছিলেন। এইবারও একলব্যের গুরু দর্শনের আনন্দ শেষ হবে আরেকটি বিষাদের গল্পের মধ্য দিয়ে। দ্রোণ যখন একলব্যের কৌশলের কিছু নমুনা দেখলেন, তার তো চক্ষু চড়কগাছ! এই ছেলে এইসব কোথায় শিখল? এ তো অর্জুনকে কি, দ্রোণকেই ছাড়িয়ে যাবার আভাস দেখাচ্ছে! আবার বলে কিনা তারই শিষ্য! আগাগোড়া কিছু বুঝতে না পেরে তিনি একলব্যের কাছে জানতে চান যে কখন তিনি এসব একলব্যকে শেখলেন। একলব্য দ্রোণের মূর্তিটি দেখিয়ে বলেন, আমি সবসময় মনে করি আপনি আমাকে পথ দেখাচ্ছেন, আর এই ভেবেই চর্চা করি। আপনি আমাকে শিষ্য হিসেবে গ্রহণ করেননি, কিন্ত আমার চোখে আপনিই আমার একমাত্র গুরু। দ্রোণ যেন পড়েছেন ইঁদুরের ফাঁদে। একজন মানুষ একাগ্র সাধনা করে শুধুমাত্র নিজে এমন উচ্চতায় নিয়ে গেছে নিজের যোগ্যতাকে, তা দেখে মুগ্ধ হওয়ার কথা হলেও, দ্রোণ যেন এবারও নিজের মনের সংকীর্ণতা থেকে বের হতে পারেন না । তিনি নিজের মনের ফাঁদ থেকে বের হবার জন্য এক কটুচাল চালেন। তিনি একলব্যের কাছে গুরুদক্ষিণা চেয়ে বসেন। গুরুদক্ষিণা চাইবার একটাই মানে, তিনি একলব্যকে শিষ্য হিসেবে শেষ পর্যন্ত স্বীকৃতি দিচ্ছেন। গুরুদক্ষিণা চাওয়ার প্রথা সাধারণত শিক্ষা সমাপ্ত হলে দেখা যেত। এতদিন যে শিক্ষককে প্রায় দেবতুল্য সম্মান দিয়েছেন, আজ তার শিষ্যত্ব পাবেন ভেবে একলব্য সরলমনে দ্রোণের যে কোন চাওয়া পূরণের প্রতিজ্ঞা করেন। দ্রোণ এইটাই আশা করছিলেন। তিনি একলব্যের ডান হাতের বুড়ো আঙুল চেয়ে বসেন। প্রতিজ্ঞাবদ্ধ ছাত্র শিক্ষকের ধূর্তামি শেষ মুহূর্তে বুঝতে পারলেও নিজের কথা রাখেন। আর দ্রোণ অর্জুনের সম্ভাব্য প্রতিদ্বন্দ্বীকে দমিয়ে দিতে পেরেছেন ভেবে ফিরে যান। কিন্ত তিনি ভাবেননি অর্জুনের আরেকজন শক্ত প্রতিদ্বন্দ্বী আছে-কর্ণ। কিন্ত সে আরেক-গল্প, অন্য সময় বলা যাবে।\n" +
                "\n" +
                "এতকিছুর পরও একলব্য থেমে যান না। বারবার বাধা পাওয়ায় একলব্যের যেন জেদ চেপে বসে, যেভাবেই হোক তীর চালনায় নিজেকে অন্য উচ্চতায় নিয়ে যাওয়ার। তিনি চার আঙুল দিয়েই আবার কঠোর সাধনা শুরু করেন। দিনের পর দিন কঠিন চর্চা করে তিনি যে আবারও নিজেকে ছাড়িয়ে যান। নিজের সাধনা পরিপূর্ণ হলে রাজ্যে ফিরে আসেন।\n" +
                "\n" +
                "একলব্য সমাজের সেই মানুষগুলোর প্রতিনিধি যারা শুধুমাত্র সমাজের বিধিনিষেধের জন্য দমে থাকার মানুষ নন। যত বাধাই আসুক তা যেন তাদের লক্ষ্য অর্জনের স্পৃহা আরও বাড়িয়ে দেয়। কিন্ত এই একাগ্র সাধকের পরিণতি মহাভারতের অন্য অনেক চরিত্রের মতই করুণ। তিনি এক পর্যায় কৃষ্ণের সাথে যুদ্ধে জড়িয়ে পরেন। রাজা কৃষ্ণ তাকে পাথরাঘাতে হত্যা করেন। এক ধারণা অনুযায়ী, রাজা জড়-সন্ধের মিত্র, শিশুপল, চেদি রাজ্যের রাজপুত্র, তার হবু পত্নী কৃষ্ণের সাথে পালিয়ে গেলে একলব্য, জড়সন্ধ আর শিশুপল তাদের তাড়া করেন। ক্রোধান্বিত কৃষ্ণ পাথর দিয়ে আঘাত করে একলব্যের খুলি গুঁড়ো করে ফেলেন। আরেক মত অনুযায়ী, যুধিষ্ঠিরের রাজসূয় যজ্ঞে রাজা জড়সন্ধ খুন হন, যেখানে কৃষ্ণের হাত ছিল। এতে ক্রোধান্বিত একলব্য কৃষ্ণ আর দ্বারকার সব যাদবকে হত্যা করে প্রতিশোধ নিতে প্রতিজ্ঞা করেন। এই আক্রমণের সময়ই তিনি নিহত হন। আরেক মত অনুযায়ী, কুরুরাজ্যের রাজপুত্র দুর্যোধন একলব্যকে হস্তিনাপুরের সমস্ত বনের রাজা ঘোষণা করেন। পরে একসময় দুর্যোধনের কথায় কৃষ্ণের ছেলে সাম্বাকে হত্যা করতে গেলে একলব্য কৃষ্ণ-যাদবের হাতে খুন হন। আর এভাবেই শেষ হয় মহাভারতের অন্যতম শ্রেষ্ঠ এক  তীরন্দাজের জীবন ।\n");
    }

    private void fifthStory() {
        title.setText(R.string.upomonu);
        description.setText("মহাভারতের গল্পে আয়োদ ধৌম্য নামে এক সুবিখ্যাত ঋষি ছিলেন। তাঁর অনেক শিষ্যের মাঝে উপমন্যু নামে এক শিষ্য ছিলেন। যে সময়ের কথা বলছি, তখন সবে উপমন্যু গুরুর আশ্রমে এসেছেন বেদপাঠের শিক্ষা নিতে। কিন্তু, সে যুগে শিক্ষা খুব একটা সহজ ছিল না। শিক্ষা তখন সমস্তটাই শ্রুতি। শুনে শুনে মনে রাখতে হত। শিক্ষার ওপর প্রবল অনুরাগ ও জেদ না-থাকলে এভাবে শিক্ষিত হওয়া যায় না। তাই অপাত্রে শিক্ষা দান করা হত না, অযোগ্যকে শিষ্য হিসেবে গ্রহণ করা হত না। শিষ্যকে নিয়মনিষ্ঠাসংযমে গুরুর আশ্রমে বাস করতে হত। তাকে নানান অবস্থার মধ্যে ফেলে যাচাই করে নেওয়া হত। গুরুর গৃহকর্ম ও পদসেবা করতে হত। উপমন্যুর ক্ষেত্রেও তার ব্যতিক্রম হল না। আয়োদ ধৌম্য তাঁকে আশ্রমের গাভিগুলি চারণের ভার দিলেন।\n" +
                "উপমন্যু প্রতিদিন গরু চরাতে যান আর সন্ধ্যাবেলায় ফিরে গুরুর চরণ বন্দনা করেন। এবং তারপরই সারাদিনে একবার মাত্র আহার গ্রহণ করেন। তাতে শরীর কৃশ হওয়ার কথা। কিন্তু, আয়োদ ধৌম্য খেয়াল করলেন, উপমন্যুর শরীর যেন দিনকে দিন আরও হৃষ্টপুষ্ট হয়ে উঠছে। তাই একদিন তিনি শিষ্যকে বললেন, বাপু হে, গোচারণে গিয়ে দিনমানে তুমি কি কিছু খাও? উপমন্যু হাতজোড় করে সত্য কথাই বললেন, গুরুদেব, আমি দিনমানে ভিক্ষা করি, তাতে যা পাই তাই খাই। আয়োদ ধৌম্য বললেন, এ তো বাপু ঠিক না, তুমি ভিক্ষে করে আহার করো, অথচ গুরুকে নিবেদন করোনা, এ তো ভালো শিক্ষা না! তাই শুনে উপমন্যু লজ্জিত হলেন। তিনি এরপর থেকে ভিক্ষে করে তা গুরুকে নিবেদন করতে শুরু করলেন।এবং, আয়োদ ধৌম্যও সেই ভিক্ষায় পাওয়া আহার্য সামগ্রীর প্রায় সবটাই গ্রহণ করতে লাগলেন। যেটুকু থাকত তাতে আর উপমন্যুর পেট যেন ভরতেই চায় না! এমনি করে কিছুদিন গেল। তবু গুরুদেব অবাক হয়ে দেখলেন শিষ্যের শরীর যে কে সেই, অর্ধাহারের ছাপ তো নেই! আবার জিজ্ঞেস করলেন, বাপু ব্যাপারখানা কি বল তো? উপমন্যু বললেন, প্রভু, আপনাকে নিবেদন করার পর আমি আবার ভিক্ষা করি, তা থেকে যা পাই তাই দিয়ে উদর পূরণ করি। তাই শুনে আয়োদ ধৌম্য বললেন, এ তোমার ভারি অন্যায়, এতে তো অন্য ভিক্ষুকদের অন্নসংস্থানের ক্ষতি হচ্ছে, তুমি তাদের বঞ্চিত করছ! এতে ভারি লজ্জা পেয়ে উপমন্যু গুরুর পদবন্দনা করে বললেন, ক্ষমা করুন প্রভু, এমন অন্যায় আর হবে না।\n" +
                "আরও কিছুদিন গেল। তবু দেখা গেল, দিনকে দিন আরও হৃষ্টপুষ্ট হচ্ছেন উপমন্যু। গুরুদেব আবার জিজ্ঞেস করলেন, ব্যাপারখানা কি? উপমন্যু জানালেন, দিনের বেলায় তাঁর খুব ক্ষিদে পায়, কিছুতেই চাপতে পারেন না। ভিক্ষেও বন্ধ। তাই এখন তিনি গভীদের দুধ পান করেন। এতে গুরুদেব খুব রেগে হলেন, বলো কী! আশ্রমের গাভীর দুধ পান করছ, বাছুরদের বঞ্চিত করছ, এ তো আরও অন্যায় করছ উপমন্যু! এরপর, উপমন্যুর দুধ খাওয়া বন্ধ হল। কিন্তু, কিছুতেই খিদের জ্বালা সহ্য করতে পারলেন না। বাছুরদের দুধ খাওয়ার পর কষ বেয়ে যে ফেনা গড়িয়ে পড়ে, তা-ই আহার করতে শুরু করলেন। তাতেও গুরুর ভর্ৎসনা শুনতে হল। গুরু বললেন, এতে নাকি বাছুরেরা উপমন্যুকে দয়া করে নিজেদের অভুক্ত রাখছে, দুধ কম খেয়ে কষ বেয়ে গড়িয়ে দিচ্ছে! ফলে, বাছুরদের মুখের ফেনা খাওয়াও বন্ধ হল।\n" +
                "খুব আতান্তরে পড়লেন উপমন্যু। দিনের বেলা গোচারণে গিয়ে যথারীতি খিদের জ্বালায় অস্থির হলেন। কিন্তু, গুরুর নিষেধে ভিক্ষে করতে পারলেন না, দুধ বা দুধের ফেনা কিছুই খেতে পেলেন না। মাঠের মাঝে খাবারই বা কোথায়! খিদের জ্বালা সইতে না পেরে সামনে একটা আকন্দের গাছ দেখতে পেয়ে হতবুদ্ধি হয়ে তার পাতা খেতে লাগলেন। তাতেই হল বিপত্তি। চোখ অন্ধ হয়ে গেল। সেই অবস্থায় হাতড়ে হাতড়ে আশ্রমে ফিরতে গিয়ে তিনি পড়ে গেলেন একটা শুকনো কুয়োয়।\n" +
                "দিন ফুরিয়ে সন্ধ্যে হল। গাভীগুলো একা একা বাড়ি ফিরে গেল। তবু, উপমন্যু ঘরে ফিরলেন না। তখন খুব চিন্তা হল আয়োদ ধৌম্যর। তিনি খুঁজতে বেরুলেন শিষ্যকে। মাঠে গিয়ে তিনি উপমন্যুর নাম ধরে ডাকতে লাগলেন। কুয়োর ভেতর থেকে সাড়া দিয়ে উপমন্যু গুরুকে জানালেন নিজের দুরবস্থার কথা। গুরু বললেন স্বর্গের চিকিৎসক অশ্বিনীকুমারদের স্তব করতে। উপমন্যুর স্তবে তুষ্ট হয়ে অশ্বিনীকুমাররা আবির্ভুত হলেন এবং উপমন্যুর দৃষ্টি ফিরিয়ে দিলেন। আশীর্বাদ করলেন সর্বশাস্ত্রজ্ঞ ও বেদজ্ঞ হয়ে ওঠার। এদিকে আয়োদ ধৌম্যও শিষ্যের এই কৃচ্ছসাধনায় ভারি তুষ্ট হলেন। তিনি পঞ্চপিতার এক পিতা, গুরুদেব। সন্তানসম শিষ্যকে শিক্ষাদানের পূর্বে তাঁর আধার বুঝে নিচ্ছিলেন। আধার অনুযায়ী এবার দেওয়া হবে শিক্ষা। উপমন্যুর মতো শিষ্য পেয়ে তিনি আজ গর্বিত। শিষ্যকে কুয়ো থেকে তুলে জড়িয়ে ধরলেন বুকে। উপমন্যুর শ্রমপর্ব শেষ হল, শুরু হল আশ্রমের শিক্ষা পর্ব।\n");
    }

    private void sixthStory() {
        title.setText(R.string.aruni);
        description.setText("অনেক অনেক দিন আগের কথা। তখন ছাত্ররা গুরুগৃহে খেকে লেখাপড়া করত। সেই সময় ধৌম্য নামে একজন আচার্য বা শিক্ষক ছিলেন। তাঁর ছিল তিনজন ছাত্রছিল। তাদের নাম ছিল আরুণি, উপমন্যু, এবং বেদ।\n" +
                "একদিন খুব বৃষ্টি হয়েছে। গুরু আরুণিকে ডেকে বললেন, জমি থেকে জল বের হয়ে যাচ্ছে, তুমি গিয়ে জমির আইল বেঁধে এসো। গুরুর আদেশে আরুণি চলে সেই জমির আইল বাঁধতে। কিন্তু অনেক চেষ্টা করেও জমির জল আটকাতে পারছিল না। আরুণিঅন্য কোন উপায়ও খুঁজে পেল না, অবশেষে নিজেই ভাঙ্গা অাইলের উপর শুয়ে পড়ল। জল বেরিয়ে যাওয়া বন্ধ হলো। এদিকে সূর্য্য ডুবে গেছে। চারিদিক অন্ধকার হয়ে আসছে। কিন্তু আরুণি ফিরে আসছে না। গুরুদেব চিন্তিত হয়ে পড়লেন। অবশেষে আরুণির খোঁজে বের হলেন, সঙ্গে তার আরও দুইজন শিষ্য।\n" +
                "অবশেষে গুরুদেব জমির কাছে গেলেন এবং উচ্চস্বরে আরুণি আরুণি বলে ডাকতে লাগলেন! বৎস তুমি কোথায় চলে এসো। গুরুদেবের ডাক শুনে আরুণি বলল, গুরুদেব আমি জমির আইলে শুয়ে আছি। তখন গুরু বললেন উঠে এসো। আরুণি গুরুর কাছে এসে তাঁকে প্রণাম করল। তার পর সমস্থ ঘটনা গুরুকে খুলে বলল। আরুণির কথা শুণে গুরুদেব অত্যন্ত খুশি হলেন।তিনি তাকে গুরুভক্তির জন্য আর্শীবাদ করে বললেন তোমার সমস্থ বিদ্য্যা অর্জিত হবে, তুমি দেশে ফিরে যাও। আর তুমি জমির আইল থেকে উঠে এসেছ। তাই তোমার নতুন নাম হবে উদ্দালক। গুরুর আর্শীবাদ পেয়ে আরুণি নিজের দেশ পঞ্চালে ফিরে গেল। আরুণির গল্প থেকে আমরা যেন, সকল গুরুজন ব্যক্তিদের শ্রদ্ধা করতে শিখি। ভক্তি ছাড়া জীবনে সফল হওয়া যায়না।\n");
    }

    private void seventhStory() {
        title.setText(R.string.narod);
        description.setText("নারদমুনি একবার বিশ্বব্রহ্মাণ্ড ঘুরে বেড়াচ্ছিলেন। একসময় তিনি বিন্ধ্য পর্বতের পাশ দিয়ে যাচ্ছিলেন, বিন্ধ্য পর্বতশ্রেণীর অবস্থান বর্তমানে ভারতের পশ্চিমাঞ্চলে। বিন্ধ্য পর্বতশ্রেণি অঞ্চলকে বিন্ধ্যাচলও বলা হয়। যদিও এটি ভূতাত্ত্বিকভাবে কোনো সঠিক পর্বতশ্রেণি নয়, বরং গিরিখাদ, পর্বত, মালভূমি এবং উপত্যকার সমন্বয়ে গঠিত। তো আবার কাহিনীতে ফেরা যাক।\n" +
                "বিন্ধ্য পর্বতের পাশ কাটিয়ে যাওয়ার সময় বিন্ধ্য পর্বতের বিশালতা দেখে এবং এর সৌন্দর্যে মুগ্ধ হয়ে নারদ মুনি বিন্ধ্য পর্রবতের সাথে কথা বলার ইচ্ছা প্রকাশ করেন। তিনি এগিয়ে গেলেন বিন্ধ্য পর্বতের কাছে। তিনি যদিও বিন্ধ্যের সাথে কেবল কথা বলতে যাচ্ছিলেন, কিন্তু তিনি তো অনর্থের দেবতা। অনর্থ না করে কি হয়! কথা বলার ছলে তিনি বিন্ধ্যকে খানিকটা রাগিয়ে দিতে চাইলেন।\n" +
                "\n" +
                "নারদ মুনি বললেন,“ওহে, বিন্ধ্য পর্বত। তোমার এই অপরূপ সৌন্দর্য যেকোনো পর্বতের সৌন্দর্যের থেকে অতুলনীয়। সৌন্দর্যের দিক থেকে তুমি সকল পর্বতের থেকে অগ্রগামী হতে পারতে। অন্তত আমি তা-ই মনে করি। কত প্রকার জীবদের তুমি ধারণ করে আছ। কত মুনি ঋষি তোমাকে আশ্রয় করে আছেন, গুহায় বসে নির্বিঘ্নে তপস্যা করছেন।”\n" +
                "কিন্তু এসব বলে নারদ মুনি মলিন মুখে দীর্ঘঃশ্বাস ছাড়লেন। নারদ মুনির গুণগান শুনে বিন্ধ্য পর্বত খুবই গর্ববোধ করছিল এতক্ষণ, কিন্তু তাঁর দীর্ঘঃশ্বাসও বিন্ধ্যের নজর এড়াল না। সে বলল, আপনাকে অনেক ধন্যবাদ মহর্ষি নারদ। কিন্তু আপনি যেমন বললেন যে, আমি মহৎ পর্বত হতে পারতাম। আমি কি মহৎ পর্বত নই?”\n" +
                "নারদ বুঝতে পারলেন, পরিকল্পনা সফল হয়েছে। কিন্তু মুখে সেই মলিন ভাব বজায় রেখে ইতস্তত ভাব দেখিয়ে বললেন,“আমি তো বলতে চাই, তুমিই পর্বতদিগের মধ্যে সেরা, কিন্তু সেটা সঠিক হবে না।”\n" +
                "\n" +
                "বিন্ধ্য পর্বতের এবার ক্রোধের উদ্রেক হলো। সে বলল, কেন? আমার থেকে মহৎ পর্বত আর কে আছে?”\n" +
                "\n" +
                "নারদ মুনি এবার একটু শঙ্কিত হলেন। কারণ তিনি জানেন যে, যদি তিনি এই প্রশ্নের সঠিক উত্তর দেন, তাহলে বিন্ধ্য আরো ক্রুদ্ধ হবে এবং তাঁকে পিষেও ফেলতে পারে ক্রোধে। কাজেই নিজের পিঠ বাঁচিয়ে তিনি বললেন,\n" +
                "ওহে, মহৎ পর্বত! আমি এ কথা বলতে পারব না তোমাকে। কিন্তু তুমি সূর্যের অনেক কাছাকাছি আছ। তাকে জিজ্ঞাসা করো যে, সে কোন পথে যাতায়াত করে। তাহলেই তুমি তোমার প্রশ্নের উত্তর পেয়ে যাবে।”\n" +
                "এই বলে নারদ মুনি তাড়াতাড়ি সরে পড়লেন সেখান থেকে।\n" +
                "নারদ মুনির কথা অনুযায়ী ক্রোধান্বিত বিন্ধ্য সূর্যকে আহ্বান করল। সূর্যদেব তখন অস্ত যাচ্ছিলেন। বিন্ধ্য এতটাই ক্রুদ্ধ ছিল যে, সে সূর্যদেবকে প্রণিপাত পর্যন্ত করল না। বরং রাগান্বিতভাবে জিজ্ঞেস করল, সূর্যদেব, আপনার যাত্রাপথের বিবরণ দিন আমাকে এখনই।”\n" +
                "সূর্যদেব নারদ মুনির অনর্থের ব্যাপারে কিছুই জানতেন না, তারপরও বিন্ধ্যের রাগান্বিত চেহারা দেখে বলে দিলেন,\n" +
                "“আমি তো প্রতিদিন পূর্ব থেকে শুরু করে পশ্চিমদিকে যাত্রা করি, এরপর মেরু পর্বতের পাশ কাটিয়ে শেষে অস্ত যাই।”\n" +
                "তখন বিন্ধ্য বললো, আচ্ছা, তাহলে মেরু পর্বতই সেই পর্বত! কোথায় মেরু পর্বত?” সূর্যদেব এবার আরো শঙ্কিত হয়ে বললেন,“স্বয়ং ব্রহ্মাদেব মেরু পর্বতে থাকেন এবং এটি এতটাই উঁচু যে, তার পাশ দিয়ে যাওয়া ছাড়া আমার আর কোনো পথ নেই।”\n" +
                "সূর্যের কথা শুনে বিন্ধ্য বললো,“কিন্তু আপনি আমার পাশ দিয়ে তো যান না। আমি কি ততটা গুরুত্বপূর্ণ নই?”\n" +
                "শুনে সূর্যদেব বললেন,“নাহ বিন্ধ্য, ঠিক তা নয়। মেরু অনেক উঁচু এবং সৃষ্টির আদি থেকে আমার যাত্রাপথ একই ছিল, এখনও যেমন আছে।”এই বলে সূর্যদেব আর বেশি অপেক্ষা করলেন না, অস্তে চলে গেলেন।\n" +
                "এদিকে বিন্ধ্য তখন রাগে ফুঁসছে আর পর্বতের উপর জমে থাকা পাথরখণ্ডগুলোকে নিক্ষেপ করছে ভূমিতে। মেরু পর্বত তার থেকে উঁচু এবং তার থেকে গুরুত্বপূর্ণ- একথাটি একদম মেনে নেয়া যায় না। শেষে বিন্ধ্য সিদ্ধান্ত নিয়ে নিল যে, সে নিজেকে মেরুর থেকে গুরুত্বপূর্ণ প্রমাণ করে দেখাবে। এই ভেবে সে নিজের উচ্চতা বৃদ্ধি করতে শুরু করল। তাকে মেরু পর্বতের থেকে উঁচু হতে হবে।\n" +
                "এদিকে বিন্ধ্যের এই উচ্চতা বৃদ্ধির কারণে পৃথিবী হয়ে পড়ল ভারসাম্যহীন, যা মানুষ ও দেবতা উভয়পক্ষের জন্যেই হয়ে উঠল সমস্যার কারণ। বিন্ধ্যকে থামানোর কোনো উপায় না পেয়ে শেষে দেবরাজ ইন্দ্র এলেন মুনি অগস্ত্যের কাছে। অগস্ত্য মুনি ছিলেন বিন্ধ্য পর্বতের গুরুদেব। কাজেই তাকে শান্ত করতে পারলে কেবল অগস্ত্য মুনিই পারবেন। দেবরাজ ইন্দ্র তাঁর অভিপ্রায় জানালেন মুনির কাছে। সব শুনে মুনি মুচকি হাসলেন এবং দেবরাজকে নিশ্চিন্তে ফিরে যেতে বললেন স্বর্গে।\n" +
                "কুটিরে ফিরে এসে তিনি তাঁর স্ত্রী লোপামুদ্রা এবং দুই পুত্রকে সবকিছু গুছিয়ে নিতে বললেন। এরপর তারা চারজন পথ চলা শুরু করলেন। একসময় তাঁরা পৌঁছে গেলেন বিন্ধ্য পর্বতের সামনে। তিনি বিন্ধ্যকে আহ্বান করলেন।\n" +
                "বিন্ধ্য নিচে তাকিয়ে দেখে যে, গুরুদেব অগস্ত্য ডাকছেন। সে মুনিবরকে অভিবাদন জানিয়ে করে বললেন,“গুরুদেব, আজ্ঞা করুন”।\n" +
                "মুনি তখন বললেন,“বৎস, আমি খুবই ব্যস্ত এবং খুব শীঘ্রই আমাকে দক্ষিণে যেতে হবে। কাজেই তুমি যদি নিচু হয়ে আমাদের চলার পথ করে দাও, তাহলে আমার পথ সহজ হবে”। একথা শুনে বিন্ধ্য খুশিমনে নত হয়ে তাঁদের পথ তৈরি করে দিলো। পর্বত পার হয়ে মুনি আবার বিন্ধ্যকে ডেকে বললেন,“ওহে বিন্ধ্য। আমি খুব শীঘ্রই ফিরে আসব। কাজেই আমি না আসা পর্যন্ত যদি তুমি এভাবে থাকো, তাহলে তোমারও আবার কষ্ট করে নত হতে হবে না এবং আমাকেও তোমাকে ডাকার জন্য অপেক্ষা করতে হবে না”। বিন্ধ্য গুরুর কথার গূঢ় অর্থ ভালোভাবে বুঝতে না পেরেই তাঁকে বললো,“যথা আজ্ঞা গুরুদেব, আপনি না আসা পর্যন্ত আমি এভাবেই থাকব।”\n" +
                "অগস্ত্য মুনি মুচকি হেসে সেই যে দক্ষিণে চলে গেলেন, আর ফিরলেন না। আর বিন্ধ্যও মুনির অপেক্ষায় নত হয়ে থাকল। ফলে পৃথিবীতে আবার ভারসাম্য ফিরে এল। অগস্ত্য মুনি আর ফিরলেন না, তাই বিন্ধ্য গুরুর কথা রাখতে আজো নত হয়ে আছে।\n");
    }

    private void eightStory() {
        title.setText(R.string.durbasa);
        description.setText("সতীশিরোমণি অনসূয়ার গর্ভে তিন পুত্রের জন্ম হয় – সোম, দত্তাত্রেয় ও দুর্বাসা। ব্রহ্মার অংশে সোম, বিষ্ণুর অংশে দত্তাত্রেয় ও রুদ্রের অংশে দুর্বাসার জন্ম হয়। দুর্বাসা যাঁর গৃহে যেতেন তাঁর অতিথি সৎকারে সামান্য একটু ত্রুটি হইলেই অভিশাপ দিতেন। দুর্বাসার অভিশাপ সাংঘাতিক। হাতে পায়ে ধরেও ঋষিকে প্রসন্ন করা যেত না।\n" +
                "ওর্ব ঋষির কন্যা কন্দলীকে দুর্বাসা বিবাহ করেন। বিবাহের সময়ে ঋষি বলেন –কন্যার একশত অপরাধ ক্ষমা করতে হবে। দুর্বাসা তাতে রাজী হন। কন্দলী কথায় কথায় ঝগড়া করত। অল্পদিনের মধ্যেই একশত অপরাধ পূর্ণ হয়ে গেল। তখন দুর্বাসা তাকে অভিশাপ দিয়ে ভস্ম করে দিলেন।\n" +
                "ইন্দ্রের শ্রীভ্রংশের মূলে দুর্বাসাঃ\n" +
                "দুর্বাসা একবার পৃথিবী ভ্রমণ করতে করতে এক বিদ্যাধরীর হাতে একটি সন্তানক পুষ্পের মালা দেখতে পান। ঋষি মালাটি তার হাত থেকে চেয়ে নিজে গলায় পরে স্বর্গের দিকে চললেন। পথে দেখলেন ইন্দ্র ঐরাবতে চড়ে আসছেন। ইন্দ্রকে দেখে মালাটি গলা থেকে খুলে ইন্দ্রের দিকে নিক্ষেপ করলেন। ইন্দ্রের কাছে সন্তানক মালার কোন মূল্য নেই। তাঁরই নন্দন-কাননে বারোমাস সন্তানক ফুটে থাকে। ইন্দ্র মালাটিকে ঐরাবতের গলায় ঝুলিয়ে দিলেন। ঐরাবত শুঁড় দিয়ে মালাটি নিয়ে পায়ের তলায় ফেলে দলিত করে চলে গেল। দুর্বাসা দাঁড়িয়ে দাঁড়িয়ে নিজের মালার পরিণতি দেখলেন। তখন তিনি ক্রোধে অগ্নিশর্মা হয়ে ইন্দ্রকে অভিশাপ দিলেন –তুমি শ্রীভ্রষ্ট হও। স্বর্গ থেকে লক্ষ্মী বিদায় নিয়ে সমুদ্রগর্ভে চলে গেলেন। দৈত্যরা এসে স্বর্গরাজ্য অধিকার করল। স্বর্গের দুর্দশার অবধি থাকল না। সমুদ্র মন্থন করে দেবতারা লক্ষ্মীকে আবার উদ্ধার করে আনলেন। সমুদ্র-মন্থনের মূলে দুর্বাসা।\n" +
                "শকুন্তলার পতিবিরহের মূলে দুর্বাসাঃ\n" +
                "দুর্বাসার হাতে দুর্বলা নারীও অব্যাহতি পেত না। সূর্য বংশের রাজা দুষ্মন্ত কণ্বের তপোবনে শকুন্তলাকে গান্ধর্ব মতে বিবাহ করে স্বরাজ্যে চলে গেলেন। তারপর বহু দিন চলে গেল। দুষ্মন্তের কোন সংবাদই না পেয়ে শকুন্তলা গালে হাত দিয়ে দুষ্মন্তর কথা ভাবছিল। এমন সময় দুর্বাসা আসলেন অয়মহং ভোঃ বলে গর্জন করলেন। শকুন্তলা এমনই তন্ময়ী হয়ে ছিলেন যে, দুর্বাসার আসার কথা বুঝতে পারেন নি। দুর্বাসা ক্রোধে অভিশাপ দিলেন –তুই যার কথা ভাবছিস, মনে পড়িয়ে দিলেও সে তোকে চিনতে পারবে না। শকুন্তলার সখী একথা শুনতে পেয়ে ছুটে গিয়ে ঋষির পায়ে পড়ে বলল –আপনার অবলা কন্যার উপর রাগ করবেন না – আমি পাদ্য-অর্ঘ্য নিয়ে আসছি। শকুন্তলা বড় দুঃখিনী – বর অসহায়া তাকে ক্ষমা করুন। দুর্বাসা বড়ই কঠোর প্রকৃতির ঋষি, কিন্তু নারীর আবেদনে তাঁর মন গলল। তিনি বললেন –যা বলেছি, তা ত ফিরবে না। যদি কোন অভিজ্ঞান দেখাতে পারে, তবে রাজা চিনতে পারবে। সখী অনেকটা আশ্বস্ত হল – কারণ, দুষ্মন্তের দেওয়া একটা আংটি শকুন্তলার কাছে ছিল, ভাবল – তা দেখালে হবে।\n" +
                "লক্ষ্মণ বর্জনের মূলে দুর্বাসাঃ\n" +
                "রামায়ণের রামলীলার শেষ হয়েছিল দুর্বাসার দ্বারা। কালপুরুষ এসেছিলেন রামের সঙ্গে গোপনে কিছু বলবার জন্য। রামচন্দ্র লক্ষ্মণকে দ্বারে বসিয়ে রাখলেন – কেউ যেন ঐ সময়ে রামের রাজসভায় প্রবেশ করতে না পারে। লক্ষ্মণ সকলকেই বিদায় করে দিলেন, সকলেই কারণ বুঝে চলে গেল। এমন সময় দুর্বাসা এসে উপস্থিত। দুর্বাসা বললেন – আমি এক্ষণি রামের সঙ্গে দেখা করতে চাই। লক্ষ্মণ কাকুতি-মিনতি করে বললেন –আপনি দয়া করে বশিষ্ঠদেবের গৃহে একটু অপেক্ষা করুন – কালপুরুষের সঙ্গে দাদার গোপনীয় কথা হচ্ছে। তিনি চলে গেলে আপনাকে নিয়ে যাব।\n" +
                "দুর্বাসা অগ্নিশর্মা হয়ে বলে উঠল –না, আমি অপেক্ষা করব না। যদি এক্ষণি দেখা করতে না দাও – তা হলে সমস্ত অযোধ্যা শাপের আগুনে দগ্ধ করব। লক্ষ্মণ কি করেন, তিনি রামচন্দ্রকে দুর্বাসার আসার কথা জানালেন। কালপুরুষের সঙ্গে কথা ছিল – তাঁর সঙ্গে রামচন্দ্রের কথাবার্তার সময় কেউ যদি সেখানে উপস্থিত হয়, রামচন্দ্র তবে তাকে ত্যাগ করবেন। রামচন্দ্রকে দুর্বাসার জন্যই বাধ্য হয়ে লক্ষ্মণকে ত্যাগ করতে হল। লক্ষ্মণ সরযূ-সলিলে দেহত্যাগ করলেন। লক্ষ্মণকে ত্যাগ করে রামচন্দ্র আর পৃথিবীতে থাকতে চাইলেন না। ভ্রাতৃদ্বয়সহ রামচন্দ্র সরযুর জলে দেহত্যাগ করলেন। এইখানেই রামায়ণের শেষ।\n" +
                "যদু্কুলধ্বংসের মূলে দুর্বাসাঃ\n" +
                "একবার দুর্বাসা দ্বারকার পথে যাচ্ছিলেন –যাদববালকরা পথের ধারে খেলা করছিল। যাদব বালকদের অনুরোধে কৃষ্ণপুত্র শাম্ব মুশল পেটে বেঁধে নারীবেশে ঋষির সম্মুখে গিয়ে জিজ্ঞাসা করল – ঠাকুর, বল দেখি আমার কি সন্তান হবে এবং কবে সন্তান হবে? দুর্বাসাকে যাদববালকরা চিনত না। দুর্বাসা বুঝলেন, বালক তাঁকে ব্যঙ্গ করছে। দুর্বাসা বললেন –তোর পেটে মুষল জন্মাবে, সেই মুষলে যদুকুল ধ্বংস পাবে। জানিস আমি কে? আমি দুর্বাসা? দুর্বাসার নাম শুনে তাদের চৈতন্য হল। তারা তখন সেই মুষলটাকে ঘসে ঘসে গুঁড়ো করে জলে ফেলে দিল। সেই গুঁড়োগুলি থেকে নলখাগড়ার বনের জন্ম হল। অভিশাপ ঐ নলখাগড়ার মধ্যেই থেকে গেল।\n" +
                "তারপর বহু বৎসর চলে গেল। দুর্বাসার শাপের কথা কারও মনে থাকল না। সেদিনকার যাদব বালকগণ এখন প্রৌঢ়। তারা কদিন সুরাপানে উন্মত্ত হয়ে নিজেদের মধ্যে মারামারি শুরু করল। ঐ নলখাগড়াগুলি তুলে পরস্পরকে মারতে লাগল। ঐ গুলির স্পর্শ পাওয়ামাত্র সকলে প্রাণহীন হয়ে পড়ল। এইভাবে মারামারি করে যদুকুল ধ্বংস হল। তা থেকে কথা হয়েছে – মুষলং কুলনাশনম্।\n" +
                "এই যদুকুল দুর্বাসার শ্বশুরকুল। যদুবংশেই দুর্বাসা দ্বিতীয় বার বিবাহ করেন। যদুবংশীয়া পত্নীটিকে প্রথমা পত্নীর মত শাপে ভস্মীভূত করেছিলেন কিনা জানা যায় না।\n" +
                "কুন্তীকে বর প্রদানঃ\n" +
                "দুর্বাসা শুধুই শাপ দেন না –এক-আধবার বরও দিয়েছেন। তিনি বর দিয়ে একবার অনর্থও ঘটিয়েছিলেন। একবার দুর্বাসা কুন্তীভোজ রাজার গৃহে অতিথি হন। কুন্তীভোজের পালিতা কন্যা কুন্তী (পৃথা) খুবই যত্নের সঙ্গে দুর্বাসার সেবা-পরিচর্যা করেন।\n" +
                "দুর্বাসা তাতে সন্তুষ্ট হয়ে কুন্তীকে বর দেন এবং একটি মন্ত্র শিখিয়ে দেন। এই মন্ত্রের দ্বারা যে কোন দেবতাকে স্মরণ করলে তিনি পতিরূপে উপস্থিত হবে। মন্ত্রটি ঠিক খাটে কিনা, তা পরীক্ষা করবার জন্য কুন্তী অবিবাহিতা অবস্থাতেই সূর্যদেবকে স্মরণ করেন –সূর্যদেবের বরে কর্ণের জন্ম হয়। এজন্য কুন্তীর দুর্ভোগ কম হয়নি –কর্ণের দুর্গতিও কম হয়নি।\n" +
                "ব্রাহ্মণকে বরদানঃ\n" +
                "আর একবার দুর্বাসা এক উঞ্ছবৃত্তিক ব্রাহ্মণকে স্বর্গলাভের বর দেন। একজন ব্রাহ্মণ ক্ষেতের ধানযব কুড়িয়ে এনে শস্য সঞ্চয় করতেন এবং তার সামান্য কিছু অংশে জীবনধারণ করে প্রতি অমাবস্যায় যজ্ঞ করে ঐ সঞ্চিত খাদ্যে শিষ্যদের ভোজন করাতেন। দুর্বাসা সন্ধান পেয়ে প্রতি অমাবস্যায় ব্রাহ্মণের ঘরে এসে একাই সব ভোজন করে চলে যেতেন। ব্রাহ্মণ তাতে বিরক্ত না হয়ে ঋষিকে মাসে একবার ভোজন করাতেন। এই ভাবে বৎসরকাল ব্রাহ্মণের পরীক্ষা করে দুর্বাসা ব্রাহ্মণকে বর দিলেন –স্বর্গলাভ কর।\n" +
                "যথাসময়ে দেবদূত স্বর্গে নিয়ে যাবার জন্য এল।\n" +
                "ব্রাহ্মণ বললেন –তোমার স্বর্গরথে চড়বার আগে আমি স্বর্গের গুণের কথা শুনতে চাই।\n" +
                "দেবদূত স্বর্গের গুণ বর্ণনা করল।\n" +
                "দেবদূত বললেন – স্বর্গের প্রধান দোষ – পুণ্যের বলে স্বর্গ লাভ হয় বটে, কিন্তু এই পুণ্যক্ষয় হলেই আবার মনুষ্যজন্মলাভ করতে হয়। স্বর্গ চিরদিনের জন্য নয়।\n" +
                "ব্রাহ্মণ বললেন –দেবদূত রথ নিয়ে ফিরে যাও, আমি এমন স্বর্গ চাই না। দুদিনের স্বর্গে আমার কাজ নেই। আমি এমন লোকে যেতে চাই - যে লোক হতে আর মর্ত্যলোকে ফিরে আসতে হয় না।\n" +
                "ব্রাহ্মণ দুর্বাসার বর গ্রহণ না করে তপস্যা করতে বনে চলে গেলেন এবং ব্রহ্মজ্ঞান লাভ করে চিরদিনের জন্য মুক্ত হলেন।\n" +
                "কাম্যকবনে দুর্বাসাঃ\n" +
                "দুর্বাসা দশ হাজার শিষ্য নিয়ে কিছুকালের জন্য দুর্যোধনের অতিথি হলেন দুর্যোধন নিজে ঋষির পদসেবা করতে লাগলেন, শিষ্যগণের ভুরিভোজের জন্য ভাণ্ডার উন্মুক্ত হল। শিষ্যগণ ত বনের ফলমূল খেয়ে বিরক্ত ও নীরক্ত হয়ে পড়েছিল, রাজভোজ পেয়ে তারা হৃষ্টপুষ্ট হয়ে উঠল।\n" +
                "দুর্বাসা তুষ্ট হয়ে দুর্যোধনকে বললেন –তোমার কি অভীষ্ট সাধন করব বল।\n" +
                "দুর্যোধনের মনে এক চিন্তাই ছিল – কি করে পাণ্ডবদের নিধন করা যাবে। দুর্যোধন কৃতাঞ্জলি হয়ে বললেন –\n" +
                "মহাত্মা, যদি আপনি আমার ইষ্ট সাধন করতে চান – তবে একটি কথা রাখতে হবে। কাম্যকবনে পাণ্ডবরা বাস করছে। আপনি রাত্রি দশদণ্ডের পর পাণ্ডবদের আশ্রমে এই দশ হাজার শিষ্য সঙ্গে উপস্থিত হয়ে যুধিষ্ঠির ও দ্রৌপদীর ধর্মনিষ্ঠার ও আতিথেয়তার পরীক্ষা করুন। আমার এই একমাত্র নিবেদন, আপনার কাছে আর কিছু চাই না।\n" +
                "দুর্যোধন অনায়াসেই এই নির্লজ্জ প্রস্তাব করলেন, আর আশ্চর্যের বিষয় দুর্বাসা সমস্ত ব্যাপার বুঝেও স্বীকৃত হলেন। দুর্যোধনের উদ্দেশ্য – অতিথি সেবা করতে না পেরে দুর্বাসার অভিশাপে পাণ্ডবগণ ধ্বংস পাক।\n" +
                "সশিষ্য দুর্বাসা রাত্রি দশ দণ্ডের পর যুধিষ্ঠিরের আশ্রমে উপস্থিত হলেন। যুধিষ্ঠির তাঁর চরণ বন্দনা করে জিজ্ঞাসা করলেন – “মহাত্মা, দাসের প্রতি কী আজ্ঞা হয়?”\n" +
                "পরীক্ষা করতে হলে মিথ্যা কথা বলতে হয়। দুর্বাসা মিথ্যে কথাই বললেন – “হস্তিনাপুরে গিয়ে কিছুদিন কুরুরাজের অতিথি হয়েছিলাম। দুর্যোধন ভৃত্যের মত আমার যথেষ্ট সেবা করেছেন। তারপর মনে হল তোমরা কেমন আছ একবার দেখে যাই। তুমি আহারের আয়োজন কর, আমি প্রভাস তীর্থে সন্ধ্যা বন্দনা সেরে আসি।” এই বলে তিনি সশিষ্য সন্ধ্যা বন্দনায় চলে গেলেন।\n" +
                "এদিকে যুধিষ্ঠির মহা চিন্তায় পড়লেন কি রূপে রাত্রির মধ্যে এতগুলি লোকের অন্নসংস্থান হবে। দ্রৌপদীর কাছে গিয়ে যুধিষ্ঠির বিপদের কথা জানালেন। দ্রৌপদী ভাবতে লাগলেন, কিন্তু তাঁর চিন্তা তো – কৃষ্ণচিন্তা। শ্রীকৃষ্ণ ছাড়া তাঁর অন্য শরণ্য কেউ নেই। তিনি প্রাণ ভরে কৃষ্ণকে ডাকতে লাগলেন। শ্রীকৃষ্ণ তাঁর সখীর প্রার্থনা শুনলেন। তিনি স্মরণমাত্র উপস্থিত হয়ে দ্রৌপদীর রন্ধনস্থালীর গায়ে লেগে থাকা একটি শাক ও এককণা অন্ন নিজে ভক্ষণ করলেন – তাতেই দুর্বাসার শিষ্যদের ক্ষুধা নিবৃত্তি হল। তাদের আর নড়বার শক্তি থাকল না। তখন দুর্বাসার দুরুভিসন্ধি নাসিকাগর্জনে পরিণত হল।\n" +
                "দুর্বাসার দর্পচূর্ণ\n" +
                "অম্বরীষ ছিলেন একজন সূর্যবংশীয় রাজা। তার হরিভক্তির তুলনা ছিল না। তিনি একবার এক বৎসর ধরে একটি ব্রত গ্রহণ করেন। বৎসর শেষে তিনি তিনদিন উপবাস করে চতুর্থ দিনে বিধিমত দান-ধ্যান করে পারণা করতে যাবেন এমন সময় দুর্বাসা ঋষি এসে উপস্থিত। দুর্বাসা অতিথি হলেন – অতিথি সৎকার না করে রাজা পারণা করতে পারেন না। দুর্বাসা নদীতে স্নানের জন্য গেলেন, সন্ধ্যা হয়ে গেল তখনও তিনি ফিরলেন না। এদিকে রাজার ব্রত ভঙ্গ হয়। তিনি অন্যান্য ঋষির উপদেশে বিষ্ণুর পাদোদক গ্রহণ করলেন। এমন সময় দুর্বাসা এসে উপস্থিত। রাজা জলগ্রহণ করেছেন শুনে দুর্বাসা ক্রোধে অগ্নিশর্মা হলেন। তিনি রেগে একগাছা জটা ছিঁড়ে মাটিতে ছুঁড়ে ফেললেন। সেই জটা থেকে এক দৈত্যের আবির্ভাব হল।\n" +
                "দৈত্য অম্বরীষকে বধ করতে উদ্যত হলে বিষ্ণুর চক্র এসে দৈত্যকে বধ করল এবং দুর্বাসাকে বধ করতে গেল। দুর্বাসা প্রাণপণে ছুটতে লাগল – চক্রও পিছনে পিছনে ছুটল। ত্রিভুবন ঘুরেও দুর্বাসা কোথাও আশ্রয় পেলেন না।\n" +
                "শেষে দুর্বাসা রাজা অম্বরীষের শরণাপন্ন হয়ে বললেন – “মহারাজ আমায় ক্ষমা করুন – ক্ষমা করুন।” অম্বরীষ বললেন – ঋষিবর বিনা কারণে আমায় বধ করতে চেয়েছিলেন। যাইহোক, আপনাকে আশ্রয় দিলাম। আর কখনও হরি ভক্তের উপর অত্যাচার করবেন না। অম্বরীষের প্রার্থনায় বিষ্ণুর চক্র বৈকুন্ঠে চলে গেল। এই ঘটনার পর দুর্বাসা অনেকটা সংযত হয়েছিলেন।\n");
    }

    private void ninthStory() {
        title.setText(R.string.prohollad);
        description.setText("সবসময় সত্যকথা বলার নাম সত্যবাদিতা। যে কোন অবস্থায় যে কারো সামনে সত্যকথা বলতে পারাকেও বলে সত্যবাদিতা।  সত্যবাদীরা লাভ ক্ষতির কথা চিন্তা করে না। জীবন-মৃত্যুর কথা ভাবে না।  সত্যই তাদের একমাত্র অবলম্বন। জীবন গেলেও তারা মিথ্যা বলে না। কোন অবস্থাতেই তারা সত্য থেকে বিচ্যুত হয় না। প্রাচীনকালে এমন একজন সত্যবাদী ছিলেন। তাঁর নাম প্রহ্লাদ। চলো আমরা প্রহ্লাদের সত্যবাদিরতার গল্প শুনি।\n" +
                "দৈত্যদের রাজা হিরণ্যকশিপু। তাঁর পুত্র প্রহ্লাদ। হিরণ্যকশিপু দেবতা বিষ্ণুর বিরোধী। কিন্তু প্রহ্ললাদ হয়ে উঠলেন অতিশয় বিষ্ণুভক্ত। একথা জানতে পেরে হিরণ্যকশিপু ভীষন রেগে গেলেন। তিনি প্রহ্লাদকে ডেকে বললেন, বিষ্ণু আমার শত্রু। তোমাকে বিষ্ণুনাম ছাড়তে হবে।\n" +
                "প্রহ্লাদ: তা কি করে সম্ভব, বাবা? তিনি যে ঈশ্বর।\n" +
                "হরিণ্যকশিপু: বিষ্ণু দৈত্যদের শত্রু। তাই দৈত্যকূলে জন্মে তুমি বিষ্ণুনাম নিতে পারবে না।\n" +
                "প্রহাদ: বাবা, শ্রীবিষ্ণু তো ঈশ্বর। ঈশ্বর কারো শত্রু নন। তাই আমি তাঁর নাম ছাড়তে পারব না।\n" +
                "হিরণ্যকশিপু রেগে গেলেন। কিন্তু কী করবেন? ছেলে তো। তাই তিনি তাকে গুরুমশাইয়ের নিকট পাঠালেন। যদি সংশোধন হয়। কিন্তু কোন ফল হলো না। প্রহ্লাদ আগের মতই বিষ্ণু নাম জপতে লাগলেন। হিরণ্যকশিপু আর সহ্য করতে পারছেন না। তাই ছেলেকে মেরে ফেলার সিদ্ধান্ত নিলেন। রাজার আদেশে সেনারা তরবারি দিয়ে তাঁকে আঘাত করল। কিন্তু প্রহ্লাদ মরলেন না। তাঁকে আগুনে নিক্ষেপ করা হলো। আগুন নিভে গেল। পায়ে পাথর বেঁধে নদীতে ফেলা হল। পাথর ভেসে উঠল। হাতির পায়ের নিচে ফেলা হল। হাতি শুঁড় দিয়ে পিঠে তুলে নলি। তাঁকে সাপের ঘরে ঢুকিয়ে দেওয়া হলো। সাপ ফনা তুলে তাঁর চারদিকে নাচতে লাগল। বিষ মাখানো খাবার খাওয়ানো হলো। তাতেও প্রহ্লাদের মৃত্যু হলো না।\n" +
                "তারপর একদিন হিরণ্যকশিপু সিংহাসনেব বসে আছেন। ক্রোধে তাঁর চোখ লাল। তিনি প্রহ্লাদকে ডাকলেন। প্রহ্লাদ বিষ্ণুনাম জপতে জপতে পিতার কাছে এলেন। হিরণ্যকশিপু ক্রোধে কাঁপতে কাঁপতে হুংকার দিয়ে বললেন, আমি নিজের হাতে তোমাকে মারব। দেখি কে তোমাকে বাঁচায়!\n" +
                "প্রহ্লাদ: বিষ্ণুই আমাতের বাঁচাবেন।\n" +
                "হির\u200C্যণকশিপু: এখানে এসে?\n" +
                "প্রহ্লাদ : তিনি সর্বত্রই আছেন , বাবা।\n" +
                "হিরণ্যকশিপু: সর্বত্র! এই স্ফটিক সম্ভের মধ্যেও?\n" +
                "প্রহ্লাদ: অবশ্যই, বাবা।\n" +
                "\n" +
                "হিরল্যকশিপু তখন  হুংকার দিযে স্ফটিক স্তম্ভটি ভেঙে ফেলেলেন। আর তখনই তার মধ্য থেকে বের হয়ে এলেন এক ভয়ংকর মূর্তি। তাঁর নাম নৃসিংহ। তাঁর মুখটা সিংহের মতো আর শরীরটা নৃ অর্থাৎ মানুষের মতো। বের হয়েই হিরণ্যকশিপুকে দুই উরুর উপর রেখে বধ করলেন। প্রহ্লাদ কড়জোড়ে নৃসিংরূপী বিষ্ণুর স্তব করতে লাগলেন।\n");
    }

    private void tenthStory() {
        title.setText(R.string.dhrubo);
        description.setText("অনেক অনেককাল আগের কথা । উত্তানপাদ নামে এক রাজা ছিলেন । তার ছিল দুই রানী । বড় রানী সুনীতি, ছােট রানী সুরুচি। সুনীতির ছেলে ধ্রুব । আর সুরুচির ছেলে উত্তম ।\n" +
                "সুরুচিকে রাজা বেশি ভালােবাসতেন । তাই সুরুচির ছেলে বলে উত্তম খুব আদর পেত। ধুব তেমন আদর পেত না । রাজপুরীর একপাশে ছেলে ধ্রুবকে নিয়ে থাকতেন সুনীতি । দুঃখে কষ্টে দিন কাটত তাঁর ।\n" +
                "একদিন উত্তম বাবার কোলে বসে আছে । তখন ধ্রুব সেখানে এসে উপস্থিত হল। ভাইকে বাবার কোলে দেখতে পেয়ে । সেও বাবার কোলে উঠতে চাইল । সুরুচি দূর থেকে তা দেখে এক দৌড়ে সেখানে চলে এল। বাধা দিল তাকে । ধুবের খুব দুঃখ হল । সে মনের দুঃখে মায়ের কাছে ছুটে গেল। মাকে বলল সব কথা ।\n" +
                "সুনীতি ছেলেকে আদর করে বললেন,\n" +
                "-বাবা, তুমি এক মনে হরিকে ডাক । ডাকার মতাে ডাকলে, তিনি সাড়া দেবেন । তিনিই তােমার দুঃখ দূর করে দেবেন । শান্ত হল ধুব । তারপর রাজপুরী ছেড়ে বেরিয়ে পড়ল হরির সন্ধানে । একমনে হরিকে ডেকে চলেছে ধুব । ঘুরতে ঘুরতে সে এক গভীর বনে এসে পড়ল । যাকে দেখে তাকেই হরি মনে করে । হরি নামের এমন মহিমা, বনের পশুও তাতে হিংসা ভুলে যায় ।\n" +
                "ধ্রুবের আকুল প্রার্থনায় শ্রীহরি সন্তুষ্ট হলেন, তিনি পাঠিয়ে দিলেন দেবর্ষি নারদকে । নারদ ধ্রুবর কাছে এসে জিজ্ঞেস করলেন,\n" +
                "তোমার কী চাই বৎস?\n" +
                "-আমি পৃথিবীর কিছুই চাই না, চাই শ্রীহরির শ্রীচরণ । কী করলে শ্রীহরির দেখা পাব, আমাকে তা বলুন প্রভু । নারদ তাকে হরির আরাধনার বিষয়ে অনেক কিছু শিখিয়ে দিলেন । কালিন্দী নদী তীরে বসে ধুব শ্রীহরির সাধনা করতে লাগল । ধ্রুবের কঠোর সাধনায় শ্রীহরি সন্তুষ্ট হলেন । তিনি এসে ধ্রুবকে বললেন-\n" +
                "-আমি এসেছি ধ্রুব ।\n" +
                "-পদ্মপলাশলোচন শ্রীহরি, এসেছ, এসেছ তুমি ।\n" +
                "ধ্রুব ভক্তিভরে শ্রীহরির শ্রীপাদপদ্মে প্রণাম করল ।\n" +
                "শ্রীহরি বললেন-\n" +
                "বৎস, বালক হয়েও তুমি কঠোর সাধনা করেছ । তােমার মায়ের পুণ্যে তোমার হৃদয়ে জাগ্রত হয়েছে ভক্তি । আর তার ফলে তুমি সাধনায় সিদ্ধ হলে। ধ্রুব পরে ফিরে গেল রাজপুরীতে । উত্তানপাদ ধ্রুবকেই রাজপদে অভিষিক্ত করলেন। মৃত্যুর পর ধ্রুবের স্থান হয়েছিল ব্রহ্মাণ্ডের সর্বোচ্চ স্থান ধ্রুবলোকে ।\n");
    }

    private void eleventhStory() {
        title.setText(R.string.korno);
        description.setText("মহাবীর কর্ণ — মহাভারতের অন্যতম ট্র্যাজিক চরিত্র।  জন্মলগ্ন থেকে নিয়তি যাঁর সঙ্গে পরিহাস করেছে , আমৃত্যু সেই পরিহাস করা  থেকে বিরত হন নি ভাগ্যদেবতা।  আর পরীক্ষার কথাই যদি বলতে হয় তবে কর্ণের গোটা জীবনটাই পরীক্ষা।  জীবনভোর তিনি একের পর এক কঠিন পরীক্ষা দিয়ে গিয়েছেন।  আর সকল পরীক্ষায় উত্তীর্ণ হয়েছেন সসম্মানে। \n" +
                "কর্ণের জন্মপরিচয় গোপন থাকার জন্য তাঁকে জীবনভোর অনেক অপমান সহ্য করতে হয়েছে একথা সত্য। কিন্তু কর্ণের দাম্পত্যজীবন ছিল বড়োই মধুর।  সেই নারীরা , যখন তাঁর জীবনে এলো , তারা  নিজের স্নেহ ,সান্নিধ্য দিয়ে মুছে দিয়েছিলো যাবতীয় অপমান , অনাদর , অবহেলাকে।\n" +
                "দ্রৌপদীর কাছে স্বয়ম্বর সভায় প্রত্যাখ্যাত হওয়ার পর কর্ণ দুই বার দারপরিগ্রহ করেছিলেন।  প্রথম ভ্রুশালী, আর দ্বিতীয় সুপ্রিয়া।  কর্ণের প্রথম স্ত্রী আবার পদ্মাবতী নামেও কোনো  কোনো কাহিনীতে পরিচিত। কর্ণের ঔরসে পদ্মাবতী বা ভ্রুশালীর গর্ভে জন্ম নিয়েছিল বৃষকেতু —— কর্ণের একমাত্র সন্তান। কর্ণের একমাত্র বংশধর। এই বৃষকেতুকে নিয়েই আজকের গপ্পো ।\n" +
                "শৈশব থেকেই বৃষকেতু ছিলেন পরিবারের অত্যন্ত আদরের।  তবে আদরের পাশাপাশি ছিল কড়া অনুশাসন।  আর আদরটা পদ্মাবতীর থেকে ছোটমা সুপ্রিয়ার কাছেই মিলতো বেশি।  বড় বৌ হওয়ায় পদ্মাবতীর দায়িত্ব ছিল অনেক বেশি।  আর সুপ্রিয়া ছিলেন নিতান্তই ছেলেমানুষ।  ফলে তার কাছে বৃষকেতুকে দিয়ে পদ্মা নিশ্চিন্ত।  কারণ নিঃসন্তান সুপ্রিয়ার কাছে বৃষকেতু এতটাই প্রিয় ছিলেন যে পিত্রালয়ে গেলেও ছোটমার সঙ্গী হয়ে বৃষকেতু আগেই পালকিতে উঠতো। অনেকটা “বিন্দুর ছেলের মতো।  বহুদিন পর্যন্ত বৃষকেতু জানতে পারেননি যে ছোটমা সুপ্রিয়া তার বিমাতা।\n" +
                "সুপ্রিয়ার অন্য সন্তান থাকলে কি হতো বলা যায় না।  তবে এক স্ত্রী বর্তমানে কর্ণ সুপ্রিয়াকে বিবাহ করলেও তাঁর প্রেয়সী আজীবনই পদ্মাবতী থেকে যাবেন , এ সত্য বিবাহের আগেই বুদ্ধিমতী সুপ্রিয়া উপলব্ধি করতে পেরেছিলো। তাই বিয়ের পরে প্রথম প্রথম  স্বামীকে তাঁর প্রতি কেবল কর্তব্য করতে দেখে সে একেবারেই অবাক হয়নি।  মনটা শুধু ব্যাথায় ভরে উঠতো থেকে থেকে।  তবে বড়দি পদ্মাবতী সত্যি খুব ভালো।  তাই সতিনকাঁটা না হয়ে সে হয়ে উঠেছিল সখি।  আর তার মাঝে জ্যান্ত পুতুল বৃষকেতু পদ্মাবতীর সকল দুঃখকে মুছে দিয়েছিলো। তবে ধীরে ধীরে কর্ণ সুপ্রিয়াকেও ভালোবাসতে শুরু করেন তার মধুর স্বভাবের গুণে।  ভালোবাসার থেকেও স্নেহ ছিল অনেক বেশি।  কারণ সুপ্রিয়া বয়সে অনেকটাই ছোট।\n" +
                "যাক সে কথা। একবার হঠাৎ সুপ্রিয়াকে কি কাজে পিত্রালয়ে যেতে হলো।  বৃষকেতু তখন পাঠশালায়।  উপায় নেই।  যেতেই হবে সুপ্রিয়াকে। এই প্রথম বৃষকেতুকে না নিয়ে তিনি পিত্রালয়ে যাচ্ছেন।  সে যে এখন পাঠশালায়।  যাওয়ার সময় একটিবারও  যে তার সঙ্গে দেখা হলো না।  বাড়ি ফিরে এসে তার ছোটমাকে না দেখতে না পেলে সে যে কুরুক্ষেত্র করবে।\n" +
                "যাওয়ার সময় বার বার কেন জানি না কু ডাক ডাকতে থাকে সুপ্রিয়ার মনটা।  কে জানে বৃষ পাঠশালা থেকে বাড়ি এসে তার ছোটমাকে দেখতে না পেয়ে কতই কাঁদবে।  যদিও বড়দি পদ্মাবতী আশ্বস্ত করেছেন , চিন্তা করিস না ছোট।  আমি ওকে একটুও বকবো না।  ওকে সারাক্ষণ চোখে চোখে রাখবো।  তুই একদম ঠান্ডা মাথায় বাপের ঘরে যা দেখি।  ঘুরে আয় শান্তিতে। কোথাও তো যাস না তোর কোলের ধনকে ছেড়ে।\n" +
                "চোখের জলও ফেলতে পারছে না সুপ্রিয়া।  পাছে বৃষর কোনো অকল্যাণ হয়. বড়দিদির তো হাজারটা কাজ।  বৃষ তো কাল পাঠশালা যাবে না।  এই রোদে দস্যিপনা করে বেড়াবে।  রাত্রিতে কি করে ঘুমোবে ছেলেটা ! ছোটমার গায়ের সঙ্গে লেপ্টে না শুলে তো ছেলের ঘুম আসে না।\n" +
                "একদম বাপের বাড়ি যেতে ইচ্ছে করে না ছোটবৌয়ের।  বার বার কর্ণ তাড়া  দিচ্ছেন ওদিকে।  অবশেষে সুপ্রিয়া হাত ধরে বলে গেলেন পদ্মাবতীর।  দিদি না গেলে নেহাত নয়।  তাই যাচ্ছি।  নাহলে মনটা আমার ভারী অশান্ত।  কোথাও যেতে মন চাইছে না আমার   বৃষকে ছেড়ে। না হয় কাজ একটু কম হোক।  কিন্তু আমি না ফেরা পর্যন্ত তুমি আমার ছেলেটাকে চোখের আড়াল হতে দেবে না।  এই বলে সুপ্রিয়া পালকিতে বসলেন।\n" +
                "পদ্মাবতী মাথায় হাত বুলিয়ে সুপ্রিয়াকে বিদায় দিলেও , তাঁর দু চোখও জলে ভরে উঠেছিল।  কারণ তিনি খুব ভালো ভাবে জানেন বৃষকেতুর একটুও অযত্ন হলে সুপ্রিয়া , যে পদ্মাবতী বৃষর গর্ভধারিনী মা , তাকেও গালমন্দ করতে ছাড়েন না।  প্রথম প্রথম পদ্মা বিরক্ত হলেও , পরে বুঝেছেন কতটা ভালোবাসলে সতীনের ছেলেকে কেউ এমন করে আঁকড়ে ধরে।  মনে মনে বললেন তুই তাড়াতাড়ি ফিরে আয় ছোটবৌ।  এসে তোর ছেলের দায়িত্ব নে।\n" +
                "আর ওদিকে পালকিতে বসে সুপ্রিয়া মনে মনে বললেন।  কাজটা শেষ হলেই মা-বাবার চরণবন্দনা করেই চলে আসবেন এবাড়িতে।  বৃষকে সঙ্গে নিয়ে আবার কদিন পর না হয় যাবেন।  বৃষ যে তাঁর অন্ধের যষ্ঠি।  এসে যদি দেখি আমার ছেলের একটুও অযত্ন হয়েছে , তাহলে মহারাজ কর্ণও ছাড় পাবেন না।  এ কথা অবশ্য আগেই ঘোষণা করে এসেছেন সুপ্রিয়া।  আর বড়োগিন্নি যদি বাড়ির কাজ কর্ম দেখতে গিয়ে বৃষকে গুরুত্ব না দেয় , তাহলে বড়দির কপালেও দুঃখ আছে। এই আমি বলে রাখলুম। নিজের মনে  বলেন সুপ্রিয়া।\n");
    }

    private void twelvethStory() {
        title.setText(R.string.vismo);
        description.setText("মহামহিম — এই উপাধি দিয়েছিলেন স্বয়ং শ্রীকৃষ্ণ, ভীষ্মকে। এই ব্যক্তির জীবন যেমন ঘটনাবহুল তেমন বর্ণনাময়। মহাভারতে হাজারো ঘটনা রয়েছে, কিন্তু তার মাঝে ভীষ্মের জীবন যেন সর্বদা মানুষের কৌতূহল উদ্রেক করে গিয়েছে। হস্তিনাপুরের এই রাজপুত্র কিন্তু কোনো সাধারণ মানুষ নন। রাজা শান্তনুর ঔরসে দেবী গঙ্গার গর্ভজাত অষ্টম সন্তান।কাজেই সাধারণ যে তিনি নন, তা তার মাতার কথা শুনেই অনুমান করা যায়। কিন্তু প্রকৃতপক্ষে ভীষ্মের মনুষ্যজন্ম তাঁর নিজের পাপের দণ্ড ছাড়া আর কিছুই নয়।\n" +
                "এক মহাপাপ হয়েছিল ভীষ্মের দ্বারা তাঁর পূর্বজন্মে। যে জন্য ব্রহ্মশাপে অভিশপ্ত হতে হয়েছিল তাঁকে। শুধু তাঁকে নয়, তাঁর ওপর সাত ভাইকেও।\n" +
                "অষ্ট বসুর কথা বিবৃত ভারতীয় পুরাণে। তাঁদের মধ্যে অষ্টম বসু দ্যু। একদা অষ্ট বসু আপন আপন পত্নীদের নিয়ে ভ্রমণে বেরোলেন। পথিমধ্যে পড়লো বশিষ্ঠ মুনির আশ্রম। তপোবনের সৌন্দের্য্যে মাতোয়ারা বসুপত্নীদের চোখ পড়লো মহামুনি বশিষ্ঠের গাই নন্দিনীর উপর। নন্দিনী অতিথিদের দেখে খুব খুশি হলেন। বশিষ্ঠের ছাত্রদের অনুরোধের আগেই তিনি দুধ, দধি সহ নানা উৎকৃষ্ট মানের খাদ্য দিয়ে অতিথিদের আপ্যায়ন করলেন। ভুললেন না বসুপত্নীদের মূল্যবান বস্ত্র ও অপরূপ কারুকার্যমন্ডিত অলংকার উপহার হিসেবে দিতেও।\n" +
                "নন্দিনী ছিলেন স্বর্গের কামধেনু সুরভীর কন্যা। বশিষ্ঠের কাছে গাই এসেছিলো এই দেবাশিস স্বরূপ। ঋষি তাঁকে মাতৃজ্ঞানে শ্রদ্ধা করতেন। আর নন্দিনীও তাঁকে পিতাতূল্য শ্রদ্ধা করতো। গৃহস্বামী পিতা বশিষ্ঠের অনুপস্থিতে কখনো সেই নন্দিনী অতিথিদের অযত্ন হতে দিতে কি পারেন?\n" +
                "নন্দিনীর দুধের এক আশ্চর্য গুণ ছিল। এই দুধ একবার যে পান করতো, সে দশহাজার বছর সুস্থ শরীরে জীবনকে উপভোগ করতে পারতো। এহেন নন্দিনীর উপর নজর পড়লো দ্যু এর স্ত্রীর। এখানে প্রশ্ন জাগে যে বসুরা তো অমর। আর তাঁদের পত্নীরাও মর্তের মরণশীল মানবী নন। কাজেই তাঁদের তো নন্দিনীর দুগ্ধ পান করে দীর্ঘজীবন লাভের প্রয়োজন নেই। কিন্তু এখানে প্রশ্ন ছিলো বন্ধুত্বের।\n" +
                "উশিনর রাজার কন্যা জিতবতী দ্যুর স্ত্রীর প্রিয় সখি। বান্ধবীকে একবার যদি এই গাইয়ের দুধ খাওয়ানো যায়, তাহলে তিনি দশহাজার বছর এই পৃথিবীর সুখ ভোগ করতে পারবেন। এর চেয়ে সুখের বিষয় আর কি রয়েছে! আর ঋষির কাছে এই রকম এক সম্পদ ফেলে রাখবার কি উপযোগিতা রয়েছে? স্ত্রীর একের পর এক যুক্তিতে দ্যু কোণঠাসা হয়ে পড়ছিলেন। তার মধ্যেই সেই নারী প্রয়োগ করলেন তাঁর মোক্ষম যুক্তিটি, দেখো বিয়ের পর কিছুই চাইনি তোমার কাছে।\n" +
                "আজ–ও অমূল্য কোনো কিছু চাইছিনা। চাইছি একজনের বহু ব্যবহৃত গাই। তাও দিতে তোমার এতো আপত্তি। আর এটা নিয়ে গেলে তো শুধু আমার নয় তোমার–ও প্রয়োজনে লাগবে। কত মানুষের দুধের দরকার। কিন্তু তা কেনার সঙ্গতি নেই তাদের। আমরা স্বর্গ থাকলেও প্রয়োজনে এই গাইটির দুধ দিয়ে তাদেরকে সাহায্য করতে পারবো। তাহলে সবটাই তো হচ্ছে বিশ্বসংসারের কল্যাণের জন্য। তাহলে তো আর তোমার অসুবিধে কোথায়?\n" +
                "দ্যু একবার বলতে চেষ্টা করেছিলেন দেখো যতই জগতের কল্যাণের জন্য আমরা নন্দিনীকে নিয়ে যাই না কেন, এতে কিন্তু আমাদের অপরাধের গুরুত্ব কোনো অংশে কমে যায় না। পরের সম্পত্তি না বলে গ্রহণ করাকে চুরি বলে। আর আমাদের এই কাজ চুরি হিসেবেই গণ্য হবে। তখন কিন্তু ঋষির ক্রোধ থেকে আমরা কেউ নিস্তার পাবো না। ভালো করে এর পরিণাম ভেবে দেখো। তখন কিন্তু স্বয়ং প্রজাপতি ব্রহ্মা–ও আমাদের উপর রুষ্ট হবেন। তার থেকে কিছুক্ষণ অপেক্ষা করা যাক। ঋষি ফিরে আসুন। তাঁর অনুমতি নিয়েই বরং আমরা নন্দিনীকে নিয়ে যাই। সকলের কল্যাণ হবে ভেবে নিশ্চই বশিষ্ঠ মুনি এতে অমত করবেন না। আর তাছাড়া আমরা আপাতত একটা পাত্র চেয়ে নিয়ে তোমার সখি জিতবতীর জন্য দুগ্ধ নিয়ে যাই। পরে না হয় এসে মহর্ষির সঙ্গে কথা বলে যাবো।\n" +
                "কিন্তু বসুপত্নী এতে কিছুতেই সম্মত হলেন না। তাঁর মতে বশিষ্ঠ ফিরে এলে কোনোভাবেই তিনি নন্দিনীকে হাতছাড়া করতে চাইবেন না। বার বার তিনি স্বামীকে পীড়াপীড়ি করতে থাকেন।\n" +
                "অপর সপ্ত বসু–ও এ ব্যাপারে কেউ কেউ নির্বিকার থাকেন। আবার কেউ কেউ দ্যুকে গৃহশান্তি বজায় রাখবার পরামর্শ দেন। শেষপর্যন্ত নিজের বিচার বিবেচনা হারিয়ে অষ্ট বসুরা নন্দিনীকে বাছুর সমেত চুরি করে নিয়ে গেলেন।\n" +
                "নন্দিনী অনেক চেষ্টা করেছিলেন আশ্রম ছেড়ে না যাওয়ার। কিন্তু আশ্চর্যের ব্যাপার বিশ্বামিত্রের সৈন্যদলকে যিনি পরাস্ত করতে পেরেছিলেন অবলীলাক্রমে, তিনি এব্যাপারে ব্যর্থ হলেন। কে জানে বসুরা বা তাঁদের পত্নী — কেউই তো আদপে মরলোকের বাসিন্দা নন। তাই নন্দিনীর পরাক্রম হয়তো তাঁদের ষোলো জনের সঙ্গে লড়বার পক্ষে পর্যাপ্ত ছিল না।\n" +
                "এদিকে আশ্রমে প্রত্যাবর্তন করলেন বশিষ্ঠ। শিক্ষার্থীরা ভয়ে গুরুদেবকে কিছু বললো না। কিন্তু নন্দিনীকে না দেখতে পেয়ে প্রথমে এদিক ওদিকে পাগলের মতো খুঁজলেন মহামুনি। তারপর ধ্যানযোগে সব ঘটনা অবগত হলেন। ক্রোধভরে বললেন মহামুনি দেবতা হয়েও অষ্ট বসু মানুষের মতো কাজ করেছো। তোমাদের স্থান স্বর্গলোকে নয়, মানুষের সমাজই তোমাদের উপযুক্ত স্থান।যাও শোকে তাপে ভরপুর এই ধরিত্রীতে মানবরূপে জন্মগ্রহণ করো।\n" +
                "দেবসমাজ প্রমাদ গুণলেন। এক বিশাল সভা ডাকা হলো স্বর্গলোকে। সেখানে দাঁড়িয়ে সপ্ত বসু থরথর করে কাঁপছেন। ব্যতিক্রম শুধু দ্যু। তিনি অবিচল। জানেন খুব ভালো করেই যে অপরাধ তাঁরা বিশেষত তিনি করেছেন, তার শাস্তি তাকে পেতেই হবে। তাই তিনি দণ্ড ভোগের জন্য প্রস্তুত, অবিচল।\n" +
                "ত্রিদেব ক্ষোভে ফেটে পড়েছেন। স্পষ্ট ভাষায় রায় দিলেন তাঁরা। দেবতা বলে কি তোমাদের ধর্ম–অধর্মের ভয় নেই? কি মনে করো তোমরা নিজেদেরকে। তোমরা জান নন্দিনী কে? তা সত্ত্বেও তোমরা এমন নিন্দনীয় কাজ কি ভাবে করতে পারলে? তোমাদের জন্য দেবতাদের মাথা হেঁট হয়ে গেছে। স্বর্গের বাসিন্দারাই যদি চৌর্যবৃত্তি করেন, তাহলে মনুষ্যজাতিকে আমরা কি শিক্ষা দেব? আর তারাই বা আমাদের আদেশ মানবে কেন? ক্ষমার কোনো প্রশ্ন ওঠে না। ক্ষমা তোমরা একমাত্র পেতে পারো মহামুনি বশিষ্ঠের কাছে। তিনি যদি তোমাদের ক্ষমা করে নিজের অভিশাপ ফিরিয়ে নিতে পারেন, তাহলেই তোমরা স্বর্গে থাকতে পারবে। নচেৎ মহামুনি যা বলেছেন তাই হবে।\n" +
                "আট ভাই চিন্তিত হয়ে নিজেদের মধ্যে পরামর্শ করলেন। ত্রিদেব–ও মুখ ফিরিয়ে নিয়েছেন। এখন উপায় একমাত্র মহামুনির চরণ স্পর্শ করে নিজেদের অপরাধ স্বীকার করা। একটা ক্ষীণ আশা যদি তিনি সন্তুষ্ট হন।\n" +
                "যেমনি ভাবা তেমনি কাজ। নন্দিনীকে মাতৃসম্বোধন করে তাঁকে বাছুর সহ আট ভাই নিয়ে গেলেন মহর্ষি বশিষ্ঠের আশ্রমে। গিয়ে ঋষির পা জড়িয়ে ধরলেন সাত ভাই। মুনিবর আমাদের ক্ষমা করুন। আমরা খুব বড় অপরাধ করে ফেলেছি। আমাদের মার্জনা করে দিন এবারের মতো। আর কখনো এরকম ভুল আমাদের হবে না।\n" +
                "আসন ছেড়ে উঠে দাঁড়ালেন মহামুনি। পায়চারি করলেন। তারপর শোনা গেলো তাঁর চিরাচরিত মৃদু অথচ দৃঢ কণ্ঠ। অন্যায় নয়। পাপ করেছো। আজ আমাকে যে কষ্ট তোমরা দিয়েছো, তা হয়তো আমি মেনে নিতে পারতাম। কিন্তু আমার নন্দিনীকে তোমরা যেভাবে অপমান করেছো, তার কোনো ক্ষমা হয় না। আমার অনুপস্থিতে তোমাদের আপ্যায়নে সে তো কোনো ত্রুটি রাখেনি। সাধ্যের বাইরে গিয়ে সে তোমাদের সমাদর করেছে। কিন্তু তোমরা তাঁকে মাতা নয়, পশু হিসেবে বিবেচনা করেছো। অবোলা বাছুরটিকে পর্যন্ত তোমরা কেড়ে নিয়েছো তার মায়ের থেকে, যাতে নন্দিনী স্বেচ্ছায় তোমাদের সঙ্গে যেতে বাধ্য হয়। নাহলে নন্দিনীকে নিয়ে যাওয়া তোমাদের কর্ম নয় বসুগন। আর আসল অপরাধী অষ্টম বসু দ্যু। সে তো কি একবার ক্ষমা চায়নি। কারণ সে আসলে অনুতপ্তই নয়। অথচ তার অপরাধ সবচেয়ে বেশি। তার জন্যই এবারে তোমাদের সকলকে এই দুষ্কর্মের ফল ভোগ করতে হবে। কারণ চুরি না করলেও তোমরাও সেই পাপে সমান পাপী। কেউ তোমরা দ্যুকে সুপরামর্শ দাওনি। কেউ তাঁকে কুপথে যাওয়ার জন্য প্ররোচিত করেছো। কেউ বা পাশ কাটিয়ে গিয়েছো। কাজেই তোমাদের ভূমিকাও কিছু কম নয়।\n" +
                "ইতিমধ্যে মহামুনির আশ্রমে আগমন ঘটেছে ত্রিদেবের। তাঁরাও দ্যুয়ের এই আচরণে স্তম্ভিত। এবার মুখ খুললেন দ্যু। প্রথমে নন্দিনীর বাছুরটিকে কোলে নিয়ে নন্দিনীর পাশে দাঁড় করলেন। মাতা–সন্তানের মিলন হলো। এরপর মহামুনির চরণ বন্দনা করে বললেন হে ঋষি, আমি ক্ষমা চাই না। আমাকে আপনি আমার এই পাপের কঠোর  শাস্তি প্রদান করুন। এমন দৃষ্টান্তমূলক শাস্তি হোক আমার, যা দেখে আর কোনো দেব যেন এমন ভয়ঙ্কর পাপ করতে সাহস না করে। এমন দৃষ্টান্তমূলক শাস্তি দিন আমায়, যাতে আমি প্রতিমুহূর্তে আমার পাপের কথা মনে করে কষ্ট পাই। আজ যদি আপনি আমাকে ক্ষমা করেন, তাহলে এরকম অপরাধ দেবত্বের গৌরবে আরো অনেকেই করবে। সর্বোপরি ন্যায়ের মর্যাদা ধূলিলুণ্ঠিত হবে। মর্ত্যের মানুষজনকে এরপর ত্রিদেব কি শিক্ষা দেবেন। তারা ভাববে এরা নিজেদের লোকের বাসিন্দাদের তো পাপ করলেও কোনো শাস্তি দেন না। ফলে আমরাও নির্ভয়ে পাপ করতে পারি। তখন মানুষকে শাস্তি দিলে তো পক্ষপাতিত্ব হবে। অপরাধ করে যেখানে দেবতা ছাড় পেয়ে যাচ্ছেন, সেখানে মর্ত্যলোকের মানুষ কেন সাজা পাবে? এ কেমন বিচার হবে! মানুষের মধ্যে থেকে ধর্ম–অধর্মের ভেদাভেদ লুপ্ত হয়ে যাবে। এ আমি কখনো চাই না।\n" +
                "অভিভূত মহামুনি। ত্রিদেবের দিকে তাকালেন বশিষ্ঠ। এরপর দ্যু–এর মাথায় স্নেহের হাত রেখে বললেন। তবে তাই হোক বৎস। তোমার মনোস্কামনাই পূর্ণ হোক। মর্তলোকে মানুষের মরণশীল জীবনের সবটুকু অতিবাহিত করে দুঃখের আগুনে পরিশুদ্ধ হয়ে নিখাদ সোনায় পরিণত হয়ে তুমি ফিরে আসবে স্বর্গলোকে।\n" +
                "কোনো অন্তিম প্রার্থনা বৎস?\n" +
                "দ্যু নিবেদন করলেন যে আমার সাত ভ্রাতাকে আপনি ক্ষমা করুন মহামুনি। ওদের ভাগের সকল দুঃখ আমি একাকী ভোগ করতে প্রস্তুত। সপ্ত ভ্রাতা স্তম্ভিত। এ কি বলে চলেছে দ্যু। তাঁরা অভিশাপ থেকে মুক্তি পেতে চেয়েছেন প্রত্যেকেই। কিন্তু তা তো দ্যু–কে বাদ দিয়ে নয়। দ্যুকে ছেড়ে তাঁরা থাকবেন কি করে? বিচার করলে তাঁদের পাপ–ও তো বড় কম নয়। দ্যু যদি পাপী হন, তাহলে তাঁদের অপরাধ কিছুমাত্র কম নয়।\n" +
                "এবার যথার্থই চিন্তিত দেখায় মহামুনিকে। মনে মনে ভাবেন ক্রোধের বশবর্তী হয়ে এ কি করেছেন তিনি। এতো বড় অভিশাপ দিয়ে বসেছেন তিনি। হাত জোড় করে ত্রিদেবের কাছে নিবেদন করেন আপন ব্যর্থতা। একবার মুখের থেকে বাক্য নির্গত হলে তা তো ফিরিয়ে নেয়ার উপায় নেই ভগবন। কিন্তু এক কাজ করতে পারি আমি দ্যুয়ের ইচ্ছের সম্মান রেখে। সে হলো সপ্ত বসুরা পৃথিবীতে একবার করে জন্মগ্রহণ করলেই তাঁদের শাপমুক্তি ঘটবে। তাঁরা আবার ফিরে আসবেন স্বর্গলোকে। এটুকু আমি করতে পারবো আমার যোগবলে।  কিন্তু দ্যুয়ের ক্ষেত্রে তা হওয়ার নয়। তাঁকে এই ধরিত্রীতে দীর্ঘ মানবজীবন অতিবাহিত করে দুঃখ ভোগ করে তাঁর পাপের প্রায়শ্চিত্ত করতে হবে।\n" +
                "মহামুনি বশিষ্ঠের চরণবন্দনা করে দ্যু জানালেন তাই হবে মহামুনি। আপনার এই দণ্ড আমার কাছে আশীর্বাদ। তা আমি সানন্দে মাথা পেতে নিলাম।\n" +
                "কান্নায় ভেঙে পড়লেন দ্যু–এর স্ত্রী। সব ভুল তো আমার। তাহলে শাস্তি তুমি এক কেন পাবে? আমি তো তোমাকে প্ররোচিত করেছিলাম এহেন পাপ কর্মে। তুমি তো বার বার আমাকে নিষেধ করেছিলে। স্ত্রী সর্বদা স্বামীর হিতসাধনে সাহায্য করে। আমি প্রথম স্ত্রী, যে তার স্বামীর অনিষ্টের জন্য দায়ী।\n" +
                "মহর্ষির চরণ বন্দনা করে নিবেদন করলেন দ্যু পত্নী, আমাকেও মর্ত্যে জন্ম গ্রহণের অভিশাপ দিন হে মহামুনি। আমার স্বামীর বিরহে আমি কেমন ভাবে এই স্বর্গপুরীতে দিন যাপন করবো? আর সকল দশ তো আমার। তাহলে শাস্তি উনি একা কেন পাবেন?\n" +
                "মহামুনি সস্নেহে তুললেন সেই নারীকে। বললেন স্বামীর কর্তব্য স্ত্রীকে বিপথে যেতে না দেয়া। কিন্তু দ্যু এই কর্তব্য পালনে অসমর্থ। দেখো মা, আমরা সকলে নিমিত্ত মাত্র। এ সবই পরম করুণাময়ের ইচ্ছে। তাঁর উপরে আমরা কেউই যেতে পারি না।\n" +
                "আর দণ্ড তো তুমি পেয়েই গিয়েছো। তোমার স্বামীর থেকে দূরে থাকাই তোমার সবচেয়ে বড় শাস্তি।\n" +
                "স্বর্গে বসে তুমি যখন দেখবে মর্তলোকে তোমার স্বামী নিরন্তর সংগ্রাম করে চলেছেন, কিন্তু তাঁর প্রাপ্তির ভান্ডার শূন্য। তখন সেই শূন্যতা কি স্বর্গে বসে তোমাকেও স্পর্শ করবে না?\n" +
                "ভয় নেই তোমার স্বামীর হৃদয়ে কোনো মরলোকের নারী স্থান পাবে না। বা কোনো সন্তান ও তাঁর থাকবে না। তুমি সর্বদা তাঁর হৃদয়ের অধীশ্বরী হয়ে থাকবে। কিন্তু তাঁর স্নেহের পাত্র প্রচুর থাকবে। তাঁর চারপাশে বহু মানুষজন তাঁকে সদা সর্বদা ঘিরে থাকবেন। কিন্তু এতো মানুষের মধ্যে থেকেও তিনি থাকবেন জীবনভর একাকী।\n" +
                "নিজের চারপাশে যে ক্ষুদ্র অদৃশ্য গন্ডি টেনে দ্যু রাখবেন, তা কেউ কোনো দিন অতিক্রম করতে পারবেন না, শুধু তিন জন ব্যতীত।প্রথম দুজন দুই যুবক। এদের মধ্যে একজন হবেন স্বয়ং ঈশ্বর। তিনি এই পৃথিবীর ভার লাঘবের জন্য মনুষ্যদেহ ধারণ করে ধরায় অবতীর্ণ হবেন। আর এক যুবক থাকবেন, যিনি সবটা না হলেও কিছুটা তোমার স্বামীর মনের কথা বুঝবেন। আর তৃতীয় জন কে হবেন, তা আমি আমার যোগবলেও বুঝতে পারছি না। সে নারীও হতে পারে কিংবা পুরুষও। সে আদৌ তোমার স্বামীর জীবনে আসবে কিনা সেকথাও আমি এখনই বলতে পারছি না তোমায়।\n" +
                "এরপরের ঘটনা আমাদের অনেকেরই জানা। হস্তিনাপুরের রাজা ছিলেন রাজা প্রতীকের পুত্র শান্তনু। তাঁর প্রথম স্ত্রী গঙ্গাদেবী। শোনা যায় রাজা প্রতীক গঙ্গাদেবীকে পূর্বেই প্রতিশ্রুতি দিয়েছিলেন যে, আমার পুত্রের সঙ্গে এই লক্ষ্মী মেয়ের বিবাহ দেব।অবশ্য রাজা শান্তনুও দেবী গঙ্গার অপরূপ রূপ লাবণ্যে মুগ্ধ ছিলেন। কিন্তু বিবাহের শর্ত একটিই ছিল। তা হলো রানীর কোনো কাজে রাজা বাধাদান করতে পারবেন না। অন্যথায় রানী রাজ্য ত্যাগ করবেন তথা নিজের স্বামীর ঘর ছেড়ে চলে যাবেন। বিবাহের পর সাত বছরে শান্তনুর ঔরসে দেবী গঙ্গার গর্ভে সাতটি দেবকুমারের মতো পুত্র জন্ম গ্রহণ করে। আর জন্ম মাত্রেই রানী তাদেরকে গঙ্গার জলে ভাসিয়ে দেন।\n" +
                "এই সপ্ত পুত্র ছিলেন আসলে সাত বসু। বশিষ্ঠের সির্দ্ধান্ত শুনে সপ্ত বসু গঙ্গা দেবীকে অনুরোধ করেন, মাগো পৃথিবীতে যখন জন্মাতেই হবে, তাহলে অপর কারুর নয়, স্বয়ং সর্ব পাপ বিনাশিনী মা গঙ্গার গর্ভে আমরা তাঁর সন্তান রূপে ভূমিষ্ঠ হতে চাই। মা গঙ্গা তাঁদের প্রার্থনায় সাড়া দেন। তাই জন্ম মাত্রেই তাঁদেরকে গঙ্গায় ভাসিয়ে দেন। ঋষি বশিষ্ঠের কথা অনুযায়ী এভাবেই তাঁদের শাপ মুক্তি ঘটে। কারণ পৃথিবীতে একবার জন্মগ্রহণ করার অন্তেই তাঁদের শাপমোচন হবে, একথাই বলেছিলেন মহর্ষি।\n" +
                "সপ্ত বসু একটা ক্ষীণ আশাতেও ছিলেন যে দৈবাৎ অষ্টম গর্ভের সন্তানকেও যদি কোনোক্রমে মা গঙ্গা গঙ্গা স্রোতে ভাসিয়ে দিতে পারেন নিজে হাতে, তবে দ্যু-এর শাপমুক্তি ঘটবে।কারণ বিধির বিধানে মা গোনাগ ও রাজা শান্তনুর অষ্টম সন্তানই হলেন শাপগ্রস্থ অষ্টম বসু দ্যু। কিন্তু বিধি বাম।\n" +
                "রাজা শান্তনুর হৃদয় বিদীর্ণ হয়ে যায় এদিকে। প্রতিটি সন্তানকে রানী এভাবে গঙ্গার স্রোতধারায় ভাসিয়ে দেন। একটু কি মায়া জাগে না তাঁর মনে ! এদিকে পূর্বপ্রতিশ্রুতি অনুযায়ী রানীকে হারাবার ভয়ে তাঁকে কিছু বলতেও পারেন না।\n" +
                "কিন্তু আর যে সয় না। অষ্টম গর্ভের সন্তানকে দেখে শান্তনুর চোখ জুড়িয়ে গেলো। এতগুলি সন্তানের মধ্যে সবচেয়ে প্রিয়দর্শন এই শিশু। বাস্তবিক অষ্ট বসুর মধ্যে দ্যু-ই ছিলেন সর্বাপেক্ষা সুদর্শন। এমনটাই ভারতীয় পুরাণ সহ অপরাপর মহাকাব্যগুলি বলে থাকে।\n" +
                "গঙ্গা যখন নির্মল হাসি হেসে সেই শিশুসন্তানকেও গঙ্গার স্রোতে ভাসিয়ে দিতে গেলেন, শান্তনু দৃঢ মুষ্টিতে ধরলেন প্রিয়তমা পত্নীর মণিবন্ধ। তাঁর প্রেমিক সত্তা অন্তর্হিত হয়ে সে স্থান অধিকার করেছে চিরন্তন পিতৃসত্তা। দোহাই তোমার রানী, এই সন্তানটিকে অন্তত এভাবে হত্যা করো না। তুমি মা না পাষাণী ! দশ মাস দশ দিন গর্ভ যন্ত্রনা সহ্য করে কি অবলীলাক্রমে তুমি তোমার সন্তানদের নিজের থেকে বিচ্ছিন্ন করে দাও রানী। কিন্তু আমি যে আর পারি না।অন্তত এই সন্তানকে তুমি বাঁচতে দাও। আর আমি তোমার কাছে কখনো কিছু চাইবো না, কথা দিলাম। আমার এই অনুরোধটুকু অন্তত রাখো তুমি গঙ্গা।\n" +
                "গঙ্গার মুখে মৃদু হাসি। তোমার আদেশ শিরোধার্য স্বামী। এই নাও তোমার সন্তান। আমি চললাম। এই বলে কোলের সন্তানের দিকে তাকিয়ে আদর করে বলেন, বাবা আর আমি কিছু করতে পারলাম না তোমার জন্য।  এ স্বয়ং বিধাতার বিধান। যে তোমাকে ভূলোকে দীর্ঘকাল অতিবাহিত করতে হবে। না হলে বিবাহের আট বছর পরে ঠিক তোমার বেলাতেই রাজা শান্তনু আমাকে বাধা দেবেন কেন? যাওঁ পুত্র, তোমার পিতার হাতে তুলে দিয়ে আপাতত বিদায় নিলো তোমার এই মা। তোমাকে স্বর্গে ফেরাতে পারলাম না। তোমার ভাইদের একটা ক্ষীণ আশা ছিল। যে আমি হয়তো পারবো। কিন্তু ক্ষমা করো তোমার এই মাকে। তাঁর এই ব্যর্থতা ইচ্ছাকৃত নয়। আমার আশীর্বাদ সর্বদা তোমার সঙ্গে থাকবে।\n" +
                "রাজা কিছুই বুঝতে না পেরে অবাক হয়ে তাঁর প্রিয়তমার দিকে তাকিয়ে থাকেন। এ কি বলছেন রানী?\n" +
                "মা গঙ্গা তখন রাজাকে দ্যু ও আরো সপ্ত বসুর কাহিনী বিবৃত করলেন। সেই সঙ্গে জানালেন যে তাঁর পৃথিবীতে এসে একজন মরণশীল রাজাকে বিবাহ করবার মূলে এটি অন্যতম কারণ হলেও একমাত্র কারণ নয়। সে কারণ না হয় পরে বলবো।আপাতত আমাকে ত্যাগ করতে হবে তোমার প্রাসাদ।\n" +
                "প্রত্যুত্তরে নীরব থাকেন রাজা। কেননা রানীর কাজে বাধাদানের পরিণাম কি হবে, তা তিনি পূর্বেই জ্ঞাত। তবুও শেষ চেষ্টা করতেই একবার তাকান তাঁর প্রিয়তমা রানীর মুখের পানে। দু চোখের তারায় তাঁর করুণ মিনতি। যেও না তোমার শান্তনুকে ছেড়ে গঙ্গা। মৃদু হেসে প্রিয়তম স্বামীর হাত ধরেন গঙ্গা। অধরে হাসি অথচ দু চোখের কোণে রোধ করে থাকা অশ্রুধারাকে সংযত করে বলেন দুঃখ পেও না স্বামী। এ সবই বিধাতার বিধান। তুমি -আমি নিমিত্ত মাত্র। আমাদের বিবাহের পরিণতি যে বিচ্ছেদ এ তো পূর্বেই নির্দিষ্ট ছিল। তবে এ বিবাহ, এ সম্পর্ক বিফলে যাবে না।\n" +
                "তোমার-আমার এই অষ্টম সন্তান পরম যশস্বী হবে। কিন্তু জীবনে সুখী হবে না। সকল কর্তব্য পালন করবে নিষ্ঠা ভরে, কিন্তু জীবনে শান্তি পাবে না। এই এর নিয়তি। পরম মেধাবী ও বীরশ্রেষ্ঠ এই সন্তান হতে তোমার গৌরব দিন দিন বৃদ্ধি পাবে।পৃথিবীর মানুষজন তোমাকে জানবেন যত না রাজা শান্তনু হিসেবে তার চেয়েও বেশি দেবব্রতর পিতা হিসেবে। হ্যাঁ, দেবব্রত \u00AD\u00AD\u00AD—-আমাদের সন্তানের এই নামকরণ করে গেলাম আমি। এটুকু অধিকার মা হিসেবে হয়তো আমার প্রাপ্য।\n" +
                "মন খারাপ করো না। যখনি তোমার আমার কথা মনে হবে, তখনি আমাকে স্মরণ করো। আমাদের দুজনের দেখা হবে।\n" +
                "কি যেন ভাবলেন একমুহূর্ত। তারপর গঙ্গা জানালেন তাঁর সির্দ্ধান্ত তিনি পরিবর্তন করছেন। আর হ্যাঁ তোমার এই সন্তানকে আপাতত আমি নিয়ে গেলাম। কারণ সদ্যজাত সন্তানের কাছে মায়ের প্রয়োজনীয়তা পিতার চাইতে অধিক। যথাসময়ে আমি আমাদের এই সন্তানকে সকল বিষয়ে পারদর্শী করে তোমার হাতে অর্পণ করবো। ততদিন অপেক্ষার প্রহর গুণতে হবে তোমাকে স্বামী।\n" +
                "এই বার আর পারলেন না গঙ্গা। তিনি যে শুধু দেবী নন। রাজা শান্তনুর প্রিয়তমাও বটে। ঝরে পড়লো এতক্ষণ বহু কষ্টে রোধ করে রাখা অশ্রুধারা। যাওয়ার সময় হলো। কিন্তু মন যে প্রিয়তমকে ছেড়ে যেতে চায় না. দেবব্রতকে কোলে নিয়ে পা বাড়ালেন তিনি। ফিরে তাকালেন পিছনে, যেখানে সাশ্রুনয়নে অপেক্ষমান তাঁর প্রেমিক পুরুষ। মনে মনে বললেন, এ বিচ্ছেদ, এ মনোবেদনা কেবল তোমার নয় স্বামী, আমারও। এ বিচ্ছেদ দেবব্রতর পিতা-মাতার। অপেক্ষা কর স্বামী। আবার দেখা হবে দুজনার। মরলোকেও, এমনকি মৃত্যুর পর অমরাবতীতেও। শুধু তুমি প্রেমিক থেকো।\n" +
                "শান্তনু-গঙ্গার এই অষ্টম সন্তানই পরবর্তী কালে ভীষ্ম নাম পরিচিত হন। বশিষ্ঠের অভিশাপ ফলেছিলো। আজীবন নিজের দায়িত্ব পালন করেছেন নিষ্ঠাভরে। কিন্তু নিজে শান্তি পাননি এক মুহূর্তের জন্যও। আজীবন সংঘর্ষ, সংগ্রাম।আপনার জনদের মৃত্যু দেখে যুবাবয়সে নেয়া নিজের সির্দ্ধান্তকে দায়ী করেছেন মনে মনে। কিন্তু মুখ ফুটে একটি কথাও বলতে পারেননি।\n" +
                "চারিদিকে এতো লোকজন। কিন্তু দুজন ছাড়া কেউ-ই তাঁর নিকটে আসতে পারেনি। একজন দ্বারকাধীশ শ্রীকৃষ্ণ, ভগবান বিষ্ণুর মনুষ্য অবতার। আর অপরজন হলেন তাঁর পৌত্র তৃতীয় পান্ডব অর্জুন। পিতামহ ভীষ্মের মনের কথা এই শ্যামল বর্ণের যুবকই যা একটু আধটু বুঝতে পারতেন। আর তৃতীয় জন হলেন শিখণ্ডিনী, পরে শিখন্ডি। যিনি ভীষ্মকে যন্ত্রণামুক্তির পথে এগোতে পরোক্ষে সাহায্যই করেছিলেন। মহাকাব্য যদিও সেকথা বলে না।\n" +
                "নিজের কুকর্মে যেমন স্বর্গচ্যুত হয়ে মরলোকের অধিবাসী হতে হয়েছিল দ্যুকে, তেমন ভীষ্মের অবিবাহিত থাকবার ভয়ংকর প্রতিজ্ঞা বহু পূর্বেই কুরুক্ষেত্র যুদ্ধের বীজ বপন করেছিল। যে প্রতিজ্ঞার জন্য দেবতারা দেবব্রতের নামকরণ করেন ভীষ্ম। পিতৃভক্তির পরাকাষ্ঠাস্বরূপ এই প্রতিজ্ঞা করার জন্য স্বর্গ  থেকে দেবব্রতের মাথায় পুষ্পবৃষ্টি হলেও দেবব্রত তথা ভীষ্মের জীবনের পরবর্তী অধ্যায় অত্যন্ত বেদনাদায়ক।  সারা জীবন ধরে অশান্তি, জটিলতা,শোকের কূলপ্লাবী ধারায় স্নাত হয়েছেন তিনি। পরিশেষে এলো সেই ক্ষণ। দীর্ঘ প্রতীক্ষার অবসান হলো। শোকের আগুনে পুড়ে পুড়ে নিখাদ বৈদূর্যমণির উজ্জ্বলতা নিয়ে স্বর্গলোকে প্রত্যাবর্তন করলেন ভীষ্ম তথা অষ্টম বসু দ্যু ।\n");
    }

    private void thirteenthStory() {
        title.setText(R.string.nondini);
        description.setText("তুমি নিশ্চয় শুনেছ ঋষি বিশ্বামিত্রের নাম। সেই যে, যাঁর সঙ্গে রাম-লক্ষ্মণ বনে গিয়ে তাড়কা রাক্ষসীকে বধ করে ঋষিদের নির্বিঘ্নে যজ্ঞ করার সুযোগ করে দিয়েছিলেন। তিনি কিন্তু প্রথমেই ঋষি ছিলেন না। ছিলেন এক রাজার সন্তান। তাঁর বাবা ছিলেন কান্যকুব্জ বা কনৌজের রাজা গাধি।\n" +
                "বিশ্বামিত্র তাঁর আসল নাম ছিল না। প্রথম জীবনে তিনি ছিলেন গাধিনন্দন কৌশিক। রাজা হয়ে তিনি অনেক যুদ্ধ জিতেছিলেন। এরকমই এক যুদ্ধশেষে কৌশিক রাজ্যে ফিরছেন। পথশ্রমে সৈন্য-সামন্ত সকলেই ক্লান্ত। একটা মহাবন পেরিয়ে তাঁর রাজ্য। কিন্তু রাত হয়ে গেছে বলে সৈন্যরা আর এগোতে চাইল না। সেখানেই বসে পড়ল।\n" +
                "সেই বনে ছিল মহামুনি বশিষ্ঠের আশ্রম। মহর্ষি বশিষ্ঠ ছিলেন ক্রোধহীন প্রেমময়। তাঁর স্বভাবগুণে রাজা প্রজা সকলেই তাঁকে সম্মান করত আর ভালবাসত। তিনি ছিলেন তখনকার ভারতবর্ষের শ্রেষ্ঠ বংশ সূর্যবংশের কুলপুরোহিত। কৌশিক ক্লান্ত সেনাদের নিয়ে খাদ্য ও আশ্রয়ের জন্য সেই আশ্রমে এসে উপস্থিত হলেন।\n" +
                "তখন বেশ রাত হয়েছে। সেই সময় এতগুলি ক্ষুধার্ত লোকের উৎপাতে বশিষ্ঠের ঘুম ভেঙে গেল। তিনি দরজা খুলে বাইরে আসতেই কৌশিক এগিয়ে এসে একটু হুকুমের সুরেই বললেন, \"মুনিবর, আজ যুদ্ধক্লান্ত আমি আর আমার সেনারা ক্ষুধার্ত হয়ে আপনার কাছে এসেছি। রাতটা আমরা এখানেই কাটাতে চাই। আপনি আমাদের খাদ্য ও বিশ্রামের ব্যবস্থা করে অতিথিসৎকার করুন।\"\n" +
                "বশিষ্ঠ একটু হেসে মৃদু গলায় বললেন, \"মহারাজ, আমি আপনাদের পরিচয় জানি। দেশের রাজা আমার গৃহে উপস্থিত হয়েছেন। আপনাকে নমস্কার। আপনার সৈন্যদের সামনের জলাশয় থেকে হাত মুখ ধুয়ে এসে শান্তভাবে বসতে বলুন। আমি যথাসাধ্য আহারের ব্যবস্থা করছি।\" শিষ্যদের তিনি অতিথিদের জন্য আলোর ব্যবস্থা করতে বলে ঘরে ঢুকলেন।\n" +
                "সেই সময় ঋষি বশিষ্ঠের আশ্রমে গোমাতা সুরভির কন্যা নন্দিনী থাকতেন। নন্দিনী ছিলেন কামধেনু, অর্থাৎ তাঁর কাছে যা চাওয়া যেত, তিনি তা-ই দিতে পারতেন। বশিষ্ঠের আশ্রমে থাকতেন বলে তিনি ঋষির সমস্ত ইচ্ছাই পূর্ণ করতেন। তাঁর আদেশ ছাড়া কোন কাজ করতেন না। ঋষিও তাঁকে যথেষ্ট সম্মান করতেন। এত রাতে বশিষ্ঠ তাঁকে জাগিয়ে রাজার আগমনের সংবাদ দিলেন এবং সমস্ত সৈন্য সমেত রাজার জন্য খাদ্য ও শয্যা প্রার্থনা করলেন। নন্দিনী আশ্রয়দাতার বিপদে সাহায্য করতে পেরে খুশিই হলেন। তিনি বিভিন্ন রকমের এত পরিমাণ খাদ্য দিলেন যে, রাজা ও সৈন্যরা পরিতৃপ্ত হয়ে আকন্ঠ ভোজন করলেন। শুধু তাই নয়, পরদিন সকালেও ঋষি তাদের তৃপ্তিসহকারে খাওয়ালেন।\n" +
                "কিন্তু এত আপ্যায়ন সত্ত্বেও রাজার মন তুষ্ট হল না। রাজার মন তো- সব সময়েই বাঁকা পথে চলে। পেট ভরল তো মনে হতে লাগল, এই রকম উৎকৃষ্ট খাদ্য ও শয্যা এই রাতে ঋষি পেলেন কোথা থেকে? বশিষ্ঠ মুনি বনে থাকেন, চারদিকে দারিদ্র্যের চিহ্ন, কিন্তু তাঁর পরিবেশিত খাবার তো রাজার ঘরের মতন। রহস্যের কিনারা করতে না পেরে রাজার আর ঘুমই এল না।\n" +
                "সকালে উঠে রাজা বিদায় নেবার জন্য তৈরী হয়েছেন। বশিষ্ঠ রাজার সঙ্গে দেখা করতে এলেন। কুশল জিজ্ঞাসা ও আশীর্বাদের পর বিদায় প্রার্থনা করে রাজা বললেন, \" মুনিবর, আপনার আতিথ্যে আমি ও আমার সৈন্যদল অত্যন্ত তৃপ্ত হয়েছি। একটা প্রশ্ন মনে জাগছে, এত রাত্রে আপনি এই রকম রাজকীয়ভাবে আমাদের আহার ও শয়নের ব্যবস্থা করলেন কি ভাবে? এই আয়োজন তো রাজপ্রাসাদেও দুর্লভ। \"\n" +
                "ঋষি মৃদু হেসে বললেন, \" মহারাজ, এতে আমার কোন কৃতিত্ব নেই। গোমাতা সুরভির কন্যা নন্দিনী এই সময় দয়া করে আমার আশ্রমে রয়েছেন। তিনি প্রকৃতিতে কামদুঘা। তিনি আমার প্রার্থনা পূর্ণ করেছেন মাত্র।\"\n" +
                "রাজা কৌশিক উত্তেজিত হয়ে বললেন, \" সুরভি-কন্যা নন্দিনী কামদুঘা? তবে তো তিনি রাজ-অধিকারে থাকবার যোগ্য। ঈশ্বর শ্রেষ্ঠ বস্তু রাজার জন্যই সৃষ্টি করেন। এই দুর্লভ সৃষ্টিকে আপনার মত দরিদ্র ব্রাহ্মণ কি ভাবে সমাদর করবেন? মহামুনি, একে আমায় দান করুন।\"\n" +
                "বশিষ্ঠ বললেন, \" মহারাজ, নন্দিনী আমার অধিকারভুক্ত সম্পত্তি নন। তিনি আমার আশ্রমে দয়া করে কিছুদিনের জন্য থাকতে এসেছেন। তাই তাঁকে দান করার অধিকার আমার নেই।\"\n" +
                "রাজা অত্যন্ত অবজ্ঞার সঙ্গে হাতটা নাকের সামনে নেড়ে কথাটা উড়িয়েই দিলেন। বললেন, \" ওসব কথার কথা মুনিবর। আসল কথা হল, নন্দিনীর আপনার কাছে থাকবার কোন যুক্তিই নেই। একটি গাভীর এরকম অদ্ভুত ক্ষমতা যদি রাজার কাজেই না লাগল তো তার থেকেই বা কি লাভ! আর রাজা কিছু চাইলে সেটাতো তাঁকে পেতেই হবে। ভাল কথায় কাজ না হলে বলপ্রয়োগে। \"\n" +
                "বশিষ্ঠ বিরক্ত হলেন। তিনি একটু কঠোর স্বরে বললেন, \" রাজা, নন্দিনী আমার কন্যাসমা। তিনি দেবীতুল্যা, সামান্য রাজা তাঁকে পেতে পারেন না। তাছাড়া তিনি আমার মাননীয়া, আমার কাছে গচ্ছিতা আছেন। আপনা্র অনুরোধ রক্ষা করা আমার পক্ষে সম্ভব নয়। \"\n" +
                "কৌশিক স্বভাবে মোটেই ভাল রাজা ছিলেন না। তাঁর খেয়ালখুশিতে বাধা পড়লেই তিনি ভীষণ রেগে যেতেন। মান্যজনকে তিনি উপযুক্ত সম্মান করতেন না। এখন তাঁর মনে হল, দরিদ্র ঋষির এত সাহস যে, তাঁর মত রাজাকে অমান্য করে। তবেতো একে কেড়েই নিতে হবে। তিনি সৈন্যদের হুকুম দিলেন নন্দিনীকে নিয়ে যাবার জন্য।\n" +
                "সৈন্যরা হুকুম পেয়ে নন্দিনীর দড়ি ধরে টানাটানি শুরু করল। নন্দিনী আর্তস্বরে ঋষিকে বললেন, \"মুনিবর আমায় রক্ষা করুন। এই দুষ্ট লোকেরা আমায় চুরি করার মতলব করেছে।\"\n" +
                "বশিষ্ঠ দুঃখিতস্বরে বললেন, \" মা, আমি ব্রাহ্মণ মানুষ। পূজা-অর্চনা, পঠন-পাঠন এসব নিয়েই থাকি। তোমায় রক্ষা করার মত ক্ষাত্রবল আমার নেই। দুষ্ট রাজার কবল থেকে উদ্ধার পেতে তুমি নিজ শক্তির ব্যবহার না করলে তো তোমার রক্ষার উপায় দেখি না।\"\n" +
                "ঋষির অনুমতি পেয়ে নন্দিনী ভয়ংকরস্বরে ডাকতে শুরু করলেন। তাঁর চোখ দিয়ে আগুনের হল্কা বেরোতে লাগল। কিছু সৈন্য তো তাতেই মরে গেল। তাঁর মুখ দিয়ে খস, পুক্কুস প্রভৃতি বন্য দুর্ধর্ষ সৈন্যরা স্রোতের মত বেরিয়ে আসতে লাগল। তাদের সামনে রাজসৈন্যেরা জলের মত ভেসে গেল। রাজা কৌশিককে তারা দড়ি দিয়ে বেঁধে প্রচন্ড প্রহারে প্রায় অর্ধমৃত করে বশিষ্ঠের সামনে এনে ফেলল। রাজা কোনরকমে হাত জোড় করে ঋষির কাছে জীবন ভিক্ষা চাইলেন। বশিষ্ঠ তাঁকে নন্দিনীর কাছে ক্ষমা চাইতে বললেন।\n" +
                "নন্দিনীর কাছ থেকে মার্জনালাভের পর বশিষ্ঠ রাজাকে বললেন, \" হে রাজা, প্রজারা রাজার শক্তি। তাদের সঙ্গে দুর্ব্যবহার করে নিজের শক্তি ক্ষয় কোরো না। কেড়ে নেওয়া রাজার ধর্ম নয়। দান করতে শেখো। লোভ কোরো না। লোভ থেকে হিংসার জন্ম হয়, ও পরিণামে তা মানুষকেই ধ্বংস করে। অক্রোধ মানুষকে মহান করে। ক্ষমা মহতের স্বভাব। এই কটি কথা মনে রেখো। তোমার মঙ্গল হোক।\"\n" +
                "কৌশিক রাজ্যে ফিরে এলেন। তাঁর মনে বশিষ্ঠের উপদেশে আমূল পরিবর্তন এসেছে। তিনি ভাবছিলেন, এতবড় ঘটনাটা ঋষিকে একটুও বিচলিত করেনি। কোন উপায়ে তিনি এত শান্ত থাকতে পারলেন? এই শক্তি তাঁকেও পেতে হবে। তাঁর মনে হল, যে কোন কারণে বিচলিত হতে সবাই পারে। কিন্তু এই অবিচল থাকতে পারাটাই অনেক বড় ক্ষমতা।এটা রাজশক্তির থেকেও বড়।\n" +
                "রাজা তাঁর ছেলেদের কাছে রাজ্যের শাসনভার ছেড়ে দিয়ে বেড়িয়ে পড়লেন এই শক্তি লাভের জন্য মনকে জয় করার তপস্যা করতে। তপস্যার সময় অনেক অভিজ্ঞতার মধ্য দিয়ে যেতে যেতে তাঁর রাজসত্ত্বার বিলোপ ঘটল। অহংকার ও আত্মাভিমান মুছে গিয়ে বেরিয়ে এলেন নতুন মানুষ, ঋষি বিশ্বামিত্র।\n");
    }

    private void fourteenthStory() {
        title.setText(R.string.ostobosu);
        description.setText("দক্ষরাজার কন্যা \"বসু\" এর গর্ভজাত আটপুত্রকে বলা হয় অষ্টবসু। তাঁরা হলেন- ধর, ধ্রুব, সোম, অহ, অনিল, অনল, প্রত্যূষ ও প্রভাস।\n" +
                "অষ্টবসুর জন্ম–বিবরণঃ \n" +
                "হস্তিনানগরের রাজা শান্তনু, তার গুণে পৃথিবী পূর্ণ। ধার্মিক, মহাধনুর্দ্ধর রাজা বনে মৃগয়া(শিকার) করতে যান। একদিন একা ভ্রমণ করছেন গঙ্গার তীরে, হঠাৎ সেখানে দেবী গঙ্গা উপস্থিত হলেন। পদ্মের কেশরের মত বর্ণ, শ্বেতবস্ত্র পরিধানে, অপরূপ লাবণ্য-দেখে শান্তনু আশ্চর্য হলেন। তিনি কন্যার কাছে গিয়ে তার পরিচয় জানতে চাইলেন। তার রূপে রাজা মুগ্ধ। তিনি তাকে তার নারী হতে অনুরোধ করলেন। কন্যা বলেন –রাজা, আমি তোমার স্ত্রী হবো, কেবল একটি নিবেদন। আমার কোন কাজে তুমি বাধা দেবে না, নিষেধও করবে না। যেদিন তুমি এর বিরোধীতা করবে, সেদিনই আমি তোমায় ত্যাগ করবো। রাজা সকল শর্ত মেনে নিলেন। এভাবে শান্তনু ও গঙ্গার বিবাহ হল। রাজা দিব্যরত্ন ভূষণে স্ত্রীকে সাজালেন। সর্বদা তাকে তুষিলেন। এভাবে মহাসুখে শান্তনু গঙ্গার সাথে অবস্থান করলেন। ধিরে ধিরে অষ্টবসু শান্তনুর গৃহে পূর্ণশশীরূপে জন্ম নিতে লাগল। পুত্রের রূপ দেখে শান্তনু খুশি হলেন। নানা দান, যজ্ঞ করলেন। \n" +
                "এদিকে গঙ্গা পুত্র নিয়ে জলে ভাসিয়ে আসেন। দেখে শান্তনু কষ্ট পান। এভাবে এক এক করে পুত্র জন্মায় এবং গঙ্গা সকলকে ভাসিয়ে দেন। পূর্ব সত্যের ভয়ে রাজা গঙ্গাকে কিছু বলতে পারেন না। কিন্তু প্রতি মুহুর্তে তিনি পুত্রশোকে জ্বলতে থাকেন। এভাবে সাত পুত্র জন্ম নেয় এবং গঙ্গায় ভেসে যায়। পুত্রশোকে শান্তনু গুমড়ে ওঠেন। শেষে আবার তার পুত্র জন্মায়। এই অষ্টম পুত্রকে নিয়ে গঙ্গা জলে নামেন। ক্রুদ্ধ হয়ে শান্তনু গঙ্গাকে বলেন –কে তুমি মায়াবী! কোথা থেকে এলে! তোমার মত নিন্দিতা পৃথিবীতে দেখা যায় না, যে নিজ গর্ভের সন্তানকে নিজে হত্যা করে। কিভাবে তুমি নিজের সন্তানদের হত্যা করো, হে পাষানী! এতো বলে নিজপুত্রকে কোলে তুলে নিলেন। গঙ্গা বলেন –রাজা, পুত্র বাঞ্ছা করলে! তুমি আর আমায় চাও না। এই পুত্রটিকে তুমি যত্ন করে পালন করো। এবার আমি তোমায় নিজ পরিচয় দেবো। আমি জাহ্নবী। তিনলোক আমায় পূজা করে। আমার গর্ভে যে পুত্রগুলি জন্মাল তারা সব অষ্টবসু। বশিষ্টের শাপে তারা কাতর হয়ে আমায় প্রার্থনা করেন। আমি তাদের অঙ্গীকারে সম্মত হই এবং সে কারণে তোমার স্ত্রী হই। \n" +
                "রাজা বলেন -পূর্বের কথা বলো। কি কারণে বসুদের বশিষ্ট শাপ দিলেন। জলাধিপতি বরুণ গঙ্গা বলেন –বরুণের পুত্র বশিষ্ট মুনি। হিমালয় পর্বতে তার তপোবন। নানা ফলফুলে তার বন শোভিত। দক্ষ-কন্যা সুরভি তার গৃহিনী। কামধেনু তাদের কন্যা। বশিষ্ট মুনি একদিন অষ্টবসুরা(ভব, ধ্রুব, সোম, বিষ্ণু, অনিল, অনল, প্রত্যুষ ও প্রভব-এরা দক্ষের কন্যা বসুর পুত্র)তাদের স্ত্রীদের সাথে সেই বনে উপস্থিত হলেন। তারা মুনির তপোবনে স্ত্রীদের সাথে ভ্রমণ করছিলেন। প্রভব বা দিব্যবসুর স্ত্রী কামধেনুকে দেখে মুগ্ধ হলেন। স্বামীকে এই গাভীটি সম্পর্কে জানতে চাইলেন। কামধেনু দিব্যবসু বলেন এটি মুনি বশিষ্টের গাভী। এর অনেক গুণ। এর একপলা দুধও যদি নরলোক পায় তবে তা পান করে দশ সহস্র বছর বাঁচা যায় এবং চির যৌবন প্রাপ্ত হয়। স্বামীর কথায় স্ত্রী বলেন তার নরলোকে সখী উশীনর-কন্যা জিতবতীর জন্য তিনি কামধেনুর দুগ্ধ চান। স্ত্রীর বশ হয়ে বসু গাভীটি নিয়ে নিজ গৃহে যান। এ সময় বশিষ্ট আশ্রমে ফিরে এসে গাভী দেখতে না পেয়ে তাকে খুঁজতে বের হন। অনেক খুঁজেও যখন পেলেন না, তখন চিন্তিত হয়ে ধ্যানে বসে জানতে পারেন অষ্টবসুর গৃহে তার গাভী হরণ করে নিয়ে গেছে। ক্রোধে বশিষ্ট শাপ দেন তারা মনুষ্যলোকে জন্মাবেন। \n" +
                "বসুরা এই শাপের কথা শুনে মুনিকে স্তব করতে থাকেন। মুনি বলেন –আমার বাক্য খন্ডন হবে না। প্রতি বছর একজন করে মুক্তি পাবে। তবে যে গাভীটি চুরি করেছে সেই দিব্যবসু পৃথিবীতে থেকে যাবে। সে ধার্মিক, সর্বশাস্ত্রবিশারদ, পিতার প্রিয়কারী এবং স্ত্রীসম্ভোগত্যাগী হবে। পরে যদিও মুক্তি পাবে। এরপর গঙ্গা বলেন -মুনির শাপে কাতর বসুরা আমাকে স্তব করল এবং প্রার্থনা করল জন্ম মাত্র যেন তাদের ভাসিয়ে দেওয়া হয়, তবেই তারা মুক্তি পাবে। সে কারণেই আমার মর্তে তোমার স্ত্রী রূপে আগমন। রাজা, এই পুত্র অবতার। তবে মায়ের বিহনে পুত্র দুঃখ পাবে তাই এই পুত্র আমার সাথে এখন যাবে। পুত্র সুশিক্ষিত ও যৌবনপ্রাপ্ত হলে আবার তাকে তোমার কাছে ফিরিয়ে দিয়ে যাবো। এত বলে গঙ্গা পুত্রকে নিয়ে অন্তর্ধান হলেন। রাজা শান্তনু স্ত্রী ও পুত্র শোকে কাঁদতে কাঁদতে নিজ গৃহে ফিরে গেলেন।\n");
    }

    private void fifteenthStory() {
        title.setText(R.string.ostobosu);
        description.setText("হিন্দু পৌরাণিক কাহিনি মতে– ত্রেতা যুগে আবির্ভুত বিষ্ণুর তিন অবতারের মধ্যে ইনি ছিলেন দ্বিতীয় এবং বিষ্ণুর দশ অবতারের মধ্যে ষষ্ঠ। ইনি জমদগ্নীর ঔরসে রণুকার গর্ভে জন্মেছিলেন। এঁদের পাঁচ পুত্রের মধ্যে পরশুরাম ছিলেন কনিষ্ঠ। অপর চার পুত্ররা হলেন– বসু, বিশ্ববসু, বৃহদ্ভানু, বৃহত্কণ্ব। উদ্ধত ক্ষত্রিয়দের হাত থেকে ব্রাহ্মণদের রক্ষা করার জন্য বিষ্ণু পরশুরাম হিসাবে পৃথিবীতে জন্মগ্রহণ করেছিলেন। ভৃগু বংশে জন্ম বলে ইনি ভার্গব নামে পরিচিতি লাভ করেন।\n" +
                "একবার চিত্ররথ নামক রাজাকে সস্ত্রীক জলবিহার করতে দেখে এঁর মা রেণুকা কামার্তা হয়ে পড়েন। জমদগ্নি এই দৃশ্য দেখে, তাঁর পুত্রদেরকে মাতৃহত্যার জন্য আদেশ করেন। এঁর চার পুত্রের ভিতর একমাত্র পরশুরাম পিতার আদেশে কুঠার দিয়ে তাঁর মায়ের শিরোশ্ছেদ করেন। এতে জমদগ্নি খুশি হয়ে পরশুরামকে বর প্রার্থনা করতে বললে– পরশুরাম মায়ের পুনর্জন্ম, মাতৃহত্যাজনিত পাপ এবং মাতৃহত্যা স্মৃতি বিস্মৃত হওয়া, ভাইদের জড়ত্বমুক্তি, নিজের দীর্ঘায়ু ও অজেয়ত্ব লাভের বর প্রার্থনা করেন। জমদগ্নি তাঁকে সেই বরই প্রদান করেন।\n" +
                "মাতৃহত্যাজনিত পাপে, এঁর হাতের কুঠার হাতের সাথে সংযুক্ত হয়ে গিয়েছিল। ব্রহ্মকুণ্ডে স্নান করার পর, তাঁর হাত থেকে এই কুঠার বিচ্ছিন্ন হয়েছিল। পরে ইনি মানুষের কল্যাণের জন্য ব্রহ্মকুণ্ডের জল মুক্ত করে দেন। এই প্রবাহিত জলই পরবর্তী সময়ে ব্রহ্মপুত্র নামে পরিচিতি লাভ করে। এরপর একদিন জমদগ্নির পুত্রদের অনুপস্থিতিতে, কার্তবীর্য আশ্রমে এসে হেমধেনুর বাছুর অপহরণ করে এবং আশ্রম তছনছ করে। পরশুরাম ফিরে এসে এই ঘটনা শুনে কার্তবীর্যকে অনুসরণ করে আক্রমণ করেন এবং ভল্লের আঘাত কার্তবীর্যকে হত্যা করেন। এর ফলে কার্তবীর্যের পুত্ররা পুনরায় আশ্রমে হানা দিয়ে তপোমগ্ন জমদগ্নিকে হত্যা করে।\n" +
                "কার্তবীর্যের পুত্ররা তাঁর পিতা জমদগ্নীকে হত্যা করেছে, এই সংবাদ পাওয়ার পর পরশুরাম ক্ষত্রিয় নিধন করার উদ্যোগ নেন। মহাভারতের মতে তিনি একুশবার পৃথিবীকে নিঃক্ষৎত্রিয় করেন। এই সময় ক্ষত্রিয়দের রক্ত দিয়ে পাঁচটি হ্রদ তৈরি করেন। পরে সেই হ্রদের রক্ পিতৃলোকের তর্পণ করেছিলেন। এরপর ঋচীক প্রভৃতি পিতৃগণ এসে এই কাজের প্রশংসা করেন এবং বর প্রার্থনা করতে বলেন। উত্তরে পরশুরাম বলেন যে, \"হে পিতৃগণ! যদি প্রসন্ন হইয়া ইচ্ছানুরূপ বর প্রদানে অনুগ্রহ করেন, তাহা হইলে ক্রোধে অধীর হইয়া ক্ষৎত্রিয়বংশ ধ্বংস করিয়া যে পাপরাশি সঞ্চয় করিয়াছি, সেই সকল পাপ হইতে যাহাতে মুক্ত হই এবং এই শোণিতময় পঞ্চহ্রদ অদ্যাবধি পৃথিবীতে তীর্থস্থান বলিয়া যাহাতে প্রখ্যাত হয়, এরূপ বর প্রদান করুন। পিতৃগণ সেই বর প্রদান করে, ক্ষত্রিয়দের হত্যা বন্ধ করার কথা বলে যান। এরপর পরশুরাম ক্ষত্রিয়নিধন বন্ধ করে দেন। এরপর তিনি যজ্ঞের দ্বারা কশ্যপকে পৃথিবী দান করে, মহেন্দ্রপর্বতে বসবাস শুরু করেন।\n" +
                "ভীষ্ম ও দ্রোণ পরশুরামের কাছে অস্ত্রবিদ্যা শিক্ষা করেন। তাঁর ভাই বিচিত্রবীর্যের বিবাহের জন্য, কাশীরাজ তাঁর তিন কন্যাকে (অম্বা, অম্বিকা ও অম্বালিকা) ভীষ্ম স্বয়ংবর সভা থেকে অপহরণ করেন। এর ভিতর ঘটনাক্রমে অম্বা ভীষ্মকে জানান যে- ইনি পূর্বেই শাম্বরাজকে মনে মনে পতিত্বে বরণ করেছেন। ভীষ্ম অম্বাকে শাম্ববরাজের কাছে যাবার অনুমতি দিলেও, অপহৃতা কন্যা বলে শাম্বরাজ তাঁকে প্রত্যাখান করেন। এরপর অম্বা ভীষ্মের কাছে ফিরে এসে তাঁকে বিবাহ করতে অনুরোধ করলে, ভীষ্ম তা প্রত্যাখ্যান করেন। এরপর অম্বা পরশুরামের শরণাপন্ন হন। পরশুরাম এসে ভীষ্মকে এই বিয়েতে রাজী করাতে ব্যর্থ হলে, পরশুরামের সাথে ভীষ্মের ঘোরতর যুদ্ধ হয়। বহুদিন যুদ্ধের পরও পরশুরাম ভীষ্মকে পরাজিত করতে অসমর্থ হয়ে পুনরায় মহেন্দ্র পর্বতে ফিরে যান এবং অস্ত্রলাভের জন্য মহাদেবের তপস্যা করেন।\n" +
                "তপস্যায় খুশি হয়ে মহাদেব পরশুরামকে জগতের কল্যাণের জন্য ও মহাদেবের সন্তুষ্টির জন্য দেব-শত্রুদের ধ্বংস করার আশীর্বাদ প্রদান করেন। এরপর তিনি মহাদেবের আজ্ঞায় বহু দানব হত্যা করেন। সে কারণে, মহাদেব তুষ্ট হয়ে তাঁকে পরশু নামক অস্ত্র দান করেন। নীচ জাতীয় পুরুষ বিবেচনায় দ্রৌণাচার্য কর্ণকে শিষ্য হিসাবে গ্রহণ না করায়, ইনি ব্রাহ্মণের বেশে পরশুরামের কাছে ব্রহ্মাস্ত্র শিক্ষার জন্য যান। একবার পরশুরাম কর্ণের উরুতে মাথা রেখে ঘুমিয়ে থাকা অবস্থায় কর্ণকে অলর্ক কীট আক্রমণ করে। গুরুর ঘুম ভেঙে যাবার ভয়ে কর্ণ যন্ত্রণা নিরবে সহ্য করতে থাকেন। পরে পরশুরাম ঘুম ভেঙে উঠে সকল বিষয় দেখে এবং কর্ণের কষ্ট সহিষ্ণুতা লক্ষ্য করে তাঁর প্রকৃত পরিচয় জানতে চান। অবশেষে কর্ণ ব্রাহ্মণ নয় জেনে এবং গুরুকে প্রতারণা করার জন্য অভিশাপ দিয়ে বললেন যে, কপট উপায়ে ব্রহ্মাস্ত্র লাভের জন্য কার্যকালে কর্ণ এই অস্ত্রের কথা ভুলে যাবেন।\n" +
                "রামায়ণে আছে- বিষ্ণুর ষষ্ঠ অবতার পরশুরামের মৃত্যুর পূর্বেই সপ্তম অবতার রামের জন্ম হয়। রামচন্দ্রের হরধনু ভঙ্গের পর সীতাসহ যখন অযোধ্যায় ফিরে আসছিলেন, তখন পরশুরাম হরধনু ভঙ্গের সংবাদ লোকমুখে শুনতে পান। এতে ইনি রাগান্বিত হয়ে রামের মুখোমুখি হন এবং গর্বের সাথে বলেন যে- তাঁর কাছে বৈষ্ণব ধনু আছে এবং এই বৈষ্ণব ধনু ভঙ্গ করে প্রকৃত বীরত্ব দেখানোর জন্য রামকে আহ্বান করেন। একই সাথে তিনি বলেন যে- রাম এতে অসমর্থ হলে- তাঁর সাথে দ্বন্দ্বযুদ্ধে অবতীর্ণ হতে হবে। রাম পরশুরামের কাছ থেকে বৈষ্ণব ধনু নিয়ে পরশুরামের তপস্যার্জিত সমস্ত লোক বিনষ্ট করেন। ফলে পরশুরামের তেজ ক্ষয়প্রাপ্ত হয়। এরপর ইনি নির্বীর্য হয়ে রামচন্দ্রকে পূজা ও প্রদক্ষিণ করে, আবার মহেন্দ্র পর্বতে ফিরে যান।\n");
    }

    private void sixteenthStory() {
        title.setText(R.string.ostobosu);
        description.setText("শ্রীকৃষ্ণের ছেলেবেলার বন্ধু সুদাম। তাঁরা বড় গরীব। কৃষ্ণ তো মথুরায় রাজা হয়েছেন। কিন্তু সুদামের দারিদ্র্য আর ঘোচে না। তার স্ত্রী একবার বললেন, \"ওগো, তোমার বন্ধু তো মথুরার রাজা, তাঁকে বলে আমাদের সংসারে একটু আর্থিক স্বাচ্ছন্দ্যের ব্যবস্থা করতে পারো না?\" সুদাম দীর্ঘশ্বাস ফেলে উত্তর দিলেন, \"ছেলেবেলায় একসাথে খেলাধুলো করেছি, সর-ননী কাড়াকাড়ি করে খেয়েছি, এমনকি তারসাথে যে হাতাহাতি মারামারিও হয়েছে! সেই প্রাণের বন্ধু কানাইয়ের কাছে টাকা চাইব? ছি, ছি, এ আমি পারব না! তবে তাকে রাজবেশে দেখতে বড় সাধ হয়! একবার ঘুরে আসি মথুরায়, কি বলো? তুমি বরং কানাইয়ের প্রিয় কিছু খাবার তৈরি করে দাও, খালি হাতে তো আর রাজদর্শনে যাওয়া চলে না!\"\n" +
                "কিন্তু দরিদ্র-পরিবারে তেমন কিছুই ছিল না, তাই সুদামের স্ত্রী একটু চালভাজা পুঁটলিতে বেঁধে স্বামীর হাতে দিয়ে বললেন, \"তোমার সখাকে বোলো, তার বৌদিদি নিজে হাতে এই চালভাজা তৈরি করে পাঠিয়েছে।\"\n" +
                "তিনদিন ধরে হাঁটতে হাঁটতে ক্লান্ত অবশ দেহে, ধূলিধূসরিত অঙ্গে সুদাম পৌঁছালেন মথুরার রাজদুয়ারে, কিন্তু তার মলিন বেশভূষা দেখে সেপাই তাকে ভিতরে প্রবেশ করতে দিলনা। তিনি যতই বলেন, \"কানাই আমার বন্ধু,\" সেপাই গর্জন করে বলে-\"কৌন কানাই? ভাগো হিঁয়াসে।\" সুদাম হতাশায় ভেঙ্গে পড়ে রাজবাড়ির অনতিদূরে এক গাছের তলায় বসে থাকলেন।\n" +
                "ওদিকে রাজসভায় বসে শ্রীকৃষ্ণ চঞ্চল হয়ে উঠলেন। মন্ত্রীকে বললেন, \"আমার প্রাণের সখা এসেছে, সে ভিতরে প্রবেশ করতে পারছে না, তাকে আদর করে ভিতরে নিয়ে আসুন।\" মন্ত্রী বাইরে এসে সেপাইকে জিজ্ঞাসাবাদ করলেন। সেপাই বলল, \"না না, রাজার বন্ধু কেউ তো আসেনি, তবে ঐ ময়লা কাপড় পরা লোকটা সকালে এসে ভিতরে ঢুকতে চাইছিল, আমি তাকে বের করে দিয়েছি।\" বুদ্ধিমান মন্ত্রী কিছু আন্দাজ করতে পেরে সুদামকে ডেকে পাঠালেন। বললেন, \"চলো, আমাদের মহারাজ তোমাকে ডাকছেন।\"\n" +
                "কুণ্ঠিত সুদাম চালভাজার পুঁটলিটা কাপড়ের খুঁটে লুকিয়ে ধূলা পা নিয়ে জড়োসড়ো হয়ে প্রবেশ করলেন রাজদরবারে। কৃষ্ণ সুদামকে দেখামাত্র রাজ-সিংহাসন থেকে নেমে এসে বাল্যবন্ধুকে বুকে জড়িয়ে ধরে কাঁদতে লাগলেন। তারপর তাকে ধরে এনে জোর করে সিংহাসনে বসালেন, রানী রুক্মিণীকে বললেন, \"দেখছ না, সখা আমার ক্লান্ত হয়ে পড়েছে! পাখা কর!\" রুক্মিণী নিজেহাতে সুদামকে পাখার বাতাস করতে লাগলেন, শ্রীকৃষ্ণ স্বয়ং সখার পা ধুইয়েদিলেন-সুদামের কোনো আপত্তি শুনলেনই না। রাজপ্রাসাদের জাঁকজমক দেখে সুদাম আর সেই চালভাজার পুঁটলি বেরই করলেন না।\n" +
                "কিছু পরে কৃষ্ণ তাঁর কানের কাছে মুখ রেখে বললেন, \"বৌদিদি আমার জন্য নিজে হাতে তৈরি করে যেটা পাঠিয়েছে, সেটা বুঝি তুমি আমায় না দিয়ে একলা খাবে মনে করেছ? শিগগির বের করো!\"\n" +
                "সুদাম আনন্দাশ্রু বিসর্জন করতে লাগলেন। বন্ধুর যত্নআত্তিতে কয়েকদিন কাটিয়ে বাড়ির পথ ধরলেন। দূর থেকেই দেখলেন, তার ভাঙ্গা কুঁড়ের জায়গায় বিশাল প্রাসাদ।কাছাকাছি যেতেই তার স্ত্রী ছুটে এসে উত্তেজিত ভাবে বলতে লাগলেন, \"মহা আশ্চর্য ব্যাপার! তুমি যাবার পরেই কোথা থেকে সব রাজমিস্ত্রীর দল এসে বলল, রাজার আদেশে নাকি এখানে কুঁড়েঘরে বাস করা চলবে না! তারাই দিনরাত্রি এক করে এই প্রাসাদ তুলেছে!\" সুদামের দারিদ্র্য ঘুচে গেল।\n" +
                "ভগবানের সঙ্গে বন্ধুত্ব করাই লাভজনক। তার চেয়ে ভাল বন্ধু ত্রিভুবনে আর নেই।\n");
    }

}